compressConvert.rawText:
 async (
  absoluteSourceFilePath: string,
  config?: {
    isStatusLogged?: boolean;
    /**
     * If given, outputted file will be put in this folder
     */
    outputFolderPath?: string;
    /**
     * If given, name will change into this
     */
    name?: string;
    /**
     * frames per second (only for targetformat mp4)
     */
    fps?: number;
    /**
     * width pixels, will preserve same size if not given
     */
    sizeWidthPx?: number;
    /**
     * if not given, will preserve ratio
     */
    aspectRatio?: { x: number; y: number };
    /**
     * quality number in range: 0-100
     */
    quality?: number;
    targetFormat?: "webp" | "png" | "mp4" | "mp3" | "wav";
    /**
     * If true, and targetformat is wav, will convert to 16bit wav (uses `-ar 16000 -ac 1 -codec:a pcm_s16le` as extra options)
     *
     * Useful for whisper.cpp
     */
    is16bitWav?: boolean;
    /**
     * if true, original won't be thrown away afterwards
     */
    keepOriginal?: boolean;
    isDebug?: boolean;
  }
) => {
  if (isLocked(absoluteSourceFilePath)) {
    return;
  }
  if (!fs.existsSync(absoluteSourceFilePath)) {
    console.log("Path doesn't exist", absoluteSourceFilePath);
    return;
  }

  let result = ffmpeg({ source: absoluteSourceFilePath });
  if (config?.quality) {
    result = result.addOption(`-quality ${config?.quality || 100}`);
  }

  if (config?.fps && config.targetFormat === "mp4") {
    // set output fps
    result = result.withOutputFps(config.fps);
  }

  if (config?.targetFormat === "wav" && config?.is16bitWav) {
    result = result.outputOptions(["-ar 16000", "-ac 1", "-codec:a pcm_s16le"]);
  }

  if (config?.sizeWidthPx) {
    result = result.size(`${config?.sizeWidthPx}x?`);
  }

  if (config?.aspectRatio) {
    result = result.aspect(`${config?.aspectRatio.x}:${config.aspectRatio.y}`);
  }

  if (config?.targetFormat) {
    result = result.toFormat(config?.targetFormat);
  }

  const parsedPath = path.parse(absoluteSourceFilePath);

  const destinationName = config?.name || parsedPath.name;

  const folderPath = config?.outputFolderPath || parsedPath.dir;

  const extension = config?.targetFormat
    ? `.${config?.targetFormat}`
    : parsedPath.ext;

  // ffmpeg can't edit in-place
  const destinationPath = path.join(
    folderPath,
    `${destinationName}.converted${extension}`
  );

  const finalDestinationPath = replaceLastOccurence(
    destinationPath,
    ".converted",
    ""
  );

  // if (fs.existsSync(finalDestinationPath)) {
  //   console.log(
  //     `Shouldn't happen, but compressConvert was called for a file for which the finalDestinationPath already seems to exist`
  //   );
  //   return;
  // }

  result = result.save(destinationPath);

  let totalTime = 1;
  let shownPercentage = 0;

  const finalPath = await new Promise<string | undefined>((resolve) => {
    result
      .on("codecData", (data) => {
        // HERE YOU GET THE TOTAL TIME
        totalTime = parseInt(data.duration.replace(/:/g, ""));
        console.log("Converting Started...");
      })
      .on("progress", (progress) => {
        // HERE IS THE CURRENT TIME
        const time = parseInt(progress.timemark.replace(/:/g, ""));
        // AND HERE IS THE CALCULATION
        const percent = Math.floor((time / totalTime) * 100);

        lock(absoluteSourceFilePath, `${percent}%`);

        if (config?.isDebug) {
          console.log(`${percent}%`);
        } else if (
          percent % 2 === 1 &&
          percent !== shownPercentage &&
          config?.isStatusLogged
        ) {
          shownPercentage = percent;
          console.log(`${destinationName} status: ${percent}%`);
        }
      })
      .on("end", async () => {
        if (!config?.keepOriginal) {
          // new file is created, old one is not overwritten, and we need to remove the old
          // NB: don't wait for it!
          await fs.rm(absoluteSourceFilePath);
        }

        unlock(absoluteSourceFilePath);

        if (fs.existsSync(finalDestinationPath)) {
          // already exists for some reason...
          await fs.rm(destinationPath);
          resolve(destinationPath);
          return;
        }

        if (!fs.existsSync(destinationPath)) {
          //doens't existalthough we just converted it, multiple processes doing the same, probably
          resolve(undefined);
          return;
        }

        await fs.rename(destinationPath, finalDestinationPath);
        resolve(finalDestinationPath);
      })
      .on("error", (e: Error) => {
        console.log({ e });
        resolve(undefined);
      });
  });

  return finalPath;
}

