fileExplorerOpen.rawText:
 async (
  projectRelativePath: string
): Promise<{ isSuccessful: boolean; message: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return {
      isSuccessful: false,
      message: "No projectroot",
    };
  }

  const absolutePath = path.join(projectRoot, projectRelativePath);

  if (!absolutePath || !fs.existsSync(absolutePath)) {
    return { isSuccessful: false, message: "This path doesn't exist" };
  }

  const pathStats = await fs.stat(absolutePath);

  const folderPath = pathStats.isDirectory()
    ? absolutePath
    : path.parse(absolutePath).dir;

  var isWindows = process.platform === "win32";

  const command = isWindows ? `start ${folderPath}` : `open ${folderPath}`;

  execSync(command);

  return { isSuccessful: true, message: "Opened" };
}

getFileType.rawText:
 (fullPath: string): keyof typeof typeIcons => {
  if (fullPath.endsWith(".ts")) return "typescript";
  if (fullPath.endsWith(".tsx")) return "typescript";
  if (fullPath.endsWith(".js")) return "javascript";
  if (fullPath.endsWith(".jsx")) return "javascript";
  if (fullPath.endsWith(".md")) return "markdown";
  if (fullPath.endsWith(".mdx")) return "markdown";
  if (fullPath.endsWith(".json")) return "json";
  if (!fullPath.split("/").pop()?.includes(".")) return "folder";

  return "other";
}

MatchingText.rawText:
 (props: {
  text: string;
  search: string;
  defaultTextClassName: string;
  matchTextClassName: string;
  truncateLength?: number;
}) => {
  const {
    truncateLength,
    text,
    defaultTextClassName,
    matchTextClassName,
    search,
  } = props;

  // 1: find index of search in text
  const matchIndex = text.toLowerCase().indexOf(search.toLowerCase());

  // 2: find stuff before that and after that
  const beforeText = text.slice(0, matchIndex);
  const afterText = text.slice(matchIndex + search.length);
  const matchText = text.slice(matchIndex, matchIndex + search.length);

  const totalLength = beforeText.length + search.length + afterText.length;

  const reduceCharactersAmount = truncateLength
    ? totalLength - truncateLength
    : 0;

  const beforeTextLengthPercentage =
    beforeText.length / (beforeText.length + afterText.length);
  const afterTextLengthPercentage =
    afterText.length / (beforeText.length + afterText.length);

  const reduceBeforeCharacters = Math.round(
    beforeTextLengthPercentage * reduceCharactersAmount
  );
  const reduceAfterCharacters = Math.round(
    afterTextLengthPercentage * reduceCharactersAmount
  );

  const truncatedBeforeText =
    reduceBeforeCharacters > beforeText.length && truncateLength
      ? beforeText.slice(0, beforeText.length - reduceBeforeCharacters - 2) +
        ".."
      : beforeText;
  const truncatedAfterText =
    truncateLength && reduceAfterCharacters > afterText.length
      ? ".." + afterText.slice(afterText.length - reduceAfterCharacters - 2)
      : afterText;

  // 3: render before, match, and after
  return matchIndex === -1 ? (
    // with no match, truncate at truncateLength at the end
    <P title={text} className={defaultTextClassName}>
      {truncateLength && text.length > truncateLength
        ? text.substring(0, truncateLength - 2) + ".."
        : text}
    </P>
  ) : (
    /**
     * with a match, the total length needs to be `truncateLength`
     *
     * the `search` result should not be truncated
     *
     * `beforeText` should be truncated at the end to reduce to `truncateLength`
     * `afterText` should be truncated at the beginning to reduce to `truncateLength`
     *
     * it should be truncated as much as needed on both sides, but we must take into account at the length of the sides as well
     */
    <P
      title={text} //`(${reduceCharactersAmount}== ${reduceBeforeCharacters} <> ${reduceAfterCharacters} .... ${beforeTextLengthPercentage}, ${afterTextLengthPercentage}) ${text}`
      className={defaultTextClassName}
    >
      {beforeText}
      <Span className={matchTextClassName}>{matchText}</Span>
      {afterText}
    </P>
  );
}

PathSearchResults.rawText:
 (props: {
  paths: string[];
  search: string;
  augmentedWords?: AugmentedWord[];
}) => {
  const { paths, search, augmentedWords } = props;

  /**
   * TODO: include this in the results somehow nicely...
   */
  const augmentedWordResults = augmentedWords?.filter((x) => {
    const word = x.isCaseInsensitive ? x.word.toLowerCase() : x.word;
    const searchQuery = x.isCaseInsensitive ? search.toLowerCase() : search;

    const isMatch = word.includes(searchQuery);

    return isMatch;
  });

  const results = paths
    .map((path) => {
      const augmentedWordsThisPath = augmentedWordResults?.filter(
        (x) => x.queryPath === path
      );

      return { path, augmentedWordsThisPath };
    })
    .filter((item) => {
      const isPathMatch = item.path
        .toLowerCase()
        .includes(search.toLowerCase());
      const isMatch = item.augmentedWordsThisPath?.length || isPathMatch;

      return isMatch;
    })
    .slice(0, 100);

  return (
    <Div>
      {results.map((item) => {
        const filename = item.path.split("/").pop()!;

        return (
          <ALink href={`/${item.path}`}>
            <Div className="px-2 hover:dark:bg-gray-600">
              <MatchingText
                truncateLength={30}
                text={filename}
                search={search}
                defaultTextClassName=""
                matchTextClassName="text-blue-500"
              />

              <MatchingText
                truncateLength={30}
                text={item.path}
                search={search}
                defaultTextClassName="text-xs"
                matchTextClassName="text-blue-500"
              />

              {item.augmentedWordsThisPath?.map((augmentedWord) => {
                return (
                  <MatchingText
                    truncateLength={30}
                    text={augmentedWord.word}
                    search={search}
                    defaultTextClassName="text-xs"
                    matchTextClassName="text-blue-500"
                  />
                );
              })}
            </Div>
          </ALink>
        );
      })}
    </Div>
  );
}

