useFileWriter.rawText:
 (props: {
  markdownModelName?: keyof DbModels;
  projectRelativeFilePath?: string;
  initialWriterView?: WriterViewEnum;
  disabledMenuItems?: string[];
  hideButtons?: boolean;
}) => {
  const {
    markdownModelName,
    projectRelativeFilePath,
    initialWriterView,
    disabledMenuItems,
    hideButtons,
  } = props;

  // fullPath is the project relative path
  const {
    data: fileContentsResult,
    isLoading,
    refetch,
  } = useGetFileContents(projectRelativeFilePath);
  const fileContents = fileContentsResult?.result?.fileContents;

  // console.log({ fileContents });
  const [unsavedFiles, setUnsavedFiles] = useStore("unsavedFiles");

  const unsavedFileContent = projectRelativeFilePath
    ? unsavedFiles[projectRelativeFilePath]
    : undefined;

  const isSaved = projectRelativeFilePath
    ? unsavedFileContent === undefined
    : true;

  const value = isSaved ? fileContents || "" : unsavedFileContent || "";
  const onChange = (text: string) => {
    if (!projectRelativeFilePath) return;
    setUnsavedFiles({ ...unsavedFiles, [projectRelativeFilePath]: text });
  };

  const save = async () => {
    if (!projectRelativeFilePath) return;
    const apiResult = await saveFileContents(projectRelativeFilePath, value);
    await refetch();
    const { isSuccessful, result, message } = apiResult;

    if (!isSuccessful || !result?.isSuccessful) {
      toast({
        title: "Error",
        body: result?.message || message || "Something went wrong",
      });
    } else {
      // removing the file that is now saved
      const newUnsavedFiles = { ...unsavedFiles };
      // NB: for some reason, delete` doesn't let the updated store rerender the object. React doesn't see the change! The solution is to also set it to undefined first.
      newUnsavedFiles[projectRelativeFilePath] = undefined;
      delete unsavedFiles[projectRelativeFilePath];

      await setUnsavedFiles(newUnsavedFiles);
    }
  };
  useHotkey(isCtrlS, save, [projectRelativeFilePath, value]);

  const errorMessage = !fileContentsResult?.isSuccessful
    ? fileContentsResult?.message
    : !fileContentsResult?.result?.isSuccessful
    ? fileContentsResult?.result?.message
    : null;

  const renderFileWriter = () => {
    return (
      <Div className="flex flex-1 flex-col h-full">
        {errorMessage ? (
          <P>{errorMessage}</P>
        ) : (
          <WriterInput
            key={projectRelativeFilePath}
            isLoading={isLoading}
            onChange={onChange}
            value={value}
            projectRelativeFilePath={projectRelativeFilePath}
            reload={refetch}
            save={save}
            hideButtons={hideButtons}
            initialWriterView={initialWriterView}
            isSaved={isSaved}
            markdownModelName={markdownModelName}
            disabledMenuItems={disabledMenuItems}
          />
        )}
      </Div>
    );
  };
  return {
    renderFileWriter,
    save,
    isSaved,
  };
}

