isIndexableFileId.rawText:
 (fileId: string): boolean => {
  const isOperationName = hasSubExtension(
    fileId,
    operationUnindexableNamesOrSubExtensions,
    true
  );

  const isFrontendOptionalName = hasSubExtension(
    fileId,
    frontendOptionalFileSubExtensions,
    false
  );

  const isIndex = fileId === "index";

  const isIndexable = !isIndex && !isFrontendOptionalName && !isOperationName;

  return isIndexable;
}

