findDependantsRecursively.rawText:
 async (
  operationName: string,
  already?: string[]
): Promise<string[]> => {
  const dependantOperationNames = await findDependants({
    returnOperationName: true,
    operationName,
  });

  const dependantsUntilNow = [
    operationName,
    ...dependantOperationNames,
    ...(already || []),
  ];

  const recursedDependants: string[] =
    dependantOperationNames.length > 0
      ? (
          await Promise.all(
            dependantOperationNames.map((name) =>
              findDependantsRecursively(name, dependantsUntilNow)
            )
          )
        ).flat()
      : [];

  return [...dependantsUntilNow, ...recursedDependants].filter(onlyUnique2());
}

findDependants.rawText:
 async (config: {
  operationName: string;
  /**
   * Optionally specify a specific import from an operation that you want to find the dependants for
   */
  importName?: string;
  returnOperationName?: boolean;
  /**
   * For efficiency reasons you can provide precalculated imports
   */
  imports?: TsImport[];
  /**
   * If true, only external imports are found
   */
  onlyExternal?: boolean;
}) => {
  const {
    operationName,
    importName,
    returnOperationName,
    imports,
    onlyExternal,
  } = config;

  const resultValueKey: keyof TsImport = returnOperationName
    ? "operationName"
    : "projectRelativePath";
  const theImports = imports || (await db.get("TsImport"));
  const monorepoModules = theImports
    .filter((x) => x.module === operationName)
    .filter((x) => (importName ? x.name === importName : true))
    .filter((x) =>
      onlyExternal ? x.isAbsolute && x.operationName !== operationName : true
    )
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x[resultValueKey] : null
    )
    .filter(notEmpty)
    .filter(onlyUnique);

  return monorepoModules;
}

findDependenciesRecursively.rawText:
 async (
  /**
   * all imports
   */
  imports: TsImport[],
  operations: Operation[],
  operationName: string,
  /**
   * skip recursing on these ones because they are already found
   */
  already: string[],
  ignore?: string[],
  ignoreFilter?: (operationName: string) => boolean
): Promise<string[]> => {
  if (ignore?.includes(operationName)) return [];

  const packageJson = operations.find((x) => x.operationName === operationName);

  if (!packageJson) {
    console.log(
      `WARN: couldn't find packagejson ${operationName}. weird...`,
      operations.slice(0, 5)
    );
    return [];
  }

  const indirectDependencies =
    packageJson.operation?.indirectDependencies || [];

  /**
   * Filter out ignored ones, either by ignore prop or ignore filter
   */
  const onlyIncluded = (operationName: string) =>
    !ignore?.includes(operationName) &&
    (!ignoreFilter || ignoreFilter(operationName));

  const thisImports = imports.filter((x) => x.operationName === operationName);

  const monorepoModules = thisImports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyIncluded)
    // add the indirect ones
    .concat(indirectDependencies);

  const newAlready = already.concat(monorepoModules).filter(onlyUnique);

  const dependencyPromises = monorepoModules
    .filter((x) => !already?.includes(x))
    .map((operationName) =>
      findDependenciesRecursively(
        imports,
        operations,
        operationName,
        newAlready,
        ignore
      )
    );
  const dependencies = (await Promise.all(dependencyPromises)).flat();
  const all: string[] = monorepoModules.concat(dependencies);
  return all;
}

