getDependencyTree.rawText:
 async (
  allImports: TsImport[],
  allExports: TsExport[],
  operationName: string,
  usedImports: string[],
  alreadySearched: string[] = []
): Promise<DependencyTreeChildObject> => {
  console.log(operationName);
  const uniqueExportedNames = findMonorepoExports(allExports, operationName);

  const allMonorepoImports = findMonorepoImports(allImports, operationName);
  const newStack = alreadySearched.concat(operationName);

  const newImportsPerModule = allMonorepoImports
    .map((x) => x.module)
    .filter(onlyUnique2())
    .map((operationName) => {
      return {
        operationName,
        importNames: allMonorepoImports
          .filter((x) => x.module === operationName)
          .map((x) => x.name),
      };
    });

  const children: DependencyTreeChildObject[] = (
    await Promise.all(
      newImportsPerModule.map((perModule) =>
        // don't go infinite recursion
        newStack.includes(perModule.operationName)
          ? undefined
          : getDependencyTree(
              allImports,
              allExports,
              perModule.operationName,
              perModule.importNames,
              newStack
            )
      )
    )
  ).filter(notEmpty);

  const object: DependencyTreeChildObject = {
    operationName,
    dependencyCount: children.length,
    usedExportsCount: usedImports.length,
    allExportsCount: uniqueExportedNames.length,
    cumulativeExportsCount:
      sum(children.map((x) => x.cumulativeExportsCount)) +
      uniqueExportedNames.length,
    cumulativeUsedExportsCount:
      usedImports.length +
      sum(children.map((x) => x.cumulativeUsedExportsCount)),
    usedExports: usedImports,
    // allExports: uniqueExportedNames,
    children,
  };

  return object;
}

getOldDependencyTree.rawText:
 async (
  operationNames: string[],
  stack: string[]
): Promise<DependencyTree | null> => {
  if (operationNames.length === 0) return null;
  const allImports = await db.get("TsImport");

  const dependencyTree = mergeObjectsArray(
    await Promise.all(
      operationNames.map(async (name) => {
        const deps = await findMonorepoModules(allImports, name);
        const newStack = stack.concat(name);
        const filtered = deps.filter((dep) => !newStack.includes(dep));
        const filteredOut = deps.filter((dep) => newStack.includes(dep));
        if (deps.length !== filtered.length) {
          console.log(
            `prevented circular dependency at ${name} (${filteredOut.join(
              ", "
            )} were removed)`
          );
        }
        return {
          [name]: await getOldDependencyTree(filtered, newStack),
        };
      })
    )
  );

  return dependencyTree;
}

getOperationDependencyReasons.rawText:
 async () => {
  const tsImports = await db.get("TsImport");
  const operations = await db.get("Operation");
  const tsFunctions = await db.get("TsFunction");
  const tsInterfaces = await db.get("TsInterface");

  const result = operations.map((operation) => {
    const packageJsonDependencies = operation.dependencies
      ? Object.keys(operation.dependencies)
      : undefined;

    const operationImports = tsImports
      .filter((x) => x.operationName === operation.name)
      .filter((x) => x.isModuleResolved && x.isAbsolute);

    const instances: (TsFunction | TsInterface)[] = [
      ...tsFunctions,
      ...tsInterfaces,
    ];

    const operationInstances = instances.filter(
      (x) => x.operationName === operation.name
    );

    const instancesAndTheirImports = operationInstances.map((tsFunction) => {
      return {
        name: tsFunction.name,
        fileName: tsFunction.operationRelativeTypescriptFilePath,
        imports: operationImports
          .filter(
            (x) =>
              x.operationRelativeTypescriptFilePath ===
              tsFunction.operationRelativeTypescriptFilePath
          )
          .map((x) => `${x.name} from ${x.module}`),
      };
    });

    const importDependencies = operationImports
      .filter(onlyUnique2<TsImport>((a, b) => a.module === b.module))
      .map((x) => x.module);

    const result = {
      operationName: operation.name,
      totalInstancesCount: operationInstances.length,
      totalImportCount: operationImports.length,
      totalDependencyCount: importDependencies.length,
      importDependencies,
      packageJsonDependencies,
      instancesAndTheirImports,
    };

    return result;
  });

  const getImportDependenciesRecurisve = (
    array: any[],
    operationName: string,
    already: string[] = []
  ): string[] => {
    const now: string[] =
      array.find((x) => x.operationName === operationName)
        ?.importDependencies || [];

    const filtered = now.filter((x) => !already.includes(x));

    if (filtered.length !== now.length) {
      console.log("circular skipped");
    }

    const importDependencies: string[] = filtered
      .map((x) =>
        getImportDependenciesRecurisve(array, x, [...already, ...filtered])
      )
      .flat();

    return [operationName, ...importDependencies].filter(onlyUnique2());
  };

  const withCumulative = result.map((item, index, array) => {
    const cumulativeDependencies = getImportDependenciesRecurisve(
      array,
      item.operationName
    );
    return { ...item, cumulativeDependencies };
  });

  return withCumulative;
}

