frontmatterParseToString.rawText:
 (frontmatter: Frontmatter): string => {
  const keys = Object.keys(frontmatter);

  const keyValueLines = keys
    .map((key) => {
      const value = frontmatter[key];

      const parsedFrontmatterLine = `${key}: ${value}`;

      return parsedFrontmatterLine;
    })
    .filter(notEmpty);

  return keyValueLines.length === 0
    ? ""
    : `---\n${keyValueLines.join("\n")}\n---\n`;
}

frontmatterToObject.rawText:
 (
  frontmatter: Frontmatter,
  schema: SimplifiedSchema
): FrontmatterParse => {
  if (!schema.properties || !schema.properties.length) {
    return {};
  }

  const valueObjectArray: { [key: string]: any }[] = schema.properties
    .map((property) => {
      const value = frontmatter[property.name]
        ? frontmatter[property.name].trim()
        : undefined;

      if (value === undefined) return;

      const assetInputType = getAssetInputType(property.name);

      if (
        (property.schema.type === "object" ||
          property.schema.type === "array") &&
        assetInputType?.type
      ) {
        // it's a backendAsset

        const backendAssets: BackendAsset[] = findEmbeds(value).map((x) => {
          return { alt: x.alt, relativePath: x.src };
        });

        if (backendAssets.length === 0) return;

        if (assetInputType.isMultiple) {
          return { [property.name]: backendAssets };
        }

        return { [property.name]: backendAssets[0] };
      }

      if (property.schema.type === "number" && property.name.endsWith("At")) {
        const unixTimestamp = tryParseDate(value);

        return { [property.name]: unixTimestamp };
      }

      if (property.schema.type === "boolean") {
        if (value === "true") return { [property.name]: true };
        if (value === "false") return { [property.name]: false };
        return;
      }

      if (property.schema.type === "number") {
        const number = Number(value);

        if (isNaN(number)) return;

        return { [property.name]: number };
      }

      if (property.schema.type === "null") {
        if (value !== "null") {
          return;
        }

        return { [property.name]: null };
      }

      if (property.schema.type === "string") {
        return { [property.name]: parseFrontmatterString(value) };
      }

      if (property.schema.type === "array") {
        // turn into comma separated
        // Let's only support string[] for now!

        const itemString = value
          .split(",")
          .map((x) => x.trim())
          .map(parseFrontmatterString);

        return { [property.name]: itemString };
      }

      if (property.schema.type === "object") {
        // should not be supported
        return;
      }

      return;
    })
    .filter(notEmpty);

  const parse = mergeObjectsArray(valueObjectArray);

  return parse;
}

getFrontmatterValueString.rawText:
 (
  value: FrontmatterValue
): string | null => {
  if (value === undefined) return null;
  if (value === null) return "null";
  if (typeof value === "number") return String(value);
  if (typeof value === "boolean") return String(value);
  if (typeof value === "boolean") return String(value);
  if (Array.isArray(value))
    return value.map(quotedOrNot).map(stringifyNewlines).join(", ");
  if (typeof value === "string") return stringifyNewlines(quotedOrNot(value));
  // NB: according to current support this should never happen
  if (typeof value === "object") return `"${JSON.stringify(value)}"`;

  return null;
}

markdownModelTypeToMarkdownString.rawText:
 (
  markdownModelType: Storing<MarkdownModelType>
): string => {
  const { markdown, ...parameters } = markdownModelType;
  const frontmatterString = frontmatterParseToString(parameters);
  return `${frontmatterString}\n${markdown}`;
}

objectToFrontmatter.rawText:
 (
  parse: FrontmatterParse,
  schema: SimplifiedSchema
): Frontmatter => {
  if (!schema.properties || !schema.properties.length) {
    return {};
  }

  const propertyStringValueArray: { [key: string]: string }[] =
    schema.properties
      .map((property) => {
        const value = parse[property.name];

        const assetInputType = getAssetInputType(property.name);

        if (
          (property.schema.type === "object" ||
            property.schema.type === "array") &&
          assetInputType?.type
        ) {
          // it's a backendAsset

          const realValue = value as BackendAsset | BackendAsset[] | undefined;

          if (!realValue || typeof realValue !== "object") {
            // must be an array or object, otherwise it's set to nothing
            return;
          }

          const backendAssets = makeArray(realValue);

          return {
            [property.name]: backendAssets
              .map((backendAsset) => {
                return `![${backendAsset.alt || ""}](${
                  backendAsset.relativePath || ""
                })`;
              })
              .join(", "),
          };
        }

        if (property.schema.type === "number" && property.name.endsWith("At")) {
          if (typeof value !== "number" || (!value && value !== 0)) {
            // we can only process numbers
            return;
          }

          // it's a date, convert to date

          const humanReadableDate = new Date(value).toDateString();
          return { [property.name]: humanReadableDate };
        }

        if (property.schema.type === "boolean") {
          if (value === true) return { [property.name]: "true" };
          if (value === false) return { [property.name]: "false" };
          return;
        }

        if (property.schema.type === "number") {
          if (typeof value !== "number" || (!value && value !== 0)) {
            // we can only process numbers
            return;
          }

          return { [property.name]: String(value) };
        }

        if (property.schema.type === "null") {
          if (value !== null) {
            return;
          }

          return { [property.name]: "null" };
        }

        if (property.schema.type === "string") {
          if (typeof value !== "string") {
            return;
          }
          return { [property.name]: quotedOrNot(stringifyNewlines(value)) };
        }

        if (property.schema.type === "array") {
          // turn into comma separated

          if (!Array.isArray(value)) {
            return;
          }

          // Let's only support string[] for now!
          //   const type = property.schema.items?.map(x=>x.schema)?.[0]

          const itemString = value
            .map((item: any) => {
              if (typeof item !== "string") return;

              return quotedOrNot(stringifyNewlines(item));
            })
            .filter(notEmpty)
            .join(", ");

          return { [property.name]: itemString };
        }

        if (property.schema.type === "object") {
          // should not be supported
          return;
        }

        return;
      })
      .filter(notEmpty);

  const frontmatter = mergeObjectsArray(propertyStringValueArray);

  return frontmatter;
}

parseFrontmatterString.rawText:
 (value: string) => {
  const withoutQuotes = value.includes(",")
    ? value.substring(1, value.length - 1)
    : value;

  const withNewlines = withoutQuotes.replaceAll("\\n", "\n");

  return withNewlines;
}

quotedOrNot.rawText:
 (string: string) => {
  const returnString = string?.includes(",") ? `"${string}"` : string;
  return returnString;
}

addDefaultValues.rawText:
 (
  bareItem: Creation<AugmentedAnyModelType>,
  isKvmdStorage?: boolean
): AugmentedAnyModelType => {
  const now = Date.now();

  const defaultValues = {
    createdAt: now,
    updatedAt: now,
    deletedAt: 0,
    createdFirstAt: now,
  };

  const shouldCreateSlugFromName =
    !!bareItem.name && bareItem.slug === undefined;

  const slugifiedSlug = bareItem.slug ? slugify(bareItem.slug) : undefined;

  const isValidSlug =
    bareItem.slug === undefined || slugifiedSlug === bareItem.slug;

  if (!isValidSlug) {
    log(
      `${bareItem.slug} is not a slugified slug, it has been altered to a valid slug`,
      { type: "warning" }
    );
  }

  const slug: string | undefined = shouldCreateSlugFromName
    ? slugify(bareItem.name)
    : slugifiedSlug;

  const id =
    bareItem.id === undefined
      ? isKvmdStorage
        ? kebabCase(bareItem.name)
        : generateId()
      : bareItem.id;

  const item = {
    ...defaultValues,
    ...bareItem,
    slug,
    id,
  } as AugmentedAnyModelType;

  return item;
}

alterAny.rawText:
 async (
  dbStorageMethod: DbStorageMethod,
  ...alterParameters: AlterParameters
): Promise<DbQueryResult> => {
  if (dbStorageMethod === "csv") return alterCsv(...alterParameters);
  if (dbStorageMethod === "jsonSingle")
    return alterJsonSingle(...alterParameters);
  if (dbStorageMethod === "keyValueMarkdown") {
    // this case never happens, this is never the case
    log("Wut??? this case should be prevented by its parent function", {
      type: "warning",
    });
    return alterKeyValueMarkdown(...alterParameters);
  }
  if (dbStorageMethod === "markdown") return alterMarkdown(...alterParameters);
  return alterJsonMultiple(...alterParameters);
}

alterCsv.rawText:
 async (dbFileLocation, alterFn) => {
  const { absolutePath } = dbFileLocation;
  const bareData =
    (await readCsvFile<Storing<CsvItemType>>(absolutePath)) || [];
  const isNewFile = !bareData;
  const { newStoredData, ...queryResult } = alterFn(bareData);
  if (newStoredData.length === 0 && !!bareData) {
    // NB: if new array is empty, just remove the file
    await fs.rm(absolutePath);
    return queryResult;
  }

  const newDataString = csvItemArrayToCsvString(newStoredData);
  const isSuccesful = await writeStringToFile(absolutePath, newDataString);
  if (!isSuccesful) return { isSuccesful, message: "Could not write to file" };
  return {
    isNewFile,
    isSuccesful,
    ...queryResult,
  };
}

alterJsonMultiple.rawText:
 async (
  dbFileLocation,
  alterFn
) => {
  const { absolutePath } = dbFileLocation;
  const storedJson = await readJsonFile<Storing<AugmentedAnyModelType>[]>(
    dbFileLocation.absolutePath
  );

  const storedData = storedJson || [];

  const isNewFile = !storedJson;
  const { newStoredData, ...queryResult } = alterFn(storedData);

  if (newStoredData.length === 0 && !!storedJson) {
    // NB: if new stored data is an empty array, just remove the file
    await fs.rm(absolutePath);
    return { isNewFile: false, ...queryResult };
  }

  const isSuccesful = await writeJsonToFile(absolutePath, newStoredData);

  if (!isSuccesful) return { isSuccesful, message: "Could not write to file" };
  return {
    isNewFile,
    isSuccesful,
    ...queryResult,
  };
}

alterJsonSingle.rawText:
 async (
  dbFileLocation,
  alterFn
) => {
  const { absolutePath } = dbFileLocation;
  const storedJson = await readJsonFile<Storing<AugmentedAnyModelType>>(
    dbFileLocation.absolutePath
  );
  const isNewFile = !storedJson;
  const storedData = storedJson ? [storedJson] : [];
  const { newStoredData, ...queryResult } = alterFn(storedData);

  const isSuccesful = await writeJsonToFile(absolutePath, newStoredData[0]);

  if (!isSuccesful) return { isSuccesful, message: "Could not write to file" };
  return {
    isNewFile,
    isSuccesful,
    ...queryResult,
  };
}

alterKeyValueMarkdown.rawText:
 async (
  dbFileLocation,
  alterFn
) => {
  const { absolutePath } = dbFileLocation;

  const kvmdParse: KeyValueMarkdownParse | null = await readKvmdFile(
    absolutePath,
    dbFileLocation
  );

  const isNewFile = !kvmdParse;

  const storedData = kvmdParse?.data || [];

  // console.log({ storedData });
  const { newStoredData, ...queryResult } = alterFn(storedData);
  // console.log({ newStoredData });
  const newKvmdParse: KeyValueMarkdownParse = {
    parameters: kvmdParse?.parameters || {},
    data: newStoredData as KeyValueMarkdownModelType[],
  };

  const newDataString = kvmdParseToMarkdownString(newKvmdParse);
  const isSuccesful = await writeStringToFile(absolutePath, newDataString);

  if (!isSuccesful) return { isSuccesful, message: "Could not write to file" };
  return {
    isNewFile,
    isSuccesful,
    ...queryResult,
  };
}

alterMarkdown.rawText:
 async (dbFileLocation, alterFn) => {
  const { absolutePath } = dbFileLocation;
  const markdownParse = await readMarkdownFile(absolutePath);
  const isNewFile = !markdownParse;
  const markdownModelItem = markdownParseToMarkdownModelType(markdownParse);

  const storedData = markdownModelItem ? [markdownModelItem] : [];
  const { newStoredData, ...queryResult } = alterFn(storedData);
  const finalItem: Storing<MarkdownModelType> = newStoredData[0];
  const markdownString = markdownModelTypeToMarkdownString(finalItem);
  const isSuccesful = await writeStringToFile(absolutePath, markdownString);

  if (!isSuccesful) return { isSuccesful, message: "Could not write to file" };
  return {
    isNewFile,
    isSuccesful,
    ...queryResult,
  };
}

augmentItemWithReferencedDataRecursively.rawText:
 (
  item: AugmentedAnyModelType,

  includeArray: Include[],
  /**
   * Final includeData object to take items from
   */
  includeData: IncludeDataObject
): AugmentedAnyModelType => {
  // basecase
  if (includeArray.length === 0) {
    return item;
  }

  const newItem = includeArray.reduce((itemNow, include) => {
    if (!include.referenceKey) return itemNow;

    const parameterInfo = getReferenceParameterInfo(include.referenceKey);

    if (!parameterInfo.interfaceName) return itemNow;

    const referencedItems = includeData[parameterInfo.interfaceName];

    if (!referencedItems) return itemNow;
    if (!parameterInfo.dataParameterName) return itemNow;
    if (!parameterInfo.keyInModel) return itemNow;

    const referenceItems = referencedItems.filter((x) => {
      if (!parameterInfo.keyInModel) return false;
      const itemReference: string | string[] | undefined =
        itemNow[parameterInfo.parameterName];
      const referenceItemReference = (x as any)[parameterInfo.keyInModel];

      const isMatchingReference = parameterInfo.isReferenceSingleParameter
        ? itemReference === referenceItemReference
        : parameterInfo.isReferenceMultipleParameter
        ? itemReference?.includes?.(referenceItemReference) || false
        : false;

      return isMatchingReference;
    });

    const augmentedReferenceData:
      | AugmentedAnyModelType
      | AugmentedAnyModelType[]
      | undefined = parameterInfo.isReferenceSingleParameter
      ? augmentItemWithReferencedDataRecursively(
          referenceItems[0],
          makeArray(include.include),
          includeData
        )
      : parameterInfo.isReferenceMultipleParameter
      ? referenceItems.map((i) =>
          augmentItemWithReferencedDataRecursively(
            i,
            makeArray(include.include),
            includeData
          )
        )
      : undefined;

    const newItemNow: AugmentedAnyModelType = {
      ...itemNow,
      [parameterInfo.dataParameterName]: augmentedReferenceData,
    };

    return newItemNow;
  }, item);

  return newItem;
}

calculateOperationsObject.rawText:
 async (manualProjectRoot: string) => {
  const operationBasePaths = await exploreOperationFolders({
    basePath: getPathsWithOperations({ manualProjectRoot }),
  });

  const operationsObject = mergeObjectsArray(
    operationBasePaths.map((operationBasePath) => {
      return {
        [getLastFolder(operationBasePath)]: makeRelative(
          operationBasePath,
          manualProjectRoot
        ),
      };
    })
  );

  return operationsObject;
}

findParent.rawText:
 (
  arrayItem: Storing<KeyValueMarkdownModelType>,
  newCategoryStack: CategoryStack
) => {
  const categoryStackWithItself = arrayItem.categoryStack.concat(
    arrayItem.slug
  );
  const locationString = categoryStackWithItself.join(",");
  const newItemLocationString = newCategoryStack.join(",");
  const isSameLocation = locationString === newItemLocationString;

  return isSameLocation;
}

getAugmentedData.rawText:
 async <T>(
  dbFileLocation: DbFileLocation,
  dbStorageMethod: DbStorageMethod
): Promise<T[] | null> => {
  const { absolutePath, modelName, ...modelLocation } = dbFileLocation;

  const parsedPath = path.parse(absolutePath);

  const isSingleMethod =
    dbStorageMethod === "markdown" || dbStorageMethod === "jsonSingle";

  const hasOperationRelativePath = !!modelLocation.operationRelativePath;
  const shouldAddSingleInfo = isSingleMethod && !hasOperationRelativePath;

  const slugifiedName = shouldAddSingleInfo
    ? slugify(parsedPath.name)
    : undefined;
  const isNameValid = shouldAddSingleInfo
    ? slugifiedName === parsedPath.name
    : true;
  if (!isNameValid) {
    log(`${parsedPath.name} is not a valid (slugified) filename`, {
      type: "warning",
    });
  }

  const rootRelativePath =
    modelLocation.operationRelativePath || modelLocation.projectRelativePath;
  const dbModelFolderPath = `db/${pluralize(kebabCase(modelName))}`;

  const dbModelFolderRelativePath = makeRelative(
    trimSlashes(rootRelativePath),
    dbModelFolderPath
  );
  const chunks = dbModelFolderRelativePath.split("/");
  // remove the name
  chunks.pop();
  // What's left is the folders, if any.
  const categoryStack = chunks;

  const singleInfo = shouldAddSingleInfo
    ? { name: slugifiedName, slug: slugifiedName, categoryStack }
    : undefined;
  const ext = parsedPath.ext;

  if (ext === ".json") {
    const jsonContent = await readJsonFile<any>(absolutePath);
    const jsonContentArray = jsonContent ? makeArray(jsonContent) : null;
    const augmented: T[] | null = jsonContentArray
      ? (jsonContentArray.map((x) => ({
          ...x,
          ...modelLocation,
          ...singleInfo,
        })) as T[])
      : null;

    return augmented;
  }

  if (ext === ".csv") {
    const csvContent = await readCsvFile<any>(absolutePath);

    if (!csvContent) return null;

    const augmented: T[] = csvContent.map((x) => ({
      ...x,
      ...modelLocation,
    }));
    return augmented;
  }

  if (ext === ".md") {
    const mdParse = await readMarkdownFile(absolutePath);

    if (!mdParse) return null;
    const frontmatter = mdParse?.parameters;
    if (dbStorageMethod === "markdown") {
      const markdownContent = {
        ...frontmatter,

        markdown: mdParse.raw,
        ...modelLocation,
        ...singleInfo,
        // NB: have to convert to unknown here first because of the specified markdown prop
      } as unknown as T;

      // console.log({ frontmatter, modelLocation, singleInfo });
      return [markdownContent];
    }

    const keyValueMarkdownParse = markdownStringToKvmdParse(
      mdParse.raw,
      dbFileLocation
    );

    // NB: typing as unknown is required because it will only work for keyValueMarkdown models
    const keyValueMarkdown = keyValueMarkdownParse.data as unknown as T[];

    return keyValueMarkdown;
  }

  return null;
}

getDatabaseFiles.rawText:
 async (
  modelName: string,
  mergedConfig: MergedQueryConfig
): Promise<DbFileLocation[]> => {
  const executionId = generateUniqueId();
  const performance: (PerformanceItem | undefined)[] = [];
  getNewPerformance("start", executionId, true);

  const projectRoot =
    mergedConfig?.manualProjectRoot || mergedConfig.projectRoot;
  if (!projectRoot) return [];

  const dbStorageMethod = mergedConfig.dbStorageMethod;

  performance.push(getNewPerformance("get projectRoot", executionId));

  const pattern = getLocationPattern(dbStorageMethod, modelName, mergedConfig);

  performance.push(getNewPerformance("get location pattern", executionId));

  const operationPath = await getMergedConfigOperationPath(
    mergedConfig,
    projectRoot
  );

  performance.push(
    getNewPerformance("get merged config operation path", executionId)
  );

  // console.log({ pattern, operationPath });

  // Please note, it can return false as well, which should continue here
  if (operationPath === undefined) return [];

  const rootFolders = await getRootFolders({
    mergedConfig,
    operationPath,
    projectRoot,
    manualProjectRoot: projectRoot,
  });

  // console.log({ rootFolders });
  performance.push(getNewPerformance("getRootFolders", executionId));

  cleanupTimer(executionId);
  // console.log({ performance });

  /**
  based on configuration and convention, we will fill this array with the files to get data from

  NB: this should contain the actual files, not the patterns
   */
  let dbFiles: DbFileLocation[] = [];

  const isOperationFile =
    !!mergedConfig.operationName &&
    !!operationPath &&
    !!mergedConfig.operationRelativePath;

  if (isOperationFile && !!operationPath) {
    const exactAbsoluteOperationFilePath = path.join(
      operationPath,
      mergedConfig.operationRelativePath!
    );
    //make sure that extension matches `dbStorageMethod`, warn otherwise
    const customExt = mergedConfig.operationRelativePath
      ? path.parse(mergedConfig.operationRelativePath).ext
      : undefined;
    const isWrongExtension =
      customExt !== getDbStorageMethodExtension(dbStorageMethod);

    if (isWrongExtension) {
      log(
        `Incorrect extension found in operationRelativePath, found ${customExt}`,
        { type: "warning" }
      );
    }

    const projectRelativePath = exactAbsoluteOperationFilePath.substring(
      projectRoot.length
    );
    const operationRelativePath =
      mergedConfig.operationName === null
        ? undefined
        : exactAbsoluteOperationFilePath.substring(operationPath.length);

    dbFiles.push({
      modelName,
      absolutePath: exactAbsoluteOperationFilePath,
      operationName: mergedConfig.operationName!,
      projectRelativePath,
      operationRelativePath,
    });
  }

  if (!isOperationFile && mergedConfig.projectRelativePath) {
    const absolutePath = path.join(
      projectRoot,
      mergedConfig.projectRelativePath
    );
    const operationName = null;
    const projectRelativePath = mergedConfig.projectRelativePath;

    dbFiles.push({
      modelName,
      absolutePath,
      operationName,
      projectRelativePath,
    });
  }

  if (!mergedConfig.projectRelativePath && !isOperationFile && pattern) {
    // no exact path

    const conventionedPaths: DbFileLocation[] = (
      await Promise.all(
        rootFolders.map(async (rootFolder) => {
          const absolutePathPattern = path.join(rootFolder.basePath, pattern);
          const projectRelativePath = absolutePathPattern.substring(
            projectRoot.length
          );

          const operationRelativePath =
            rootFolder.operationName === null
              ? undefined
              : absolutePathPattern.substring(rootFolder.basePath.length);

          const parsedPath = path.parse(absolutePathPattern);

          if (parsedPath.name === "*") {
            const fileNames = getWildcardDbFileLocations({
              modelName,
              parsedPath,
              operationName: rootFolder.operationName,
              projectRoot,
              rootFolder,
            });

            return fileNames;
          } else {
            const dbFileLocation: DbFileLocation = {
              modelName,
              absolutePath: absolutePathPattern,
              operationName: rootFolder.operationName,
              projectRelativePath,
              operationRelativePath,
            };
            return [dbFileLocation];
          }
        })
      )
    ).flat();

    dbFiles = dbFiles.concat(conventionedPaths);
  }

  return dbFiles;
}

getDatabaseRootFolder.rawText:
 async (
  operationName: string | null | undefined,
  manualProjectRoot?: string
): Promise<string | undefined> => {
  const projectRoot = manualProjectRoot || getProjectRoot();
  if (!projectRoot) {
    log("getDatabaseRootFolder: No project root found");
    process.exit(1);
  }

  if (!operationName) {
    return projectRoot;
  }

  const operationBasePath = await getOperationPath(operationName, {
    manualProjectRoot,
  });

  if (!operationBasePath) {
    log(`Couldn't find operation base path for ${operationName}`, {
      type: "error",
    });
  }

  return operationBasePath;
}

getDbFileLocation.rawText:
 async (
  /**
   * The ModelLocation properties in the item are completely ignored.
   *
   * Only the ID, slug and categoryStack are used to determine the exact file the item should be stored in.
   *
   * NB: storedItem MUST have a slug or ID, but this should probably be generated before this funciton
   */
  storedItem: Storing<AugmentedAnyModelType>,
  /**
   * Should be the operationName from the modellocation of the actual item.
   *
   * If it is given, this will take priority over the merged query config.
   */
  operationName: null | string,
  mergedConfig: MergedQueryConfig,
  modelName: string
): Promise<DbFileLocation | undefined> => {
  const projectRoot =
    mergedConfig.manualProjectRoot || mergedConfig.projectRoot;

  if (!projectRoot) {
    log("No project root", { type: "error" });
    return;
  }
  const realOperationName = operationName || mergedConfig.operationName;

  const rootFolder = await getDatabaseRootFolder(
    realOperationName,
    mergedConfig.manualProjectRoot
  );

  if (!rootFolder) return;

  const isSingleItemStorageMethod =
    mergedConfig.dbStorageMethod === "jsonSingle" ||
    mergedConfig.dbStorageMethod === "markdown";

  const filename = storedItem.slug || storedItem.id;

  const relativeFolder: string | undefined =
    storedItem.categoryStack?.join?.("/");
  const dbModelFolderRelativeLocation: string =
    isSingleItemStorageMethod && relativeFolder
      ? path.join(relativeFolder, filename)
      : filename;

  const pattern = getLocationPattern(
    mergedConfig.dbStorageMethod,
    modelName,
    mergedConfig
  );
  if (!pattern) return;

  const absolutePath = path.join(
    rootFolder,
    pattern.replace("*", dbModelFolderRelativeLocation)
  );
  if (!absolutePath) return;

  const operationRelativePath = makeRelative(absolutePath, rootFolder);
  const projectRelativePath = makeRelative(absolutePath, projectRoot);

  const dbFileLocation: DbFileLocation = {
    modelName,
    absolutePath,
    operationName: realOperationName || null,
    projectRelativePath,
    operationRelativePath,
  };

  return dbFileLocation;
}

getDbStorageMethodExtension.rawText:
 (
  dbStorageMethod: DbStorageMethod
): string => {
  if (dbStorageMethod === "csv") return ".csv";
  if (dbStorageMethod === "jsonMultiple") return ".json";
  if (dbStorageMethod === "jsonSingle") return ".json";
  if (dbStorageMethod === "keyValueMarkdown") return ".md";
  return ".md";
}

getDefaultLocationPattern.rawText:
 (
  dbStorageMethod: DbStorageMethod,
  modelName: string
): string | undefined => {
  const fileOrFolderName = pluralize(kebabCase(modelName));
  if (dbStorageMethod === "jsonMultiple") return `db/${fileOrFolderName}.json`;
  if (dbStorageMethod === "keyValueMarkdown")
    return `db/${fileOrFolderName}.md`;
  if (dbStorageMethod === "csv") return `db/${fileOrFolderName}.csv`;

  if (dbStorageMethod === "jsonSingle") return `db/${fileOrFolderName}/*.json`;
  if (dbStorageMethod === "markdown") return `db/${fileOrFolderName}/*.md`;
}

getLength.rawText:
 (array: any[]) => {
  if (!Array.isArray(array)) {
    log(`Array is not array`, { type: "error" }, { array });
    return 0;
  }
  return array.length;
}

getLocationPattern.rawText:
 (
  dbStorageMethod: DbStorageMethod,
  modelName: string,
  mergedConfig: MergedQueryConfig
): string | undefined => {
  const { operationRelativePath, projectRelativePath } = mergedConfig;

  if (operationRelativePath) return operationRelativePath;
  if (projectRelativePath) return projectRelativePath;

  return getDefaultLocationPattern(dbStorageMethod, modelName);
}

getMergedConfigOperationPath.rawText:
 async (
  mergedConfig: MergedQueryConfig,
  manualProjectRoot?: string
): Promise<false | string | undefined> => {
  if (mergedConfig.operationName && mergedConfig.operationName !== "*") {
    const operationPath = await getOperationPath(mergedConfig.operationName, {
      manualProjectRoot,
      notUseSdk: !!manualProjectRoot,
    });
    //
    if (operationPath === undefined) {
      log(`Operation wasn't found ${mergedConfig.operationName}`, {
        type: "warning",
      });
    }

    return operationPath;
  }
  // NB: not the same as undefined!
  return false;
}

getParentSlug.rawText:
 (
  item: Storing<KeyValueMarkdownModelType>
): string | undefined => {
  const parentSlug: string | undefined =
    item.categoryStack[item.categoryStack.length - 1];
  return parentSlug;
}

getRootFolders.rawText:
 async (config: {
  manualProjectRoot?: string;
  projectRoot: string;
  mergedConfig: MergedQueryConfig;
  operationPath: string | false;
}): Promise<RootDbFolder[]> => {
  const { mergedConfig, operationPath, projectRoot, manualProjectRoot } =
    config;

  // simple case: specific operation
  if (operationPath && mergedConfig.operationName) {
    return [
      { basePath: operationPath, operationName: mergedConfig.operationName },
    ];
  }

  const shouldSearchOperations = mergedConfig.operationName === "*";
  const shouldSearchProject =
    mergedConfig.operationName === undefined ||
    mergedConfig.operationName === null ||
    mergedConfig.operationName !== "*";

  const projectFolder: RootDbFolder | undefined = shouldSearchProject
    ? { operationName: null, basePath: projectRoot }
    : undefined;

  if (!shouldSearchOperations) {
    // early return for performance reasons
    return shouldSearchProject && projectFolder ? [projectFolder] : [];
  }

  // NB: for this we need to be sure that sdk-operations is up-to-date!
  const realProjectRelativeOperationPathsObject: {
    [operationName: string]: string;
  } = manualProjectRoot
    ? // NB: this is super slow!!! (up to 80ms)
      await calculateOperationsObject(manualProjectRoot)
    : operations;

  const operationFolders: RootDbFolder[] = Object.keys(
    realProjectRelativeOperationPathsObject
  ).map((operationName) => {
    const operationBasePath = path.join(
      projectRoot,
      realProjectRelativeOperationPathsObject[operationName]
    );

    return {
      basePath: operationBasePath,
      operationName,
    };
  });

  const rootFolders: RootDbFolder[] = [
    projectFolder,
    ...operationFolders,
  ].filter(notEmpty);

  return rootFolders;
}

