getWildcardDbFileLocations.rawText:
 async (options: {
  modelName: string;
  parsedPath: ParsedPath;
  operationName: string | null;
  projectRoot: string;
  rootFolder: RootDbFolder;
}): Promise<DbFileLocation[]> => {
  const { modelName, operationName, parsedPath, projectRoot, rootFolder } =
    options;

  if (fs.existsSync(parsedPath.dir)) {
    // if parsedPath ends with *, read everything in the folder

    const exploreAbsolutePaths = (
      await explore({
        basePath: parsedPath.dir,
        extension: getExtension(parsedPath.base),
      })
    ).map((x) => x.path);

    const dbFileLocations: DbFileLocation[] = exploreAbsolutePaths.map(
      (absolutePath) => {
        const dbFileOperationName = rootFolder.operationName;

        const projectRelativePath = absolutePath.substring(projectRoot.length);

        const operationRelativePath = dbFileOperationName
          ? absolutePath.substring(rootFolder.basePath.length)
          : undefined;

        const dbFileLocation: DbFileLocation = {
          modelName,
          absolutePath,
          operationName: dbFileOperationName,
          projectRelativePath,
          operationRelativePath,
        };

        return dbFileLocation;
      }
    );

    return dbFileLocations;
  }

  return [];
}

groupByFile.rawText:
 async <T extends { [key: string]: any }>(
  creationItems: Creation<T>[],
  mergedConfig: MergedQueryConfig,
  modelName: string
): Promise<ItemPerFileObject<T>> => {
  const dataWithLocations = (
    await Promise.all(
      creationItems.map(async (item) => {
        if (!item) {
          console.log("WARN");
          return;
        }

        const fullItem = addDefaultValues(item);
        const storingItem = makeStoringItem(fullItem) as Storing<T>;

        const itemModelLocation = getItemModelLocation(fullItem);

        const dbFileLocation = await getDbFileLocation(
          storingItem,
          itemModelLocation.operationName,
          mergedConfig,
          modelName
        );

        if (!dbFileLocation) return;

        return { storingItem, dbFileLocation };
      })
    )
  ).filter(notEmpty);

  const dataPerStorageFile = dataWithLocations.reduce(
    (fileObject, itemWithLocation) => {
      const { absolutePath } = itemWithLocation.dbFileLocation;

      const newItems = fileObject[absolutePath]
        ? fileObject[absolutePath].items.concat(itemWithLocation.storingItem)
        : [itemWithLocation.storingItem];

      fileObject[absolutePath] = {
        items: newItems,
        dbFileLocation: itemWithLocation.dbFileLocation,
      };

      return fileObject;
    },
    {} as ItemPerFileObject<T>
  );

  return dataPerStorageFile;
}

removeKeyValueMarkdown.rawText:
 (
  storedData: Storing<KeyValueMarkdownModelType>[],
  /** slug to remove */
  slug: string
): DbQueryResult & {
  newStoredData: Storing<KeyValueMarkdownModelType>[];
} => {
  // console.log("hummmmmm");
  // Base case
  const foundItem = storedData.find((item) => item.slug === slug);

  // console.log({ foundItem, storedData, slug });
  if (!foundItem) {
    return {
      newStoredData: storedData,
      amountRemoved: 0,
      message: "slug not found",
      isSuccesful: true,
    };
  }

  if (foundItem.isHeaderCalculated) {
    // console.log("is header calculated, remove all childs");
    // NB: remove all children (items of which the joined categoryStack starts with headerLocation)

    const headerLocation = foundItem.categoryStack
      .concat(foundItem.slug)
      .join(",");

    const newStoredData = storedData.filter((x) => {
      const isHeaderCalculatedToRemove = x.slug === foundItem.slug;
      const isCategoryStackSubset = x.categoryStack
        .join(",")
        .startsWith(headerLocation);

      return !isHeaderCalculatedToRemove && !isCategoryStackSubset;
    });

    const amountRemoved = storedData.length - newStoredData.length;
    return {
      newStoredData,
      amountRemoved,
      isSuccesful: true,
      message: "removed header and all it's children",
    };
  }

  // console.log("ITS ITEM");
  // It's an item...

  const allItemsInThisCategory = storedData.filter(
    (x) => getParentSlug(x) === getParentSlug(foundItem)
  );

  if (allItemsInThisCategory.length === 0) {
    return {
      isSuccesful: false,
      message: "Tautology",
      newStoredData: storedData,
      amountRemoved: 0,
    };
  }

  // If the item is one of many in a category, we can simply remove it, because the category is still relevant
  if (allItemsInThisCategory.length > 1) {
    // console.log("More itesm in cateogry", storedData);
    const newStoredData = storedData.filter((x) => x.slug !== foundItem.slug);
    const amountRemoved = storedData.length - newStoredData.length;
    return {
      newStoredData,
      isSuccesful: true,
      amountRemoved,
      message: "Removed item where item has more items in its category",
    };
  }

  // the item is the only one in a category (allItemsInThisCategory.length === 1)

  const item = allItemsInThisCategory[0];
  const category = storedData.find((x) => x.slug === getParentSlug(item));
  const newStoredDataWithoutItemAndCategory = storedData.filter(
    (x) => x.slug !== item.slug && x.slug !== getParentSlug(item)
  );

  if (!category) {
    // NB: empty categorystack for the item... this means it is a root item, no worries, we did not remove any category, so we don't need to place it back either
    const amountRemoved =
      storedData.length - newStoredDataWithoutItemAndCategory.length;
    return {
      isSuccesful: true,
      amountRemoved,
      message: "Removed it from the root",
      newStoredData: newStoredDataWithoutItemAndCategory,
    };
  }

  /** 
  we need to make the category the first item of its parent (or the start of the file)

  first remove the item and the category, then add it in the right place
  */

  const parentSlug = getParentSlug(category);

  const parentIndex = newStoredDataWithoutItemAndCategory.findIndex(
    (x) => x.slug === parentSlug
  );

  const newItem: Storing<KeyValueMarkdownModelType> = {
    ...category,
    isHeaderCalculated: false,
  };

  if (parentIndex === -1) {
    // parent doesn't exist. This means the category should become an item at the root of the file
    const newStoredData = [newItem, ...newStoredDataWithoutItemAndCategory];
    const amountRemoved = storedData.length - newStoredData.length;
    return { isSuccesful: true, newStoredData, amountRemoved };
  }

  // parent exists, so, like I said before, we need to make the category the first item of its parent (or the start of the file)

  const before = newStoredDataWithoutItemAndCategory.slice(0, parentIndex);
  const after = newStoredDataWithoutItemAndCategory.slice(parentIndex + 1);
  const parent = newStoredDataWithoutItemAndCategory[parentIndex];
  const newStoredData = [...before, parent, newItem, ...after];

  const amountRemoved = storedData.length - newStoredData.length;
  return { newStoredData, isSuccesful: true, amountRemoved };
}

removeMultiple.rawText:
 async (
  dbStorageMethod: DbStorageMethod,
  dbFileLocation: DbFileLocation,
  removeWhere: (content: AugmentedAnyModelType) => boolean
): Promise<DbQueryResult> => {
  const { absolutePath, modelName, ...modelLocation } = dbFileLocation;
  const isSingleItemStorageMethod =
    dbStorageMethod === "jsonSingle" || dbStorageMethod === "markdown";

  // NB: for single item storage methods, we can simply remove the file
  if (isSingleItemStorageMethod) {
    const content = await getAugmentedData<AugmentedAnyModelType>(
      dbFileLocation,
      dbStorageMethod
    );
    const item = content ? content[0] : null;
    if (
      fs.existsSync(dbFileLocation.absolutePath) &&
      canWriteSync(dbFileLocation.absolutePath) &&
      item &&
      removeWhere(item)
    ) {
      const res = await fs.rm(dbFileLocation.absolutePath);
      return { amountRemoved: 1 };
    }

    return { amountRemoved: 0 };
  }

  if (dbStorageMethod === "keyValueMarkdown") {
    return alterKeyValueMarkdown(dbFileLocation, (storedData) => {
      const realStoredData = storedData as Storing<KeyValueMarkdownModelType>[];

      const finalStoredData = realStoredData.reduce((storedDataNow, item) => {
        const completeItem: KeyValueMarkdownModelType = {
          ...item,
          ...modelLocation,
        };

        if (removeWhere(completeItem)) {
          // If remove, remove it with all its children
          const { newStoredData } = removeKeyValueMarkdown(
            storedDataNow,
            completeItem.slug
          );
          return newStoredData;
        }

        // Otherwise it stays the same
        return storedDataNow;
      }, realStoredData as Storing<KeyValueMarkdownModelType>[]);

      return {
        newStoredData: finalStoredData,
        isSuccesful: true,
        amountRemoved: getLength(storedData) - getLength(finalStoredData),
      };
    });
  }

  // otherwise, we'll remove the item from the array
  return alterAny(dbStorageMethod, dbFileLocation, (storedData) => {
    const fullData = storedData.map((item) => ({
      ...item,
      ...modelLocation,
    })) as AugmentedAnyModelType[];

    const newFullData = fullData.filter((item) => {
      return !removeWhere(item);
    });
    const newStoredData = newFullData.map((item) => makeStoringItem(item));
    const amountRemoved = getLength(storedData) - getLength(newFullData);
    return {
      amountRemoved,
      allRemoved: getLength(newFullData) === 0,
      newStoredData,
      isSuccesful: true,
    };
  });
}

upsertItems.rawText:
 async <
  TModels extends AnyModelObject = any,
  TModelName extends string = any
>(
  dbStorageMethod: DbStorageMethod,
  dbFileLocation: DbFileLocation,
  storingItems: Storing<TModels[TModelName]> | Storing<TModels[TModelName]>[],
  onlyInsert?: boolean
): Promise<DbQueryResult> => {
  const storingItemsArray = makeArray(storingItems);

  // Special case!
  // TODO: we don't support multiple items for this case
  if (dbStorageMethod === "keyValueMarkdown") {
    const storingItem = storingItemsArray[0];

    /**
     * Convention for KVMD storage!
     */
    const parentKey = Object.keys(storingItem).find(
      (x) => x.startsWith("parent_") && x.endsWith("Slug")
    );
    const parentSlug = parentKey ? storingItem[parentKey] : undefined;

    return alterKeyValueMarkdown(dbFileLocation, (storedData) => {
      // NB: CategoryStack is overwritten! We are first making sure that categoryStack is a real existing categoryStack based on the parent_xxxSlug.
      const realStoredData = storedData as Storing<KeyValueMarkdownModelType>[];
      const parent = parentSlug
        ? realStoredData.find((x) => x.slug === parentSlug)
        : undefined;

      // NB: we overwrite categoryStack, unless the parentKey wasn't found in the storingItem.
      const categoryStack: string[] = !parentKey
        ? storingItem.categoryStack || []
        : parent
        ? parent.categoryStack.concat(parent.slug)
        : [];

      // console.log({ parentKey, parentSlug, parent, categoryStack });

      const realStoringItem = {
        ...storingItem,
        categoryStack,
        isHeaderCalculated:
          storingItem.isHeaderCalculated !== undefined
            ? storingItem.isHeaderCalculated
            : false,
        comment: storingItem.comment === undefined ? null : storingItem.comment,
        // convert to unknown first because not every TModels[TModelName] is of type KeyValueMarkdownModelType
      } as unknown as Storing<KeyValueMarkdownModelType>;

      return upsertKeyValueMarkdown(realStoredData, realStoringItem);
    });
  }

  return alterAny(dbStorageMethod, dbFileLocation, (storedData) =>
    upsert(storedData, storingItemsArray, onlyInsert)
  );
}

upsertKeyValueMarkdown.rawText:
 (
  storedData: Storing<KeyValueMarkdownModelType>[],
  storingItem: Storing<KeyValueMarkdownModelType>
): DbQueryResult & {
  newStoredData: Storing<KeyValueMarkdownModelType>[];
} => {
  // First case: try to update if the slug is the same
  let amountUpdated = 0;
  const updatedData = storedData.map((i) => {
    const needsUpdate = i.slug === storingItem.slug;
    if (needsUpdate) {
      amountUpdated++;
      // NB: you cannot change something to be a header or not, this is a calculated value
      return { ...storingItem, isHeaderCalculated: i.isHeaderCalculated };
    }
    return i;
  });

  if (amountUpdated > 0) {
    return {
      amountUpdated,
      amountInserted: 0,
      newStoredData: updatedData,
    };
  }

  // If no "update", let's insert

  if (storingItem.isHeaderCalculated) {
    // Headers cannot be inserted.
    const errorMessage = `
      This case is not supported.

You cannot insert a header because it will not have any items, which is not what we want.

If you create items for a parent-item, the parent-item will convert into a header.
`;
    log(`upsertKeyValueMarkdown: ${errorMessage}`, {
      type: "warning",
    });

    return {
      isSuccesful: false,
      newStoredData: storedData,
      amountInserted: 0,
      amountUpdated: 0,
      message: errorMessage,
    };
  }

  // insert an item

  const parentIndex = storedData.findIndex(
    (x) => x.slug === getParentSlug(storingItem)
  );
  const parent: Storing<KeyValueMarkdownModelType> | undefined =
    storedData[parentIndex];
  // If there is no parent, insert it at the start of the file
  if (!parent) {
    const newStoredData = [storingItem, ...storedData];
    return { isSuccesful: true, amountInserted: 1, newStoredData };
  }

  if (parent.isHeaderCalculated) {
    /*
    the parent is a category. in this case, we simply put the item as the first item below that category
    */

    const newStoredData = insertAt(
      storedData,
      makeArray(storingItem),
      parentIndex + 1
    );

    return {
      amountInserted: 1,
      newStoredData,
      isSuccesful: true,
      message: "Inserted the item in the header parent",
    };
  }

  /**

  The parent is an item. in this case:

  1) the parent needs to become a category
  2) the item needs to be placed under it
  3) the parent should be removed
  4) the last item that has the same categoryStack as the parent should be found
  5) This new category with its sole item needs to be placed as the last item value of its parent (below other items, but above sub categories)

  */

  // 1
  const parentHeader = { ...parent, isHeaderCalculated: true };
  // 2
  const newCategory = [parentHeader, storingItem];

  // WORKS console.log({ newCategory });
  // 3
  const storedDataWithoutParentItem = removeIndexFromArray(
    storedData,
    parentIndex
  );

  // WORKS console.log({ storedDataWithoutItem });

  // 4

  const finalItemIndex =
    findLastIndex(
      storedDataWithoutParentItem,
      (item) =>
        item.categoryStack.join(",") === parentHeader.categoryStack.join(",") &&
        !item.isHeaderCalculated
    ) ||
    // NB: not sure if this would fix all edgecases but if the only item in a category is the one that we removed, the finalItemIndex is the parentIndex -1 (but now it's a category)
    parentIndex - 1;

  // if (finalItemIndex === undefined) {
  //   console.log("Unsupported Edgecase... ", { storedData, storingItem });
  //   return {
  //     newStoredData: storedData,
  //     amountRemoved: 0,
  //     amountInserted: 0,
  //     amountUpdated: 0,
  //   };
  // }

  const newStoredData = insertAt(
    storedDataWithoutParentItem,
    newCategory,
    finalItemIndex + 1
  );

  return {
    amountUpdated: 1,
    amountInserted: 1,
    newStoredData,
    message: "Inserted into an item (made that item a category, moved it)",
  };
}

waitForLockfile.rawText:
 async (
  lockfilePath: string
): Promise<boolean> => {
  if (fs.existsSync(lockfilePath)) {
    const unixDate = await fs.readFile(lockfilePath, "utf8");
    const msAgo = Date.now() - Number(unixDate);
    if (msAgo < maxQueryTimeSeconds * 1000) {
      // wait a 100ms!
      // console.log("Waiting for lockfile...");

      await new Promise<undefined>((resolve) =>
        setTimeout(() => resolve(undefined), 100)
      );
      return waitForLockfile(lockfilePath);
    }

    // if we get here, the file exists for more than 10 seconds

    console.log(
      "Warning: lockfile exists more than ten seconds. I'm assuming the query failed, but this is not good! Should never have such slow queries."
    );
  }

  await writeStringToFile(lockfilePath, String(Date.now()));

  return true;
}

