copyAllRelativeFiles.rawText:
 async (
  relativeFilePaths: string[],
  absoluteSourceRoot: string,
  absoluteDestinationRoot: string,
  /**
   * Overwrite existing file or directory
   */
  force?: boolean
) => {
  const promises = relativeFilePaths.map(async (relativeFilePath) => {
    const fullSourcePath = path.join(absoluteSourceRoot, relativeFilePath);
    const fullDestinationPath = path.join(
      absoluteDestinationRoot,
      relativeFilePath
    );
    if (fs.existsSync(fullSourcePath)) {
      await fs.cpAsync(fullSourcePath, fullDestinationPath, {
        recursive: true,
        force: force || false,
      });
    }
  });
  await Promise.all(promises);

  return true;
}

findFileNameCaseInsensitive.rawText:
 async (
  folderPath: string,
  /**
   * fileName with extension
   */
  fileName: string
): Promise<string | undefined> => {
  if (!fs.existsSync(folderPath)) return;

  const results = await fs.readdir(folderPath);

  const foundPath = results
    .map((fileName) => {
      const absolutePath = path.join(folderPath, fileName);
      return absolutePath;
    })
    .find((absolutePath) => {
      const parsedPath = path.parse(absolutePath);
      const isMatchCaseInsensitive =
        parsedPath.base.toLowerCase() === fileName.toLowerCase();

      return isMatchCaseInsensitive;
    });

  return foundPath;
}

