getFolderSizeObject.rawText:
 async (
  absoluteFolderPath: string,
  /**
   * Folders smaller than this won't provide details
   */
  minimumReportSizeMb: number = 0,
  ignoreGenerated: boolean = true
): Promise<ChildObject<{ size: number; name: string }>[]> => {
  const files = await fs.readdir(absoluteFolderPath, { withFileTypes: true });

  const paths = (
    await Promise.all(
      files.map(async (file) => {
        const newPath = path.join(absoluteFolderPath, file.name);

        if (file.isDirectory()) {
          if (
            ignoreGenerated &&
            [...generatedFolders, "public"].includes(file.name)
          )
            return;
          const children = await getFolderSizeObject(newPath);

          const size = sum(children.map((x) => x.size));
          return {
            name: file.name,
            size,
            /**
             *
             */
            children:
              size > minimumReportSizeMb * 1024 * 1024 ? children : undefined,
          };
        }

        if (file.isFile()) {
          const { size } = await fs.stat(newPath);

          return {
            size,
            children: undefined,
            name: file.name,
          };
        }

        return minimumReportSizeMb
          ? undefined
          : { size: 0, children: undefined, name: file.name };
      })
    )
  ).filter(notEmpty);

  return paths;
}

getFolderSize.rawText:
 async (absoluteFolderPath: string): Promise<number> => {
  const files = await fs.readdir(absoluteFolderPath, { withFileTypes: true });

  const paths = files.map(async (file) => {
    const newPath = path.join(absoluteFolderPath, file.name);

    if (file.isDirectory()) return await getFolderSize(newPath);

    if (file.isFile()) {
      const { size } = await fs.stat(newPath);

      return size;
    }

    return 0;
  });

  return (
    (await Promise.all(paths))
      //  NB: lol!
      .flat(Infinity)
      .reduce((i, size) => i + size, 0)
  );
}

getFolder.description:
if the path exists:
- if the pathString is a folder, that is returned.
- if the pathstring is not a folder, returns the pathstring without the file suffix

if the path doesn't exist: returns pathString witout last chunk (this would only work for file paths)

getFolder.rawText:
 (pathString: string) => {
  const parsedPath = path.parse(pathString);
  const hasExtension = parsedPath.ext.length > 0;

  if (hasExtension) {
    // NB: assume it's a file, let's avoid folders with dots!
    const pathChunks = pathString.split("/");
    pathChunks.pop(); //remove the filename
    return pathChunks.join("/");
  } else {
    // NB: it's already a folder!
    return pathString;
  }
}

getLastFolder.rawText:
 (pathString: string) => {
  const lastFolder = getFolder(pathString).split("/").pop()!;
  // console.log({ pathString, lastFolder });
  return lastFolder;
}

getOneFolderUpPath.rawText:
 (folderPath: string) => {
  const pathParts = folderPath.split("/");
  pathParts.pop();
  return pathParts.join("/");
}

getPathCombinations.rawText:
 (
  ...chunksSegments: (string[] | string)[]
): string[] => {
  return (
    chunksSegments
      .map((x) => makeArray(x))
      // small check to prevent crashes in case there is an invalid type given (not a string)
      .map((chunks) => chunks.filter(notEmpty))
      .reduce((cumSegments, chunks) => {
        // first chunk is the combination of itself
        if (cumSegments.length === 0) return chunks;
        // all next chunks use the accummualted segments and reduce them
        const nextCumSegments = cumSegments.reduce((all, segment) => {
          // for every segment add all the chunks to that segment
          const newChunks = chunks.map((chunk) => path.join(segment, chunk));
          //add them to all new cumSegments
          return all.concat(newChunks);
        }, [] as string[]);
        return nextCumSegments;
      }, [] as string[])
  );
}

oneUp.rawText:
 (filename: string): string => {
  const lastNumber = filename.match(/\d+$/)?.[0];
  const newNumber = lastNumber ? String(Number(lastNumber) + 1) : "1";
  return `${filename}${newNumber}`;
}

