parseMd.rawText:
 (mdFilePath: Path): Markdown => {
  const fileContents = fs.readFileSync(mdFilePath, "utf8");
  const fileStats = fs.statSync(mdFilePath);

  const config: matter.GrayMatterOption<string, any> | undefined = undefined;
  const matterResult = matter(fileContents, config);

  const fileName = mdFilePath.split("/").pop()!.replace(/\.md$/, "");

  return {
    content: matterResult.content,
    createdAt: fileStats.birthtimeMs,
    fileName,
    openedAt: fileStats.atimeMs,
    params: matterResult.data,
    updatedAt: fileStats.ctimeMs,
    modifiedAt: fileStats.mtimeMs,
  };
}

removeAllExcept.rawText:
 async (
  folderPath: string,
  config?: {
    /**
     * array of relative folder paths and file paths that should not be removed (include the extensions!)
     */
    ignore?: string[];
    typeToRemove?: "file" | "folder";
  }
): Promise<
  {
    name: string;
    removed: boolean;
  }[]
> => {
  const filesAndFolders = await fs.readdir(folderPath, {
    encoding: "utf8",
    withFileTypes: true,
  });

  const removePromises = filesAndFolders.map((dirent) => {
    if (config?.ignore?.find((x) => x.includes(dirent.name)))
      return { name: dirent.name, removed: false };

    if (config?.typeToRemove === "file" && !dirent.isFile())
      return { name: dirent.name, removed: false };

    if (config?.typeToRemove === "folder" && !dirent.isDirectory())
      return { name: dirent.name, removed: false };

    return fs
      .rm(path.join(folderPath, dirent.name), { recursive: true })
      .then(() => ({ name: dirent.name, removed: true }));
  });

  const promised = await Promise.all(removePromises);
  return promised;
}

renameAndCreate.rawText:
 async (oldPath: string, newPath: string) => {
  const newPathFolderPath = path.parse(newPath).dir;

  if (!fs.existsSync(newPathFolderPath)) {
    await fs.mkdir(newPathFolderPath, { recursive: true });
  }

  await fs.rename(oldPath, newPath);
}

updateSingleNestedJsonFile.rawText:
 async <T extends O>(
  path: string,
  partialNewObject: NestedPartial<T>
) => {
  const json = await readJsonFile<T>(path);
  if (!json) return false;
  const newData = mergeNestedObject(json, partialNewObject);
  const isSuccessful = await writeJsonToFile(path, newData);
  return isSuccessful;
}

writeJsonToFile.rawText:
 async <T>(
  p: Path,
  data: T
): Promise<boolean> => {
  if (!data) {
    console.log({ p, data });
    return false;
  }
  const stringifiedData = JSON.stringify(data, null, 2);

  const folderToBe = getFolder(p);
  if (!fs.existsSync(folderToBe)) {
    //first, make sure the folder exists
    await fs.mkdir(folderToBe, { recursive: true });
  }
  //then write it
  await fs.writeFile(p, stringifiedData, "utf8");
  return true;
}

writeStringToFile.rawText:
 async (
  p: Path,
  data: string
): Promise<boolean> => {
  if (data === undefined || data === null || typeof data !== "string") {
    log(`Incorrect data provided`, data);
    return false;
  }

  const folderToBe = getFolder(p);

  if (!fs.existsSync(folderToBe)) {
    //first, make sure the folder exists
    await fs.mkdir(folderToBe, { recursive: true });
  }

  //then write it
  await fs.writeFile(p, data, "utf8");

  return true;
}

writeToFiles.rawText:
 async (fileObject: {
  [absoluteFilePath: string]: any;
}) => {
  let s = 0;
  let e = 0;
  const writePromises = Object.keys(fileObject).map(async (filePath) => {
    const value = fileObject[filePath];
    const success = await writeJsonToFile(filePath, value);
    if (success) s++;
    if (!success) e++;

    return success;
  });
  await Promise.all(writePromises);
}

getExtension.rawText:
 (fileNameOrPath: string) => {
  const pieces = fileNameOrPath.split(".");
  return pieces.pop()!;
}

getSubExtension.rawText:
 (filename: string): string | undefined => {
  const parts = filename.split(".");
  //removes extension
  parts.pop();
  //returns extension that's left (the sub-extension)
  const subExtension = parts.pop();
  return subExtension;
}

makeRelative.rawText:
 (
  /**
   * absolute path of a file or folder without a slash at the end
   */
  absolutePath: string,
  /**
   * folder path without a slash at the end
   */
  baseFolderPath: string
): string => {
  // basecase should be handled, we don't want to get some weird slice edgecase
  if (absolutePath.length < baseFolderPath.length) return "";

  return absolutePath.slice(baseFolderPath.length + 1);
}

withoutExtension.rawText:
 (fileName: string) => {
  const pieces = fileName.split(".");
  pieces.pop();
  return pieces.join(".");
}

withoutSubExtensions.rawText:
 (fileName: string) => {
  const pieces = fileName.split(".");
  return pieces[0];
}

