getFunctionQueryPaths.rawText:
 async (
  tsFunctions?: TsFunction[]
): Promise<{ nested: NestedWebPage[]; flat: WebPage[] }> => {
  // if you don't provide it, overwrite it from the db
  tsFunctions =
    tsFunctions || (await db.get("TsFunction")).filter((x) => x.isExported);

  // TODO: use the `functionQueryPaths` and add the needed ones to main project and every bundle available
  const bundles = await db.get("BundleConfig");

  // console.log({ tsFunctions });

  const functionQueryPaths: string[] = tsFunctions
    .map((tsFunction) => {
      if (!tsFunction.operationName) {
        console.log(`no name`);
        return;
      }

      // part 1: relative path from project root until operation name
      const operationPath =
        operations[tsFunction.operationName as keyof typeof operations];

      if (!operationPath) return;

      // part 2: src relative folder path (without file)

      const srcRelativeFolderPath = getSrcRelativeFolderPath(
        tsFunction.operationRelativeTypescriptFilePath
      );

      // part 3: name of the function
      const name = tsFunction.name;

      const queryPath = srcRelativeFolderPath
        ? `${operationPath}/${srcRelativeFolderPath}/${name}`
        : `${operationPath}/${name}`;

      return queryPath;
    })
    .filter(notEmpty)
    .sort((a, b) => {
      if (a < b) return -1;
      return 1;
    });

  const flat = functionQueryPaths.map((queryPath) => {
    const webPage: WebPage = {
      queryPath,
      pageData: null,
    };
    return webPage;
  });

  const menuObject = getMenuPagesObject(flat);

  return menuObject;
}

