upsertDevice.rawText:
 async (
  serverContext: Context
): Promise<Storing<Device> | undefined> => {
  // in your function
  const executionId = generateUniqueId();
  const performance: (PerformanceItem | undefined)[] = [];

  performance.push(getNewPerformance("start", executionId, true));

  const authToken: string | undefined = serverContext.data?.authToken;
  const ip = serverContext.ip;

  if (!authToken || authToken.length < 24) {
    console.log("warn upsert device: no authToken");
    return;
  }

  // NB: range: [ <low bound of IP block>, <high bound of IP block> ], the rest is described in the type interface
  // const ipLookup = (geoip.lookup(ip) || {}) as Partial<geoip.Lookup>;
  // const {
  //   city,
  //   area: positionRadiusKm,
  //   ll,
  //   country,
  //   region,
  //   timezone,
  // } = ipLookup;

  // const position: Position | undefined =
  //   !!ll?.[0] && !!ll?.[1] ? { latitude: ll[0], longitude: ll[1] } : undefined;

  const userAgentString = serverContext.req.get("User-Agent");
  const userAgent: IResult = parseUserAgent(userAgentString);

  // const ipInfo: IPInfo = {
  //   ip,
  //   city,
  //   position,
  //   positionRadiusKm,
  //   country,
  //   region,
  //   timezone,
  // };

  const origin = serverContext.req.get("Origin") as string;
  const referer = serverContext.req.get("Referrer") as string;

  const dbPath = getRootPath("db");
  if (!dbPath) return;
  const deviceFilePath = path.join(dbPath, "devices", `${authToken}.json`);

  const exists = fs.existsSync(deviceFilePath);

  const deviceBefore: Storing<Device> | null = exists
    ? await readJsonFile<Storing<Device>>(deviceFilePath)
    : null;

  const newDevice: Storing<Device> | null = !exists
    ? {
        authToken,
        authenticationMethods: [],
        ip,
        createdAt: Date.now(),
        createdFirstAt: Date.now(),
        deletedAt: 0,
        id: authToken,
        lastOnlineAt: Date.now(),
        name: authToken,
        updatedAt: Date.now(),
        userAgentString: userAgentString || "no useragent",
      }
    : null;

  if (newDevice) {
    await writeJsonToFile(deviceFilePath, newDevice);
  }

  //savePageVisit(authToken, {ip}, referer);

  // attach persons and groups
  const groups = await db.get("Group");
  const persons = (await db.get("Person"))
    .filter((x) => deviceBefore?.personIds?.includes(x.id))
    .map((p) => {
      return {
        ...p,
        groups: groups.filter((g) => p.groupSlugs?.includes(g.slug)),
      };
    });

  const augmentedDevice: Storing<Device> = deviceBefore
    ? { ...deviceBefore, persons }
    : newDevice!;

  // console.dir({ augmentedDevice }, { depth: 999 });
  return augmentedDevice;
}

converse.rawText:
 async (searchMessage: string) => {
  const sdkFunctionPaths = await getSdkFunctionPaths();
  if (!sdkFunctionPaths) return false;
  const sdkKeyMatches = findSentenceMatches(
    searchMessage,
    sdkFunctionPaths,
    (x) => x.matcher.replaceAll("/", " ")
  );

  if (sdkKeyMatches.length === 0) {
    log("Wut?", { type: "error" });
  } else if (sdkKeyMatches.length === 1) {
    const match = sdkKeyMatches[0];
    log(`Match: ${humanCase(match.name)}`, {
      type: "success",
    });

    await getParametersAndExecute(match);
  } else {
    log(
      `There are ${sdkKeyMatches.length} things we can do: \n\n${sdkKeyMatches
        .map(
          (match, index) =>
            `${index}: ${match.name} (from ${match.relativeOperationPath})`
        )
        .join(", \n")}`,
      { type: "important" }
    );

    const n = await ask("Which number do you want?");

    if (isNaN(Number(n))) {
      log(`That's not a number`, { type: "error" });
      return;
    }

    const choice = sdkKeyMatches[Number(n)];

    if (!choice) {
      log(`That's not a function from the list`, { type: "error" });
      return;
    }

    await getParametersAndExecute(choice);
  }
}

doCli.rawText:
 async () => {
  const [cliNameString, ...parameterStrings] = process.argv.slice(2);
  const cliPath = `src/cli/${cliNameString}.cli.ts`;
  const tsFunction = (
    await db.get("TsFunction", {
      filter: (fn) => fn.operationRelativeTypescriptFilePath === cliPath,
    })
  )[0];

  if (!tsFunction || !tsFunction.operationName) {
    console.log("not found", cliPath);
    return;
  }

  const fileId = getSrcRelativeFileId(
    tsFunction.operationRelativeTypescriptFilePath
  );

  const operationPath = await getOperationPath(tsFunction.operationName);
  if (!operationPath) return;
  const jsPath = path.join(operationPath, "build", `${fileId}.js`);

  execSync(`node ${jsPath} ${parameterStrings.join(" ")}`, {
    stdio: "inherit",
  });
}

executeSdkFunction.rawText:
 async (
  operationString: string,
  parameters: string[] | undefined
) => {
  const sdkOperation = operationString as keyof typeof sdk;
  const operationFunction = sdk[sdkOperation];

  if (typeof operationFunction === "function") {
    const result = parameters
      ? // @ts-ignore
        operationFunction(...parameters)
      : // @ts-ignore
        operationFunction();

    return result;
  } else {
    log(`Could not find ${operationString}`, { type: "error" });
  }
}

getCachedExportedFunctions.rawText:
 async () => {
  if (functions.length) return functions;
  functions = await db.get("TsFunction");
  exportsArray = await db.get("TsExport");
  const fnIsExported = (fn: TsFunction) =>
    exportsArray.find(
      (e) => e?.operationName === fn?.operationName && e.name === fn.name
    );
  return functions.filter(fnIsExported);
}

getMenu.rawText:
 async () => {
