getParameters.rawText:
 async (parameters?: FunctionParameter[]) => {
  return parameters
    ? oneByOne(parameters, async (parameter) => {
        /** TODO: Walking:
         * - if a parameter is a string, it's easy
         * - if a parameter is a number or boolean, just cast string to that
         * - if a parameter is an object, walk over all keys
         * - if a parameter is/can be an array, just insert the first via the above way, but ask if you want to insert another entry after that
         * - for every entry you can also enter '?'. This will look for all operations that return something that could be used as entry here.
         */
        const answer = await ask(`${parameter.name} (${parameter.required})`);
        return answer;
      })
    : undefined;
}

getSdkFunctionPaths.rawText:
 async (): Promise<false | FnMatch[]> => {
  const menu = await getMenu();

  if (!menu) {
    return false;
  }

  const paths = Object.keys(menu).reduce((all, relativeOperationPath) => {
    //@ts-ignore
    const fnArray: TsFunction[] | undefined = menu[relativeOperationPath];

    return fnArray && Array.isArray(fnArray)
      ? all.concat(
          fnArray.map((x) => {
            return {
              matcher: `${relativeOperationPath}/${x.name}`,
              relativeOperationPath,
              ...x,
            };
          })
        )
      : all;
  }, [] as FnMatch[]);

  return paths;
}

