generateSdkOperations.rawText:
 async (
  /**
   * if not provided, will not generate env-sdks
   */
  bundleConfig?: BundleConfig,
  config?: {
    /**
     * If true, will try to run yarn install before and if it fails, does not continue.
     */
    yarnInstallBefore?: boolean;
    /**
     * if true, yarn install will be skipped when generating the sdks, but run one time afterwards
     *
     * useful if you are sure that there will be multiple yarn Installs needed otherwise
     *
     * for os installation (where all operations already exist, don't do this, yarn installs will probably not happen at all)
     */
    yarnInstallAfter?: boolean;
    manualProjectRoot?: string;
    dryrun?: boolean;
  }
): Promise<boolean> => {
  const manualProjectRoot = config?.manualProjectRoot;
  const projectRoot = manualProjectRoot || getProjectRoot();
  const yarnInstallAfter = config?.yarnInstallAfter;
  const yarnInstallBefore = config?.yarnInstallBefore;
  const dryrun = config?.dryrun;

  if (!projectRoot) {
    return false;
  }

  // NB: install this first, otherwise sdk-operations cannot be found
  const promises = [
    generateOperationsSdk({
      manualProjectRoot,
      dryrun,
    }),
    generateFunctionPathsSdk({
      manualProjectRoot,
      dryrun,
    }),
    generateInterfacePathsSdk({ manualProjectRoot, dryrun }),
    generateDbSdk({
      manualProjectRoot,
      skipYarnInstall: yarnInstallAfter,
      dryrun,
    }),
    generateFunctionSdks({
      manualProjectRoot,
      skipYarnInstall: yarnInstallAfter,
      dryrun,
    }),

    bundleConfig
      ? generateEnvSdks(bundleConfig, {
          manualProjectRoot,
          skipYarnInstall: yarnInstallAfter,
          dryrun,
        })
      : undefined,
  ];

  await Promise.all(promises);

  if (yarnInstallAfter) {
    process.stdout.write(`Installing repo `);
    try {
      // NB: I got an engine error which seemed to break it, so to fix it I ignore engines now https://stackoverflow.com/questions/56617209/the-engine-node-is-incompatible-with-this-module

      const result = execSync("yarn --prefer-offline --ignore-engines", {
        cwd: projectRoot,
        encoding: "utf8",
        stdio: "pipe",
      });

      console.log("‚úÖ");

      return true;
    } catch (e: any) {
      console.log("‚ùå");
      const error: {
        status: number;
        signal: any;
        output: (string | null)[];
        pid: number;
        stdout: string;
        stderr: string;
      } = e;
      console.log(`Could not install:`, error);

      return false;
    }
  }

  return true;
}

getFunctionSdksContent.rawText:
 async (config?: {
  manualProjectRoot?: string;
}): Promise<{
  jsFunctions?: TsFunction[];
  apiFunctions?: TsFunction[];
  uiFunctions?: TsFunction[];
}> => {
  const manualProjectRoot = config?.manualProjectRoot;

  const functionsPerClassification = await getSdkFunctionsPerClassification({
    manualProjectRoot,
  });

  if (!functionsPerClassification) return {};

  const jsFunctions = functionsPerClassification["cjs"].concat(
    functionsPerClassification["esm"]
  );

  const apiFunctions = functionsPerClassification["node-cjs"]
    .concat(functionsPerClassification["node-esm"])
    .concat(functionsPerClassification["cjs"])
    .concat(functionsPerClassification["esm"]);

  const uiFunctions = functionsPerClassification["ui-cjs"].concat(
    functionsPerClassification["ui-esm"]
  );

  return { jsFunctions, apiFunctions, uiFunctions };
}

