getSdkFunctionsPerClassification.rawText:
 async (config?: {
  manualProjectRoot?: string;
}): Promise<FunctionsPerClassification | undefined> => {
  const manualProjectRoot = config?.manualProjectRoot;
  const projectRoot = manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const sdkDependants = await findDependantsRecursively("sdk");

  const tsFunctions = await db.get("TsFunction", { manualProjectRoot });

  const exportedFunctions = tsFunctions
    .filter((x) => x.isExported)
    .filter(tsFunctionIsIndexable)
    .filter((x) => {
      return x.operationName && !sdkDependants.includes(x.operationName);
    })
    .filter(onlyUnique2<TsFunction>((a, b) => a.name === b.name));

  console.log({ exportedFunctions: exportedFunctions.length });

  const operationFolders = await exploreOperationFolders({});
  const operationClassificationObject =
    await getOperationClassificationObject();

  // console.log({ operationClassificationObject });
  const operationIsSdkableObject = mergeObjectsArray(
    await Promise.all(
      operationFolders.map(async (operationFolderPath) => {
        const packageJson = await getPackageJson({ operationFolderPath });
        const isSdkable = packageJson?.operation?.isNotSdkable ? false : true;
        const operationName = getLastFolder(operationFolderPath);

        return { [operationName]: isSdkable };
      })
    )
  );

  const sdkFunctionsPerClassification = mergeObjectsArray(
    operationClassificationConst.map((operationClassification) => {
      const sdkFunctions = exportedFunctions
        .filter((x) => {
          if (!x.operationName) return false;
          const isSdkable = operationIsSdkableObject[x.operationName];
          return isSdkable;
        })
        .filter((x) =>
          tsFunctionIsSdkable(
            x,
            operationClassificationObject,
            operationClassification
          )
        )
        .filter(onlyUnique2<TsFunction>((a, b) => a.name === b.name));

      log(
        `for ${operationClassification} we found ${sdkFunctions.length} functions`,
        { type: "debug" }
      );
      if (sdkFunctions.length === 0)
        log(
          `Warning: 0 functions found for ${operationClassification} operations`,
          { type: "debug" }
        );

      return { [operationClassification]: sdkFunctions };
    })
  ) as FunctionsPerClassification;

  return sdkFunctionsPerClassification;
}

isNonUiOperationBuild.rawText:
 (
  eventName: WatchEventType,
  path: string
) => {
  // for indexation we don't care about any other event than adding or changing a file
  if (!["add", "change"].includes(eventName)) return false;
  // in order to index a file, it must be in an operation, which means it must be in src
  if (!path.includes("/build/")) return false;
  // only ts and tsx files matter, the rest doesn't need to be indexed
  if (!["js"].includes(getExtension(path))) return false;

  // only for operations
  const operationPath = findOperationBasePath(path);
  if (!operationPath) return false;

  // we don't care about ui operations
  const classification = getOperationClassification(operationPath);
  if (!classification || classification.startsWith("ui-")) {
    return false;
  }
  return true;
}

newEnvSdk.rawText:
 async (
  /**
   * NB: if this is not a bundle, a more general purpose bundle config should be used
   */
  bundleConfig: BundleConfig,
  type: "public" | "private",
  config?: {
    manualProjectRoot?: string;
    skipYarnInstall?: boolean;
    dryrun?: boolean;
  }
): Promise<string | undefined> => {
  // 1. gets env from bundle
  const operationName = `sdk-env-${type}`;
  const description = await getSdkDescription(operationName);

  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const skipYarnInstall = config?.skipYarnInstall;
  const dryrun = config?.dryrun;
  const envVariables =
    bundleConfig.customisableBundleConfig[`${type}EnvironmentVariables`];
  const realEnv =
    envVariables && typeof envVariables === "object" ? envVariables : {};

  // 2. creates [type].ts and [type]-local.ts that adheres a type interface

  const typescriptFileString = `export const ${type}EnvironmentVariables = ${JSON.stringify(
    realEnv,
    null,
    2
  )};`;

  const localComment =
    "Optionally, overwrite some of your environent variables locally here. Only applied in development. NB: When working in a team, it may be useful to .gitignore this file!";

  const localTypescriptFileString = `import { ${type}EnvironmentVariables } from "./${type}";\n// ${localComment}\nexport const ${type}LocalEnvironmentVariables: Partial<typeof ${type}EnvironmentVariables> = {};`;

  const srcFileContentObject = {
    [`src/${type}-local.ts`]: localTypescriptFileString,
    [`src/${type}.ts`]: typescriptFileString,
  };

  // keep the same operationconfig as the os project, or make a new one if not found

  const operationBasePath = await newOperationWithFiles(
    operationName,
    description,
    srcFileContentObject,
    { manualProjectRoot: projectRoot, skipYarnInstall, dryrun }
  );
  return operationBasePath;
}

newFunctionKeysSdkOperation.rawText:
 async (
  operationName: string,
  keyVariables: {
    variableName: string;
    values: string[];
  }[],
  config?: {
    manualProjectRoot?: string;
    skipYarnInstall?: boolean;
    dryrun?: boolean;
  }
) => {
  const keysConstString = keyVariables
    .map((keyVariable) => {
      return `export const ${keyVariable.variableName} = [ ${keyVariable.values
        .map((value) => `"${value}"`)
        .join(",\n")} ];`;
    })
    .join("\n\n");

  const result = await newOperationWithFiles(
    operationName,
    undefined,
    { [`src/${operationName}.ts`]: keysConstString },
    { overwriteIfExists: true, ...config }
  );

  return result;
}

newFunctionSdkOperation.rawText:
 async (
  operationName: string,
  tsFunctions: TsFunction[],
  config?: {
    manualProjectRoot?: string;
    skipYarnInstall?: boolean;
    dryrun?: boolean;
  }
) => {
  log(`${operationName} should contain ${tsFunctions.length} functions`, {
    type: "debug",
  });
  const skipYarnInstall = config?.skipYarnInstall;
  const manualProjectRoot = config?.manualProjectRoot;
  const dryrun = config?.dryrun;
  const importsString = tsFunctions
    .map((fn) => `import { ${fn.name} } from "${fn.operationName}";`)
    .join("\n");
  const sdkConstString = `export const sdk = { ${tsFunctions
    .map((fn) => `${fn.name}`)
    .join(",\n")}};`;

  const sdkTypeString = `export type SdkType = typeof sdk;`;

  const sdkTypescriptFileString = `${importsString}\n\n${sdkConstString}\n\n${sdkTypeString}`;

  const description = await getSdkDescription(operationName);

  const filesObject = { [`src/${operationName}.ts`]: sdkTypescriptFileString };
  const result = await newOperationWithFiles(
    operationName,
    description,
    filesObject,
    {
      overwriteIfExists: true,
      manualProjectRoot,
      skipYarnInstall,
      dryrun,
    }
  );

  return result;
}

