augmentMarkdown.rawText:
 async <T extends string | null | undefined>(
  markdown: T,
  config?: {
    isAdmin?: boolean;
    /**
     * TODO: make this faster first by finding all codeblocks in the file first (if this is enabled) and taking a subset of the mapped object then (mapped object  from sdk) and then placing it into the md
     */
    augmentCode?: boolean;
    augmentWords?: boolean;
    augmentStatements?: boolean;
    /**
     * Useful for sites like dev.to and medium where I don't have the capability for creating my own context-menu
     */
    augmentContextualPrompts?: boolean;
    augmentContextualPromptResults?: boolean;
    markdown_projectRelativeFilePath?: string;
    /**
     * If given, all augmented hyperlinks will be absolute to this given domain. Useful for posting on other sites
     */
    externalHost?: string;
  }
): Promise<{
  augmentedMarkdown: T;
  /**
   * Still needed if you want to show the variants and derivatives in your own way
   */
  contextualPromptResults?: ContextualPromptResult[] | null;
  /**
   * Still needed if you watn to make it possible to do prompts in another way
   */
  contextualPromptsObject?: Omit<
    ContextualPromptsObject,
    "databaseContextualPromptSlugs"
  >;
}> => {
  if (!markdown || markdown.trim() === "") {
    // no markdown, just return the input
    return {
      augmentedMarkdown: markdown,
    };
  }

  const {
    isAdmin,
    augmentCode,
    augmentContextualPrompts,
    augmentContextualPromptResults,
    augmentStatements,
    augmentWords,
    markdown_projectRelativeFilePath,
    externalHost,
  } = destructureOptionalObject(config);
  // do stuff with this configuration here

  const fileType = getFileTypeFromPath(markdown_projectRelativeFilePath);

  const augmentedWordObject = augmentCode
    ? await getAugmentedWordObject()
    : undefined;
  // we need all prompts that seem interesting for this type of content

  const isDev = isAdmin || process.env.NODE_ENV !== "production";

  // we can't augment this to the markdown because it's a much more interactive thing, it probably wouldn't work. unless I do something with hyperlinks to the original content, where I make a page that lets you sign up to prompt something ü§Ø this way I can integrate with any place that accepts hyperlinks in posts (linkedin, medium, dev.to, etc.)
  const { databaseContextualPromptSlugs, ...contextualPromptsObject } =
    await getContextualPrompts(
      fileType === "other" ? undefined : fileType,
      undefined,
      isDev
    );

  //WordCombination, WordMatrix, Statement, AugmentedWord, and ContextualPromptResult

  const contextualPromptResults = augmentContextualPromptResults
    ? (
        (await getContextualPromptResults({
          promptSlugs: databaseContextualPromptSlugs,
          prompt_projectRelativePath: markdown_projectRelativeFilePath,
        })) || []
      ).filter((x) => {
        if (isDev) return true;
        return !x.isFake && x.isFavorite;
      })
    : [];

  const wordMatrixs = augmentWords
    ? (await db.get("WordMatrix")).map((x) =>
        getSubsetFromObject(x, [
          "slug",
          "name",
          "description",
          "descriptionAudio",
          "emoji",
          "image",
          "rank",
          "priorityLevel",
          "type",
          "usageCountCalculated",
          "english",
        ])
      )
    : [];

  const mappedWordMatrix = createMappedObject(wordMatrixs, "slug");

  const wordCombinations = augmentWords ? await db.get("WordCombination") : [];
  const statements = augmentStatements ? await db.get("Statement") : [];

  // `tsFunctions, tsVariables, tsInterfaces`: find words between backticks, replace the word between `backticks` with `["term"(---description and maybe code---)](url-to-file-or-#)`
  //  to support multiline tooltips, the augmented markdown should contain `<br>` instead of newlines in the alt. It should be a configuration, and if newlines should be disabled, the tooltip should show only that that is capable of being on a single line.

  const markdownWithCodespanAugmentation = markdown
    .split(" ")
    .map((word) => {
      if (!word.startsWith("`") || !word.endsWith("`")) {
        return word;
      }

      const wordWithoutBackticks = word.slice(1, word.length - 1);

      const augmentedWord = augmentedWordObject?.[wordWithoutBackticks];
      if (!augmentedWord || !augmentedWord.spoiler) {
        return word;
      }

      return makeMarkdownLink(
        wordWithoutBackticks,
        undefined,
        augmentedWord.spoiler
      );
    })
    .join(" ");

  const markdownAfterContextualPromptResults = contextualPromptResults.reduce(
    (previousMarkdown, contextualPromptResult) => {
      if (!contextualPromptResult.selectionString) {
        // console.log("NO selectionString");
        return previousMarkdown;
      }

      const newMarkdown: string = previousMarkdown.replace(
        contextualPromptResult.selectionString,
        `${contextualPromptResult.selectionString} ${makeMarkdownLink(
          "?",
          undefined,
          `${contextualPromptResult.selectionString}\n\n${contextualPromptResult.contextualPromptSlug}\n\n${contextualPromptResult.resultText}`
        )}`
      );

      return newMarkdown;
    },
    markdownWithCodespanAugmentation
  );
  /**
     `statements and selection results`: add ["?"(----reader-agreement, agreement, importancy, results----)](link-to-knowledge-base-or-#) at the end of the selection/statement

     Most expensive now are statements (selection results are limited because they are tied to the file already, but statements are not). We can later enhance the efficiency of statements to index which wordMatrix words they tie to, and filter out the unneeded ones this way.

     */
  const markdownAfterStatements = statements.reduce((previous, statement) => {
    /**
     * how to get this efficiently even if the propmt reulsts aren't always part of it? tough titty
     */
    const newMarkdown = previous.replace(
      statement.description,
      `${statement.description} ${makeMarkdownLink(
        "?",
        undefined,
        `Statement: ${statement.description}.\n\nreaders-agreement: ${statement.readersAgreement}, agreement: ${statement.agreement}, importancy ${statement.importancy}`
      )}`
    );
    return newMarkdown;
  }, markdownAfterContextualPromptResults);

  const markdownAfterWordCombinations = wordCombinations.reduce(
    (previousMarkdown, wordCombination) => {
      const words: Pick<
        WordMatrix,
        | "english"
        | "slug"
        | "name"
        | "description"
        | "descriptionAudio"
        | "emoji"
        | "image"
        | "rank"
        | "priorityLevel"
        | "type"
        | "usageCountCalculated"
      >[] =
        wordCombination.wordMatrixSlugs
          ?.map((slug) =>
            mappedWordMatrix[slug] ? mappedWordMatrix[slug] : undefined
          )
          .filter(notEmpty) || [];

      // TODO: later I can add things like audio and statistics
      const wordDefinitions = words
        .map((word) => {
          return `${word.name}: ${word.description}`;
        })
        .join("\n\n");
      const fullDescription = `${wordCombination.description}\n\n${wordDefinitions}`;
      // `wordCombinations`: replace term with `["term"(----description----)](link-to-website-or-#)` where description is the description of the term, but below that also all word definitions of the underlying wordmatrix words are explained
      const newMarkdown = previousMarkdown.replace(
        wordCombination.name,
        makeMarkdownLink(wordCombination.name, undefined, fullDescription)
      );

      return newMarkdown;
    },
    markdownAfterStatements
  );

  /**
  TODO:
  LATER:

  First I need to fix that I can either ADD things into a broader link that is already encapsulating something. Then I can do either that or create a new one. That will be the best solution

  - `wordMatrixs`: if there is a wordcombination here already this doesn't matter. Otherwise, just replace the word with `["word"(---description, audio, etc---)](#)`

  - `prompts`: add the prompts like `["promptname"(prompt-description)](link-to-execute-prompt?selection=abcdefg)` at the end of a header section for selection prompts, and at the end of a page for page-prompts

  */
  const augmentedMarkdown = markdownAfterWordCombinations as NonNullable<T>;

  // console.log({ augmentedMarkdown });
  return {
    contextualPromptResults,
    augmentedMarkdown,
    contextualPromptsObject,
  };
}

