canSeeFile.rawText:
 (
  parameters: FolderContent | undefined,
  isDev: boolean
) => {
  // dev can always see it
  if (isDev) {
    return true;
  }

  // no parameters seems fine
  if (!parameters) {
    return true;
  }

  if (parameters.isDraft || parameters.isSecret) {
    return false;
  }

  if (
    parameters.authorizedGroup === "premium" ||
    parameters.authorizedGroup === "enterprise"
