findClosestAbsolutePath.rawText:
 async (
  absoluteQueryPath: string
): Promise<{
  absoluteQueryPath: string;
  isFile?: boolean;
  isFolder?: boolean;
}> => {
  const pathExists = fs.existsSync(absoluteQueryPath);

  const stats = pathExists ? await fs.stat(absoluteQueryPath) : null;
  const isFile = stats?.isFile();
  const isFolder = stats?.isDirectory();

  const isValidPath = pathExists && (isFile || isFolder);

  if (isValidPath) return { absoluteQueryPath, isFile, isFolder };

  // no valid path

  const folderUpPath = getFolderJs(absoluteQueryPath);

  return findClosestAbsolutePath(folderUpPath);
}

getContextualPromptResults.rawText:
 async (config?: {
  /**
   * If given, will only provide `ContextualPromptResult`s for this path
   */
  prompt_projectRelativePath?: string;
  /**
   * If given, will only return the db result if it is part of this array
   */
  promptSlugs?: string[];
}): Promise<ContextualPromptResult[] | null> => {
  const { prompt_projectRelativePath, promptSlugs } =
    destructureOptionalObject(config);

  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("NO projectroot in getContextualPromptResults.ts");
    return null;
  }
  const prompt_absolutePath = prompt_projectRelativePath
    ? path.join(projectRoot, prompt_projectRelativePath)
    : undefined;

  const exists = prompt_absolutePath
    ? fs.existsSync(prompt_absolutePath)
    : undefined;

  const stats =
    exists && prompt_absolutePath
      ? await fs.stat(prompt_absolutePath)
      : undefined;

  const isFolder = stats ? stats.isDirectory() : false;
  const isFile = stats ? stats.isFile() : false;
  const pathParse = prompt_absolutePath
    ? path.parse(prompt_absolutePath)
    : undefined;

  if (prompt_absolutePath && !exists) {
    console.log("You gave a path that doesn't exist", { prompt_absolutePath });
    return null;
  }

  const dbResults = (await db.get("ContextualPromptResult")).map(
    omitUndefinedValues
  );

  const onlyForPath = prompt_projectRelativePath
    ? dbResults?.filter(
        (x) => x.prompt_projectRelativePath === prompt_projectRelativePath
      )
    : dbResults;

  const onlyForPrompts = promptSlugs
    ? onlyForPath.filter((result) => {
        if (!result.contextualPromptSlug) return false;

        if (promptSlugs.includes(result.contextualPromptSlug)) return true;

        return false;
      })
    : onlyForPath;

  if (!prompt_projectRelativePath || !pathParse || !prompt_absolutePath) {
    // we don't need the .index one
    console.log("No .index needed", {
      prompt_projectRelativePath,
      pathParse,
      prompt_absolutePath,
    });
    return onlyForPrompts;
  }

  const contextualPromptResultsJsonPath = isFile
    ? path.join(pathParse.dir, ".index", pathParse.name, "prompt-results.json")
    : isFolder
    ? path.join(prompt_absolutePath, ".index", "prompt-results.json")
    : undefined;

  if (
    !contextualPromptResultsJsonPath ||
    !fs.existsSync(contextualPromptResultsJsonPath)
  ) {
    // console.log("results json path doesn't exist", {
    //   contextualPromptResultsJsonPath,
    // });
    return onlyForPrompts;
  }

  const indexResults = await readJsonFile<ContextualPromptResult[]>(
    contextualPromptResultsJsonPath
  );

  if (!indexResults) {
    console.log(
      "We tried to read the results but couldn't",
      contextualPromptResultsJsonPath
    );
    return onlyForPrompts;
  }

  const allResults: ContextualPromptResult[] = onlyForPrompts
    .concat(indexResults)
    .sort((a, b) => {
      if (a.isFake) return 1;
      if (b.isFake) return -1;
      return 0;
    })
    .sort((a, b) => {
      if (a.isFavorite) return -1;
      if (b.isFavorite) return 1;
      return 0;
    });
  return allResults;
}

getContextualPromptsArray.rawText:
 async (
  /**
   * If available, will also get the scoped context
   */
  scopeProjectRelativePath?: string
): Promise<{
  databaseResult: ContextualPrompt[];
  scopeResult?: ContextualPrompt[];
}> => {
  const databaseResult = await db.get("ContextualPrompt");
  if (!scopeProjectRelativePath) {
    return { databaseResult };
  }

  // if we need the scoped contextual prompt, it should be saved in the index of the file itself.
  const projectRoot = getProjectRoot();
  if (!projectRoot) return { databaseResult };

  // it can be a file or a folder

  const scopeAbsolutePath = path.join(projectRoot, scopeProjectRelativePath);

  if (!fs.existsSync(scopeAbsolutePath)) {
    return { databaseResult };
  }

  const stats = await fs.stat(scopeAbsolutePath);

  const parsedScopePath = path.parse(scopeAbsolutePath);
  const filename = stats.isFile() ? parsedScopePath.base : undefined;
  const scopeFolderPath = stats.isDirectory()
    ? scopeAbsolutePath
    : parsedScopePath.dir;
  const scopeAbsoluteDbPath = path.join(
    scopeFolderPath,
    getFolderRelativeScopeDbFilePath(filename)
  );

  if (!fs.existsSync(scopeAbsoluteDbPath)) return { databaseResult };

  // if it exists, add it to the result
  const jsonString = await fs.readFile(scopeAbsoluteDbPath, "utf8");
  const scopeResult = tryParseJson<ContextualPrompt[]>(jsonString);
  if (!scopeResult) return { databaseResult };

  const finalScopeResult: ContextualPrompt[] = scopeResult.map((x) => ({
    ...x,
    scopeProjectRelativePath,
  }));

  // add the scopeProjectRelativePath so we don't need to store it in the db
  return {
    databaseResult,
    scopeResult: finalScopeResult,
  };
}

getContextualPrompts.rawText:
 async (
  /**
   * If not given, will return all
   */
  contextType?: FileType,
  scopeProjectRelativePath?: string,
  isDev?: boolean
): Promise<ContextualPromptsObject> => {
  const contextualPromptsObject = await getContextualPromptsArray(
    scopeProjectRelativePath
  );

  const databaseContextualPromptSlugs =
    contextualPromptsObject.databaseResult.map((x) => x.slug);
  const contextualPrompts = Object.values(contextualPromptsObject).flat();

  const rightType = contextualPrompts
    .filter((x) => (contextType ? x.contextType?.includes(contextType) : true))
    .filter((x) => (isDev ? true : x.isFavorite))
    .map(omitUndefinedValues)
    // sort on fav first
    .sort((a, b) => {
      if (a.isFavorite && !b.isFavorite) return -1;
      if (b.isFavorite && !a.isFavorite) return 1;
      return 0;
    });

  const selectionContextualPrompts = rightType.filter(
    (x) => x.usesSelection || x.usesAnyContext
  );

  const pageContextualPrompts = rightType.filter(
    (x) => (!x.usesSelection && x.usesContext) || x.usesAnyContext
  );

  const folderContextualPrompts = rightType.filter(
    (x) =>
      (!x.usesSelection && !x.usesContext && x.folderContentContext) ||
      x.usesAnyContext
  );
  return {
    selectionContextualPrompts,
    pageContextualPrompts,
    folderContextualPrompts,
    databaseContextualPromptSlugs,
  };
}

getFirstFile.rawText:
 async (
  fullPath: string
): Promise<string | undefined> => {
  const content = await fs.readdir(fullPath, {
    encoding: "utf8",
    withFileTypes: true,
  });
  const files = content.filter((x) => x.isFile());
  const readme = files.find((x) => x.name.toLowerCase() === "readme.md")?.name;
  const index = files.find((x) => x.name.startsWith("index."))?.name;
  const firstFile = readme || index || files[0]?.name || undefined;
  return firstFile;
}

getReaderPageProps.rawText:
 async (
  /**
   * BasePath for this project
   */
  basePath: string | undefined,
  /**
   * QueryPath as in the URL
   */
  queryPath: string,
  /**
   * If true, isDev will be overwritten to be false, even in prod
   */
  isAdmin?: boolean,
  /**
   * If given, will be used instead of basePath, if it exists.
   */
  absoluteBasePath?: string
) => {
  const contextualPromptCategories: CategoryChildObject =
    await getContextualPromptCategories();

  // console.log("getReaderPageProps:", { projectRelativeFilePath });
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return {
      props: {
        notFound: true,
        notFoundReason: "No projectroot",
        contextualPromptCategories,
      },
    };
  }

  const expectedAbsolutePath = absoluteBasePath
    ? path.join(absoluteBasePath, queryPath)
    : basePath
    ? path.join(projectRoot, basePath, queryPath)
    : path.join(projectRoot, queryPath);

  const { absoluteQueryPath, isFile, isFolder } = await findClosestAbsolutePath(
    expectedAbsolutePath
  );
  const isValidPath = absoluteQueryPath === expectedAbsolutePath;

  const folderPath = isFile
    ? path.parse(absoluteQueryPath).dir
    : absoluteQueryPath;

  const dirents = await fs.readdir(folderPath, {
    withFileTypes: true,
    encoding: "utf8",
  });

  // TODO: Confirm that this works if you run the server locally. For development locally it should be working
  const isDev = process.env.NODE_ENV === "development";

  const readmeDirent = dirents.find(
    (x) => x.name.toLowerCase() === "readme.md"
  );

  const readmePath = readmeDirent
    ? path.join(folderPath, readmeDirent.name)
    : undefined;
  const readmeFrontmatter = readmePath
    ? (await readMarkdownFile(readmePath))?.parameters
    : undefined;
  const canSeeFolder = readmePath
    ? canSeeFileContent(readmeFrontmatter, isDev)
    : true;
  // TODO: look in all readmes from basePath until the folder we are, to see if this should be allowed to see. It's very easy now to still see and access content if there's folders inside of folders that should not be allowed to be seen.

  const navigation: FolderContent[] = (
    await Promise.all(
      dirents.map(async (dirent) => {
        // TODO: think about it. Maybe we should do something with symbolic links because we don't want to go deep forever, it could actually be nice to use this feature of the OS as well!"

        const ignoredFilesFolders = [".DS_Store", ".index"];
        if (ignoredFilesFolders.includes(dirent.name)) return;

        const fullPath = path.join(folderPath, dirent.name);

        const type = dirent.isDirectory()
          ? "folder"
          : dirent.isSymbolicLink()
          ? "link"
          : dirent.isFile()
          ? "file"
          : "unknown";

        const firstFile =
          type === "folder" ? await getFirstFile(fullPath) : undefined;

        /**
         * If it's a markdown file, take its parameters
         * If it's a folder, take the parameters of the REAMDE in that folder.
         */
        const frontmatter =
          getExtension(dirent.name) === "md"
            ? (await readMarkdownFile(fullPath))?.parameters
            : type === "folder"
            ? (await readMarkdownFile(path.join(fullPath, "README.md")))
                ?.parameters
            : undefined;
        const folderContent: FolderContent = {
          type,
          name: dirent.name,
          firstFile,
          frontmatter,

          projectRelativePath: makeRelative(
            path.join(folderPath, dirent.name),
            projectRoot
          ),
        };

        return folderContent;
      })
    )
  )
    .filter(notEmpty)
    .map(({ frontmatter, ...other }) => {
      const folderContent: FolderContent = {
        ...other,
        ...expandFrontmatter(frontmatter),
      };
      return folderContent;
    })
    .filter((file) => {
      // filter out the files that should be HIDDEN
      return canSeeFile(file, isDev);
    })
    .map(omitUndefinedValues);

  const markdownParse = isFile
    ? await readMarkdownFile(absoluteQueryPath)
    : null;

  const allowedOtherExtensions = [".ts", ".tsx", ".json"];

  const pathParse = path.parse(absoluteQueryPath);

  const rawContent =
    isFile && allowedOtherExtensions.includes(pathParse.ext)
      ? await fs.readFile(absoluteQueryPath, "utf8")
      : undefined;

  // TODO: in case of a folder, we might be able to show an indexed thing based on the information of this emptyness (just the files and folders around it). This could provide a lot of interesting content already.
  const canSeeContent = canSeeFileContent(markdownParse?.parameters, isDev);

  const fileContentString =
    !canSeeContent || !canSeeFolder || !isFile
      ? null
      : markdownParse
      ? markdownParse.raw
      : rawContent
      ? rawContent
      : null;

  const augmentedResult = await augmentMarkdown(fileContentString, {
    // for now no code yet, it's too slow
    isAdmin,
    augmentCode: false,
    augmentContextualPrompts: false,
    augmentContextualPromptResults: true,
    augmentStatements: true,
    augmentWords: true,
    externalHost: undefined,
    markdown_projectRelativeFilePath: makeRelative(
      absoluteQueryPath,
      projectRoot
    ),
  });

  const props: ReaderProps = {
    contextualPromptCategories,
    notFound: !isValidPath,
    notFoundReason: isValidPath
      ? null
      : `Path isn't valid: ${expectedAbsolutePath}. We found ${absoluteQueryPath}`,
    isFolder,
    canSeeContent,
    unauthorizedWarningMessage: canSeeContent
      ? null
      : "You have to be premium to see this. [Click here to see our offers](/offers)",
    markdown: augmentedResult?.augmentedMarkdown || rawContent || null,
    navigation,
    actualProjectRelativeFilePath: makeRelative(absoluteQueryPath, projectRoot),
    contextualPromptResults: augmentedResult?.contextualPromptResults || null,
    contextualPromptsObject: augmentedResult?.contextualPromptsObject || null,
  };

  return { props };
}

makeMarkdownLink.rawText:
 (text: string, url?: string, alt?: string) => {
  return `[${text}](${url || "#"}${
    alt ? ` "${alt.replaceAll("\n", "<br>").replaceAll('"', '\\"')}"` : ""
  })`;
}

readerPageGetStaticPaths.rawText:
 async () => {
  // TODO: what does context.locale do? can we use that for the domain or so? because it's the only context we have here , so that might be super useful

  const basePath = process.env.NEXT_PUBLIC_BASEPATH;
  // can probably also fetch the domain using the api, right? I might be able to determine what the basepath is based on an environment variable.

  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("NO PROJECTROOT");
    return { fallback: "blocking", paths: [] };
  }

  const realBasePath = basePath
    ? path.join(projectRoot, basePath)
    : projectRoot;

  // const paths = (
