readerPageGetStaticProps.rawText:
 async (
  context: GetStaticPropsContext
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    // NB: this can happen on revalidation and blocked static requests, because we aren't using .projectRoot file anywhere. It's fine
    console.log("NO PROJECTROOT");

    const props: ReaderProps = {
      notFound: true,
      notFoundReason: "This file cannot be found.",
    };

    return { props };
  }

  const basePath = process.env.NEXT_PUBLIC_BASEPATH;

  // the queryPath from
  const queryPath = context.params?.queryPath;
  const realQueryPathArray = queryPath ? makeArray(queryPath) : [];
  const realQueryPath = realQueryPathArray.join("/");

  const staticPropResult = omitUndefinedValues(
    await getReaderPageProps(basePath || "", realQueryPath, false)
  );

  return staticPropResult;
}

