FileActions.rawText:
 (props: {
  basePath?: string;
  navigation?: FolderContent[];
  projectRelativeFilePath?: string;
}) => {
  const { basePath, navigation, projectRelativeFilePath } = props;
  const actions = useFileActions(basePath, navigation);
  const admin = useAdmin();
  const alert = useAlert();
  const { save, isSaved } = useFileWriter({
    projectRelativeFilePath,
    hideButtons: true,
    initialWriterView: "edit",
  });

  const saveAction: ContextMenuItem = {
    getTitle: () => "üíæ Save",
    getIsEnabled: () => !isSaved,
    onClick: () => save(),
  };

  const allActions = [saveAction].concat(actions);

  return (
    <Div className="flex flex-row flex-wrap">
      {allActions.map((action) => {
        if (action.getIsEnabled?.(projectRelativeFilePath) === false) {
          return null;
        }

        return (
          <Div
            className="hover:bg-black/10 p-2 cursor-pointer"
            onClick={async () => {
              await action.onClick(projectRelativeFilePath);

              if (!admin.refetch) {
                alert?.("WTF no admin");
              }
              admin.refetch?.();
            }}
          >
            {action.getTitle(projectRelativeFilePath)}
          </Div>
        );
      })}
    </Div>
  );
}

Menu.rawText:
 (
  props: MenuProps & {
    message?: string;
  }
) => {
  const {
    queryPaths,
    isLoading,
    message,
    augmentedWords,
    webPagesNested,
    webPagesFlat,
    menuHeader,
  } = props;

  const realWebPages: NestedWebPage[] =
    !webPagesNested && queryPaths
      ? getLegacyMenu(queryPaths) || []
      : webPagesNested || [];

  const [showMenu, setShowMenu] = useStore("menu.showMenu");

  useHotkey(isCtrlP, () => setShowMenu(true), []);
  useHotkey(isAltB, () => setShowMenu(!showMenu), [showMenu]);

  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );

  /**
   * This is a slow function to calculate for a lot of paths, needs to be optimised
   */

  const [search, setSearch] = useState("");
  const isSearching = search.length > 0;

  const closeMenu = (
    <Div className="lg:hidden">
      <Div>
        <ClickableIcon
          emoji="‚ï≥"
          onClick={() => {
            setIsMobileMenuEnabled(false);
          }}
        />
      </Div>
    </Div>
  );

  return (
    <Div>
      <Div className="flex flex-row justify-between w-full">
        {menuHeader?.()}
        {closeMenu}
      </Div>

      <Input
        value={search}
        placeholder="Search"
        onChange={(e) => setSearch(e.target.value)}
        className="border px-2 border-white bg-transparent w-full rounded-lg h-9 focus:outline-none"
        autoComplete="off"
      />

      {isLoading ? (
        <FancyLoader />
      ) : isSearching && webPagesFlat ? (
        <PathSearchResults
          search={search}
          paths={webPagesFlat.map((x) => x.queryPath)}
          augmentedWords={augmentedWords}
        />
      ) : !isSearching && realWebPages ? (
        <NestedMenu items={realWebPages} headersClickable />
      ) : (
        <P>{message || "Menu.tsx: Something's wrong, no message given"}) </P>
      )}
    </Div>
  );
}

NavButton.rawText:
 (props: {
  onClick?: () => void;
  href?: string;
  isActive?: boolean;
  title: string;
  id?: string;
  isDisabled?: boolean;
  openContextMenuProps?: object;
}) => {
  const {
    href,
    title,
    onClick,
    isActive,
    openContextMenuProps,
    id,
    isDisabled,
  } = props;

  const className = ` ${isActive ? "dark:bg-blue-800" : "dark:bg-gray-700"} ${
    isDisabled ? "" : "dark:hover:bg-gray-600"
  } ${isActive ? "bg-blue-300" : "bg-gray-100"} ${
    isDisabled ? "" : "light:hover:bg-gray-200"
  } rounded-md border border-black p-2 my-1 mr-2 cursor-pointer lg:mr-4`;
  if (onClick) {
    return (
      <P
        id={id}
        onClick={onClick}
        className={className}
        {...openContextMenuProps}
      >
        {title}
      </P>
    );
  }

  return (
    <ALink id={id} href={href} className={className}>
      <div id={id} {...openContextMenuProps}>
        {title}
      </div>
    </ALink>
  );
}

PromptButton.rawText:
 (props: {
  item: ContextualPrompt;
  markdown?: string | null;
  contextSelection?: string | null;
  projectRelativeFilePath?: string;
}) => {
  const { item, markdown, projectRelativeFilePath, contextSelection } = props;
  const admin = useAdmin();
  const router = useRouter();
  const showPromptAlert = usePromptResultAlert();
  const alert = useAlert();
  const title = `${item.isFavorite ? "‚≠êÔ∏è " : ""}${item.title || item.name}`;

  const projectRelativeFolderPath = getFolderJs(projectRelativeFilePath);
  return (
    <Tooltip
      hoverTimeout={100}
      tooltip={
        <Div>
          <P className="font-bold text-xl">{title}</P>
          {item.description ? (
            <MarkdownContent content={item.description} config={{}} />
          ) : null}
          <br />

          <ClickableIcon
            emoji={
              item.isFavorite
                ? "‚≠êÔ∏è Favorite (click to change)"
                : "üí© Not favorite (click to change)"
            }
            onClick={async () => {
              // go to upsert ContextualPrompt db page.
              const apiResult = await api.upsertDbModel("ContextualPrompt", {
                ...item,
                isFavorite: !item.isFavorite,
              });

              showStandardResponse(apiResult);

              admin.refetch?.();
            }}
          />

          <ClickableIcon
            emoji="‚úèÔ∏è Edit"
            onClick={() => {
              if (!admin.isAdminActive) {
                warningToast(
                  "You can't do this yet! You first need to install your server on your machine"
                );
                return;
              }
              // go to upsert ContextualPrompt db page.
              router.push(`/edit/ContextualPrompt?id=${item.id}`);
            }}
          />

          <ClickableIcon
            emoji="üóë Delete"
            onClick={async () => {
              alert?.(
                "Are you sure?",
                "You're going to remove this prompt from your database",
                [
                  {
                    text: "Do it",
                    style: "destructive",
                    onPress: async () => {
                      const apiResult = await api.deleteDbModel(
                        "ContextualPrompt",
                        item.id
                      );

                      showStandardResponse(apiResult);

                      if (apiResult?.result?.isSuccesful) {
                        admin.refetch?.();
                      }
                    },
                  },
                  {
                    text: "Cancel",
                    style: "cancel",
                  },
                ]
              );
              // delete prompt
            }}
          />

          {contextSelection ? null : (
            <ClickableIcon
              emoji="üí• Apply on folder"
              onClick={() => {
                if (!projectRelativeFolderPath) return;

                alert?.(
                  "Apply on folder",
                  `You're going to apply the prompt ${item.name} on all files in the folder. Are you sure?`,
                  [
                    { style: "cancel", text: "cancel" },
                    {
                      style: "default",
                      text: "Yes",
                      onPress: async () => {
                        const apiResult =
                          await api.processPromptOnFolderWithContext({
                            projectRelativeFolderPath,
                            promptSlug: item.slug,
                            isRecursive: false,
                          });

                        showStandardResponse(apiResult);
                      },
                    },
                    {
                      style: "destructive",
                      text: "Yes, recursive",
                      onPress: async () => {
                        const apiResult =
                          await api.processPromptOnFolderWithContext({
                            projectRelativeFolderPath,
                            promptSlug: item.slug,
                            isRecursive: true,
                          });

                        showStandardResponse(apiResult);
                      },
                    },
                  ]
                );
              }}
            />
          )}
        </Div>
      }
      placement="top"
    >
      <NavButton
        onClick={async () => {
          if (!markdown) return;
          await processPrompt({
            contextualPromptSlug: item.slug,
            showPromptAlert,
            contextualContent: {
              contextContent: markdown,
              contextSelection,
              context_projectRelativeFilePath: projectRelativeFilePath,
            },
          });

          router.replace({
            query: {
              queryPath: router.query.queryPath,
              variant: item.slug,
            },
          });
        }}
        title={item.title || item.name}
      />
    </Tooltip>
  );
}

