ReaderPage.rawText:
 (props: ReaderProps) => {
  const [sidebarHidden, setSidebarHidden] = useState(false);
  const router = useRouter();
  const [customUrl, setCustomUrl, customUrlInfo] = useStore("api.customUrl");
  const [_, setAuthToken] = useStore("api.authToken");
  const admin = useAdmin();
  const queryPath = router.asPath.slice(1);
  const showPromptAlert = usePromptResultAlert();
  const alert = useAlert();
  const basePath = process.env.NEXT_PUBLIC_BASEPATH;

  const {
    markdown,
    actualProjectRelativeFilePath,
    contextualPromptResults,
    contextualPromptsObject,
    navigation,
    isFolder,
    canSeeContent,
    unauthorizedWarningMessage,
    notFound,
  } = props;

  const { renderFileWriter, save, isSaved } = useFileWriter({
    projectRelativeFilePath: actualProjectRelativeFilePath,
    hideButtons: true,
    initialWriterView: "edit",
  });

  const promo = `**What to do now?**
- [ü§ù Let's get to know each other! Book a call](https://calendly.com/karsens/30min)
- [üï∫ Join our discord](https://discord.gg/gehCtKJk)${
    admin.isAdminActive
      ? ""
      : "\n- [üß™ Install your server](/gpt-ide/README.md)"
  }
- [üèõ See my premium services and offers](/offers)
- [ü§Ø Join our AI hacker villa üèù](https://codefrombali.com)`;

  const onFocus = async () => {
    if (!customUrlInfo.hydrated) {
      return;
    }
    const localUrl = "http://localhost:42000";

    if (customUrl === localUrl) {
      return;
    }

    const isAlive = (await apiWithConfig.ping({ apiUrl: localUrl })).result;

    if (isAlive) {
      setCustomUrl(localUrl);
      // NB: Hardcoded?
      setAuthToken("xwjkfycqhakpudymxmysxlei");
      alert?.(
        "Connected to localhost",
        "It seems your server is running locally, you're now connected. You can now interact with your local file system to apply prompts on your own knowledge base!",
        [
          { text: "Okay", style: "default", onPress: () => router.reload() },
          {
            text: "Undo",
            style: "destructive",
            onPress: () => {
              setCustomUrl(null);
            },
          },
        ]
      );
    }
  };

  React.useEffect(() => {
    window.addEventListener("focus", onFocus);
    //   window.addEventListener("blur", onBlur);
    // Calls onFocus when the window first loads
    onFocus();
    // Specify how to clean up after this effect:
    return () => {
      window.removeEventListener("focus", onFocus);
      //  window.removeEventListener("blur", onBlur);
    };
  }, []);

  const projectRelativeBaseFolderPath = isFolder
    ? actualProjectRelativeFilePath
    : getFolderJs(actualProjectRelativeFilePath);

  const filename = isFolder
    ? undefined
    : getFileOrFolderName(actualProjectRelativeFilePath);

  const thePrompts = isFolder
    ? contextualPromptsObject?.folderContextualPrompts || []
    : contextualPromptsObject?.pageContextualPrompts || [];

  const fileContextualPromptResults = contextualPromptResults?.filter(
    (x) => !x.selectionString
  );
  const selectionContextualPromptResults = contextualPromptResults?.filter(
    (x) => !!x.selectionString
  );

  const variantResult = useVariantResult(contextualPromptResults);
  // const fileType =
  //   fileTypePerExtension[
  //     getExtension(
  //       props.projectRelativeFilePath
  //     ) as keyof typeof fileTypePerExtension
  //   ] || undefined;
  // NB: this doesn't allow inline links so we cannot use this type of color highlihgting...
  const codeblockWrappedMarkdown = variantResult?.resultText
    ? variantResult?.resultText
    : // : fileType === "code"
      // ? `\`\`\`tsx\n${markdown}\n\`\`\``
      // : fileType === "data"
      // ? `\`\`\`json\n${markdown}\n\`\`\``
      markdown;

  const contentString = codeblockWrappedMarkdown
    ? codeblockWrappedMarkdown
    : !canSeeContent && unauthorizedWarningMessage
    ? unauthorizedWarningMessage
    : props.notFound
    ? props.notFoundReason || "Not found"
    : "No content";
  const markdownParse = mdToJsonParse(contentString);

  const folderPath = isFolder ? queryPath : getFolderJs(queryPath);

  const markdownContentConfig = {
    fileContextualPromptResults,
    selectionContextualPromptResults,
    projectRelativeBaseFolderPath,
    projectRelativeMarkdownFilePath: actualProjectRelativeFilePath,
    disableSelectionContextMenu: true,
    selectionPrompts: contextualPromptsObject?.selectionContextualPrompts,
  };

  const renderEditContent = () => {
    return (
      <Div className="flex flex-1 flex-col lg:h-full lg:overflow-y-scroll">
        <FileActions
          projectRelativeFilePath={actualProjectRelativeFilePath}
          basePath={basePath}
          navigation={navigation}
        />
        {actualProjectRelativeFilePath ? renderFileWriter() : null}
      </Div>
    );
  };

  const renderPage = () => (
    <Div className="flex flex-1 flex-col">
      <FileActions
        projectRelativeFilePath={actualProjectRelativeFilePath}
        basePath={basePath}
        navigation={navigation}
      />
      {sidebarHidden ? (
        <Div className="max-lg:hidden lg:block flex justify-end">
          <ClickableIcon emoji="<<" onClick={() => setSidebarHidden(false)} />
        </Div>
      ) : null}
      <VariantSelector
        projectRelativeFilePath={actualProjectRelativeFilePath}
        folderPath={folderPath}
        isFolder={isFolder}
        filename={filename}
        contextualPromptResults={fileContextualPromptResults}
      />

      {/* <Shareable > */}
      <Div className={"p-1"}>
        <MarkdownContent
          config={markdownContentConfig}
          content={contentString}
        />
      </Div>
      {/* </Shareable> */}

      <SelectionPrompts
        contentString={contentString}
        projectRelativeFilePath={actualProjectRelativeFilePath}
        selectionContextualPrompts={
          props.contextualPromptsObject?.selectionContextualPrompts
        }
      />

      <Div>
        <P className="font-bold">Apply a prompt on the whole file</P>
        <Div className="flex flex-row flex-wrap">
          {/* Speak */}
          <NavButton
            title="üé§ Speak"
            onClick={async () => {
              alert?.("Coming soon", "coming soon");
            }}
          />
          {/* Free form */}
          {isFolder ? null : (
            <NavButton
              title="üïä Free-form prompt"
              onClick={async () => {
                let question = prompt(
                  "What do you want to ask/prompt about this file?"
                );

                if (!question || question === "") {
                  return;
                }

                let name = prompt(
                  "If we should add this prompt into the database, how should we call it? (leave empty if you don't want to save it)"
                );

                const realName = name === "" ? null : name;

                const customPromptContent = `I am reading this file:\n\n\`\`\`\n%context\n\`\`\`\n\n${question}`;

                await processPrompt({
                  contextualContent: {
                    contextContent: contentString,
                    context_projectRelativeFilePath:
                      actualProjectRelativeFilePath,
                  },
                  showPromptAlert,
                  customPromptContent,
                  saveNewPromptWithName: realName,
                });
              }}
            />
          )}

          {thePrompts.map((prompt) => {
            return (
              <PromptButton
                key={`prompt${prompt.id}`}
                item={prompt}
                markdown={markdown}
                projectRelativeFilePath={actualProjectRelativeFilePath}
              />
            );
          })}
        </Div>
      </Div>
      {isFolder && admin.isAdminActive ? (
        <Div>
          Execute for all files
          <Div className="flex flex-row flex-wrap">
            {contextualPromptsObject?.pageContextualPrompts.map((prompt) => {
              return (
                <NavButton
                  onClick={() => {
                    alert?.(
                      "Not implemented yet",
                      "Execute a certain prompt for all files in a folder, recursively. Should not show the result immediately because these things should be queued and the result is too big to show immediately. There should be settings before you execute all prompts: recursively nested folders? how fast should it be executed? Which model? Etc."
                    );
                  }}
                  title={prompt.title || prompt.name}
                />
              );
            })}
          </Div>
        </Div>
      ) : null}

      <Div className="pt-4 pb-12">
        <MarkdownContent content={promo} config={{}} />
      </Div>
    </Div>
  );

  const variantsOnLg =
    !fileContextualPromptResults ||
    sidebarHidden ||
    !fileContextualPromptResults.length ? null : (
      <Div className="lg:block max-lg:hidden lg:w-[280px] 2xl:w-[400px] lg:h-full lg:overflow-y-scroll">
        <Div className="flex flex-row ml-3">
          <ClickableIcon emoji="‚ï≥" onClick={() => setSidebarHidden(true)} />
          <P className="ml-2 font-bold">File Prompt Results</P>
        </Div>
        {fileContextualPromptResults?.map((result) => {
          if (!result.resultText) return null;
          return (
            <Div className="m-2 p-2 bg-gray-100 dark:bg-slate-800 rounded-lg">
              <Div className="w-full flex justify-between">
                <P className="font-bold">{result.contextualPromptSlug}</P>
                <ClickableIcon
                  emoji="üëÄ"
                  onClick={() => {
                    const query: {
                      queryPath: string | string[] | undefined;
                      variant?: string;
                    } = {
                      queryPath: router.query.queryPath,
                      variant: result.contextualPromptSlug,
                    };

                    router.push({ query }, undefined, { shallow: true });
                  }}
                />
              </Div>
              <MarkdownContent
                config={markdownContentConfig}
                content={result.resultText}
              />
            </Div>
          );
        })}
      </Div>
    );
  return true ? (
    <MarkdownParsePresentation
      markdownParse={markdownParse}
      projectRelativeBaseFolderPath={projectRelativeBaseFolderPath || "/"}
      projectRelativeMarkdownFilePath={actualProjectRelativeFilePath || "/"}
    />
  ) : (
    <Div className="lg:flex lg:flex-row h-full">
      <Menu
        notFound={notFound}
        projectRelativeFilePath={actualProjectRelativeFilePath}
        folderPath={folderPath}
        filename={filename}
        isFolder={isFolder}
        navigation={navigation}
        contextualPromptsObject={contextualPromptsObject}
        fileContextualPromptResults={fileContextualPromptResults}
        selectionContextualPromptResults={selectionContextualPromptResults}
