              emoji: "üõù",
              title: "Slides",
              renderTab: () => (
                <MarkdownParsePresentation
                  markdownParse={markdownParse}
                  projectRelativeBaseFolderPath={
                    projectRelativeBaseFolderPath || "/"
                  }
                  projectRelativeMarkdownFilePath={
                    actualProjectRelativeFilePath || "/"
                  }
                />
              ),
            },
            { emoji: "‚úèÔ∏è", title: "Write", renderTab: renderEditContent },
          ]}
          title={queryPath.split("?")[0]}
        />
      </div>

      {variantsOnLg}
