getAllOperationSourcePaths.rawText:
 async (config?: {
  manualProjectRoot?: string;
}) => {
  const manualProjectRoot = config?.manualProjectRoot;
  const operationFolders: string[] = await exploreOperationFolders({
    basePath: getPathsWithOperations({ manualProjectRoot }),
  });

  const operationSourceFolders = operationFolders.reduce(
    (allSources, operationPath) => {
      const srcPath = path.join(operationPath, "src");

      if (!fs.existsSync(srcPath)) return allSources;

      return allSources.concat(
        [srcPath]
        // TODO: this function would be great, but the inputs in tsconfig contain glob patterns and that doesn't work.
        // getPackageSourcePaths({ packageFolder: operationPath })
      );
    },
    [] as string[]
  );

  return operationSourceFolders;
}

getImportedDependencies.rawText:
 ({
  operationFolderPath,
}: {
  operationFolderPath: string;
}) => {
  const project = getTsMorphProject(operationFolderPath);
  if (!project) return;
  const sourceFiles = project.getSourceFiles("src/*");
  const importModuleStrings = sourceFiles.reduce((imports, sourceFile) => {
    const allImports: ImportDeclaration[] = sourceFile.getImportDeclarations();
    const absoluteImports = allImports.map((i) =>
      i.getModuleSpecifier().getLiteralText()
    );
    return imports.concat(absoluteImports);
  }, [] as string[]);

  const packages = importModuleStrings.filter(isAbsoluteImport).map(getPackage);
  return packages;
}

calculatePackageJsonDependencies.rawText:
 (
  /**
   * Current dependencies object in your operation
   */
  dependencies: PackageInfoObject | undefined,
  /**
   * All imports found in your operation
   */
  imports: Creation<TsImport>[],
  /**
   * All package-json's in your monorepo
   */
  operations: Operation[],
  operationName: string
) => {
  const dependencyImports = imports
    .filter((x) => x.isAbsolute)
    .filter((x) => !isAbsoluteImportBuiltin(x.module))
    .filter((x) => !isImportFromOptionalFile(x))
    .filter(onlyUnique2<Creation<TsImport>>((a, b) => a.module === b.module));

  /**
   *  NB: imports of types are removed and need not to be installed for running this package
   */
  const valueDependencyImports = dependencyImports.filter(
    (x) => x.type === "value"
  );

  const monorepoImports = dependencyImports.filter(
    (x) =>
      operationName &&
      x.isModuleFromMonorepo &&
      !isGeneratedOperationName(x.module)
  );

  const hasGeneratedDependenciesIndexed =
    valueDependencyImports.filter(isImportGenerated).length > 0;

  // console.log({ valueDependencyImports, hasGeneratedDependenciesIndexed });
  // const hasGeneratedImportsIndexed =
  //   dependencyImports.filter(isImportGenerated).length > 0;

  const externalImports = dependencyImports.filter(
    (x) => !x.isModuleFromMonorepo
  );

  const externalDependencyObject = mergeObjectsArray(
    externalImports
      .map((x) => {
        const moduleName = getPackageNameFromAbsoluteImport(x.module);
        if (!moduleName) return;
        const already = dependencies?.[moduleName];
        /**
         * TODO: fetch this from monorepo
         */
        const calculatedVersion = "*";

        /**
         * NB: Minimize the use of "*"
         */
        const version =
          already !== "*" && already !== undefined
            ? already
            : calculatedVersion;
        return { [moduleName]: version };
      })
      .filter(notEmpty)
  );

  const monorepoDependencyObject = mergeObjectsArray(
    monorepoImports
      .map((x) => {
        const moduleName = getPackageNameFromAbsoluteImport(x.module);
        if (!moduleName) return;
        const operation = operations.find((x) => x.name === moduleName);
        if (!operation) return;
        const { version } = operation;
        if (!version) return;
        return { [moduleName]: version };
      })
      .filter(notEmpty)
  );

  // console.log({
  //   imports: imports.length,
  //   dependencyImports: dependencyImports,
  //   dependencies,
  //   monorepoImports,
  //   monorepoDependencyObject,
  //   externalDependencyObject,
  // });

  const newDependencies: PackageInfoObject = {
    ...dependencies,
    ...monorepoDependencyObject,
    ...externalDependencyObject,
  };

  return {
    newDependencies,
    hasGeneratedDependenciesIndexed,
    // hasGeneratedImportsIndexed,
  };
}

findAndWriteImportsExports.rawText:
 async (
  operationBasePath: string,
  /**
   * Manual project root of the operation where the imports and exports needs to be detected and written to database
   */
  manualProjectRoot?: string
): Promise<void> => {
  const operationName = getLastFolder(operationBasePath);
  const sourceFiles = await getAllTsMorphSourceFiles(operationBasePath);

  if (!sourceFiles || sourceFiles.length === 0) {
    const problem = `couldn't load any SourceFiles for ${operationBasePath} (sourceFiles=${sourceFiles})`;
    log(problem, { type: "error" });
    await writeResult({
      success: false,
      message: problem,
      operationName,
      manualProjectRoot,
    });
    return;
  }

  const importsAndExports = await getImportsExports({
    sourceFiles,
    manualProjectRoot,
  });

  if (!importsAndExports) {
    const problem = "Something went wrong creating imports/exports";
    log(problem, { type: "error" });
    await writeResult({
      success: false,
      message: problem,
      operationName,
      manualProjectRoot,
    });

    return;
  }

  const { exports, imports } = importsAndExports;

  // When rebuilding operations, add `packageJson.dependencies` according to imports found.
  const operations = await db.get("Operation", { manualProjectRoot });

  await db.update(
    "Operation",
    // NB: this should always be true, but this extra check will make sure it doesn't update too much...
    (x) => x.name === operationName,
    (packageJson) => {
      const { newDependencies, hasGeneratedDependenciesIndexed } =
        calculatePackageJsonDependencies(
          packageJson.dependencies,
          imports,
          operations,
          operationName
        );

      const newOperation: Operation = {
        ...packageJson,
        operation: {
          ...packageJson.operation,
          hasGeneratedDependenciesIndexed,
        },
        dependencies: newDependencies,
      };
      return newOperation;
    },
    { operationName, manualProjectRoot }
  );

  await db.clear("TsImport", { operationName, manualProjectRoot });
  await db.clear("TsExport", { operationName, manualProjectRoot });

  // @ts-ignore
  await db.upsert("TsImport", imports, { operationName, manualProjectRoot });
  // @ts-ignore
  await db.upsert("TsExport", exports, { operationName, manualProjectRoot });

  await writeResult({
    success: true,
    message: "Succesfully created imports and exports",
    operationName,
    manualProjectRoot,
  });
}

getDefaultSymbolType.rawText:
 (
  symbol: Symbol,
  debug?: boolean
): TypeSpecifier => {
  if (debug) {
    console.log({
      kinds: symbol
        .getDeclarations()
        .map((x) => ({ kind: x.getKindName(), name: x.getText() })),
    });
  }
  const symbolTypeDeclarations = getSymbolTypeDeclarations(symbol);

  const type = symbolTypeDeclarations.length > 0 ? "type" : "value";
  /**
   * checks whether the symbol contains a type declaration which has a generic parameter
   */
  const hasGeneric =
    type === "type"
      ? !!symbolTypeDeclarations.find(
          // NB: type parameters gets the generics
          (declaration) => getHasGeneric(declaration)
        )
      : undefined;

  return { type, hasGeneric };
}

getExportSpecifierNames.rawText:
 (symbol: Symbol) => {
  return (
    symbol
      ?.getDeclarations()
      ?.filter((x) => x.isKind(SyntaxKind.ExportSpecifier))
      .map((x) => x.asKind(SyntaxKind.ExportSpecifier))
      .filter(notEmpty)
      .map((x) => x.getName()) || []
  );
}

getExportSymbolTypeRecursive.rawText:
 (
  symbol: Symbol,
  sourceFile: SourceFile,
  debug?: boolean
): TypeSpecifier | undefined => {
  const exportSpecifierNames = getExportSpecifierNames(symbol);

  if (debug) console.log(exportSpecifierNames);

  if (exportSpecifierNames.length > 0) {
    // get the ImportSpecifier of this ExportSpecifier, and if one exists, recurse this function on it.

    const importSpecifiers = getImportSpecifiersWithNames(
      sourceFile,
      exportSpecifierNames
    );

    if (importSpecifiers.length > 1) {
      // NB: warn if there is more than one as that would be strange

      log(
        `More than one importsSpecifiers with that name: ${exportSpecifierNames.join(
          ","
        )} (imports: ${importSpecifiers
          .map((x) => x.getName())
          .join(",")})... file: ${sourceFile.getFilePath()}`,
        {
          type: "warning",
        }
      );
    }

    if (importSpecifiers.length > 0) {
      return getTypeFromImportSpecifierRecursive(importSpecifiers[0], debug);
    }

    /* REmoved this.... seems that this will make it never do the default one
      else {
        if (debug) {
          console.log(
            "Weird, we couldn't find the importspecifier for exportSpecifiers"
          );
        }
        return { type: undefined };
      }
      */
  }

  return getDefaultSymbolType(symbol, debug);
}

getImportSpecifiersWithNames.rawText:
 (
  sourceFile: SourceFile,
  names: string[]
) => {
  return sourceFile
    .getImportDeclarations()
    .map((x) => x.getNamedImports())
    .flat()
    .filter((x) => names.includes(x.getName()));
}

