getImportsExports.rawText:
 async ({
  sourceFiles,
  debug,
  manualProjectRoot,
}: {
  /**
   * if given, only these sourcefiles are used, otherwise takes all source files from project.
   */
  sourceFiles: SourceFile[];
  debug?: boolean;
  manualProjectRoot?: string;
}): Promise<ImportsAndExports | undefined> => {
  if (sourceFiles.length === 0) {
    log("No source files provided", { type: "warning" });
    return;
  }

  const allOperationsPaths = await exploreOperationFolders({
    basePath: manualProjectRoot,
  });
  const allOperations = allOperationsPaths.map(getLastFolder);
  const firstFilePath = sourceFiles[0].getFilePath();
  if (!firstFilePath) return;

  const operationBasePath = findOperationBasePath(firstFilePath);
  if (!operationBasePath) return;

  const packageJson = await readJsonFile<Operation>(
    path.join(operationBasePath, "package.json")
  );

  if (!packageJson) {
    log("PackageJson could not be found", { type: "error" });
    return;
  }

  const allTsExports: Creation<TsExport>[] = (
    await mapMany(
      sourceFiles,
      async (sourceFile) => {
        const absoluteFilePath = sourceFile.getFilePath();
        if (!absoluteFilePath) return;

        const operationRelativeTypescriptFilePath = makeRelative(
          absoluteFilePath,
          operationBasePath
        );

        const exportSymbols: Symbol[] = sourceFile.getExportSymbols();
        const tsExports: Creation<TsExport>[] = exportSymbols.map((symbol) => {
          const name = symbol.getName();
          const typeSpecifier: TypeSpecifier = { type: "value" };

          /**getExportSymbolTypeRecursive(
            symbol,
            sourceFile,
            debug
          );*/

          const exportObject: Creation<TsExport> = {
            name,
            slug: kebabCase(name),
            operationRelativeTypescriptFilePath,
            // TODO:
            comments: [],
            type: typeSpecifier?.type,
            hasGeneric: typeSpecifier?.hasGeneric,
          };
          return exportObject;
        });

        return tsExports;
      },
      CONCURRENTLY_INDEXED_FILES_AMOUNT
    )
  )
    .filter(notEmpty)
    .flat();

  const allTsImports: Creation<TsImport>[] = (
    await oneByOne(sourceFiles, async (sourceFile) => {
      const absoluteFilePath = sourceFile.getFilePath();
      if (!absoluteFilePath) return;
      const operationRelativeTypescriptFilePath = makeRelative(
        absoluteFilePath,
        operationBasePath
      );

      try {
        const importDeclarations: ImportDeclaration[] =
          sourceFile.getImportDeclarations();

        const importInfos = (
          await oneByOne(importDeclarations, async (importDeclaration) => {
            try {
              const module = String(
                importDeclaration.getModuleSpecifier().getLiteralText()
              );
              const importSpecifiers = importDeclaration.getNamedImports();

              const namedImports: NamedImport[] = await oneByOne(
                importSpecifiers,
                async (x) => {
                  const name = x.getName();

                  const isOperation = allOperations.includes(module);

                  const typeSpecifier = isOperation
                    ? getTypeFromImportSpecifierRecursive(x, debug)
                    : undefined;

                  const namedImport: NamedImport = {
                    name,
                    slug: kebabCase(name),
                    type: typeSpecifier?.type,
                    hasGeneric: typeSpecifier?.hasGeneric,
                  };

                  return namedImport;
                }
              );

              const isModuleResolved =
                !!importDeclaration.getModuleSpecifierSourceFile();

              return { namedImports, module, isModuleResolved };
            } catch (e) {
              console.log(
                `catched generating import-info for import-declaration`
              );
            }
          })
        ).filter(notEmpty);

        const tsImports: Creation<TsImport>[] = importInfos
          .map((importInfo) => {
            const { module, namedImports, isModuleResolved } = importInfo;

            const nodeModules = ["fs", "path"];
            const reactModules = ["react", "react-dom", "react-native", "expo"];

            const classification: ImportClassification = nodeModules.includes(
              module
            )
              ? "node"
              : reactModules.includes(module)
              ? "react"
              : allOperations.includes(module)
              ? "operation"
              : !isAbsoluteImport(module)
              ? "internal"
              : "package";

            const importObjects: Creation<TsImport>[] = namedImports.map(
              (namedImport) => ({
                ...namedImport,
                module,
                isModuleResolved,
                // TODO
                comments: [],
                operationRelativeTypescriptFilePath,
                classification,
                isAbsolute: isAbsoluteImport(module),
                isModuleFromMonorepo: allOperations.includes(module),
                // NB: will not be resolved if build folder doesn't exist or if entry file doesn't exist
              })
            );

            return importObjects;
          })
          .filter(notEmpty)
          .flat();

        return tsImports;
      } catch (e) {
        return;
      }
    })
  )
    .filter(notEmpty)
    .flat();

  return { imports: allTsImports, exports: allTsExports };
}

getPackageNameFromAbsoluteImport.rawText:
 (
  absoluteImportName: string
) => {
  const slashParts = absoluteImportName.split("/");
  const beforeSlash = slashParts[0];

  if (!beforeSlash || beforeSlash.length === 0) return;

  // NB: scoped packages look like `@company/package` and should be parsed correctly as well
  const withoutInternalNavigation = beforeSlash.startsWith("@")
    ? slashParts.slice(0, 2).join("/")
    : beforeSlash;

  const columnParts = withoutInternalNavigation.split(":");

  const partAfterColumns = columnParts.pop()!;

  return partAfterColumns;
}

getSymbolTypeDeclarations.rawText:
 (symbol: Symbol) => {
  return [
    ...getSymbolDeclarationsOfKind<TypeAliasDeclaration>(
      symbol,
      SyntaxKind.TypeAliasDeclaration
    ),
    ...getSymbolDeclarationsOfKind<InterfaceDeclaration>(
      symbol,
      SyntaxKind.InterfaceDeclaration
    ),
  ];
}

getTypeFromImportSpecifierRecursive.rawText:
 (
  importSpecifier: ImportSpecifier,
  debug?: boolean,
  /**
   * Files it was already in (to prevent infinite loops)
   */
  fileStack?: string[]
): TypeSpecifier | undefined => {
  // NB: find the file where the import is defined

  try {
    const fileOfExport = importSpecifier
      .getImportDeclaration()
      .getModuleSpecifierSourceFile();

    // NB: without the module source we can't know the type of this importspecifier
    if (!fileOfExport) return;
    const filePath = fileOfExport.getFilePath();
    if (!filePath) return;

    const realFileStack = fileStack || [];

    if (realFileStack.includes(filePath)) {
      console.log("Infinite loop found, returning...", { filePath, fileStack });
      return;
    }

    const newFileStack = realFileStack.concat(filePath);

    // NB: in the source, find all exported stuff
    const exportSymbols: Symbol[] = fileOfExport.getExportSymbols();

    // NB: find the export with the same name as the import
    const importedSymbol = exportSymbols.find(
      (x) => x.getName() === importSpecifier.getName()
    );

    // NB: if the source doesn't contain any export with that name, we can't know its type
    if (!importedSymbol) return;

    /**
     * NB: all ExportSpecifiers don't have a more specific SyntaxKind because they are imported.
     * If there are any ExportSpecifiers with a matching ImportSpecifier, recurse on that!
     */
    const exportSpecifierNames = getExportSpecifierNames(importedSymbol);

    if (exportSpecifierNames.length > 0) {
      const importSpecifiers = getImportSpecifiersWithNames(
        fileOfExport,
        exportSpecifierNames
      );

      if (importSpecifiers.length > 1) {
        // NB: warn if there is more than one as that would be strange

        log("More than one importsSpecifiers with that name", {
          type: "warning",
        });
      }

      if (importSpecifiers.length > 0 && newFileStack.length < 10) {
        // console.log(`recursing`, { newFileStack, filePath });

        // NB: recursion!
        const first = importSpecifiers[0];
        return getTypeFromImportSpecifierRecursive(first, debug, newFileStack);
      } else {
        if (debug)
          console.log("Weird, no importspecifiers found for exportSpecifier!!");
        return;
      }
    }

    // console.log({
    //   fileOfExport: fileOfExport?.getFilePath(),
    //   exportSymbols: exportSymbols?.map((x) => x.getName()),
    //   importSpecifier: importSpecifier.getName(),
    //   importedSymbol: importedSymbol?.getName(),
    //   name: importSpecifier.getName(),
    // });

    return getDefaultSymbolType(importedSymbol, debug);
  } catch (e) {
    console.log("failed");
    return;
  }
}

isAbsoluteImportBuiltin.rawText:
 (absoluteImportName: string) => {
