writeResult.rawText:
 async (options: {
  operationName: string;
  success: boolean;
  message: string;
  manualProjectRoot?: string;
}) => {
  const { message, operationName, success, manualProjectRoot } = options;

  if (!success) {
    log(`Couldn't write imports/exports.`);

    await db.update(
      "Operation",
      () => true,
      (old) =>
        mergeNestedObject(old, {
          operation: {
            buildResultIndexed: { indexImportExportError: message },
          },
        }),
      { operationName, manualProjectRoot }
    );

    return false;
  }
}

getOperationBins.rawText:
 async (operationFolderPath: string) => {
  const packageJson = await getPackageJson({ operationFolderPath });
  return packageJson?.bin ? Object.keys(packageJson.bin) : [];
}

getOperationPackageName.rawText:
 async (operationFolderPath: string) => {
