findOperationBasePathWithClassification.rawText:
 (
  startPath: string
):
  | { folderPath: string; classification: OperationClassification }
  | undefined => {
  // returns if `getOperationClassification` does not return `undefined`
  const result = findFolderWhereMatch(startPath, getOperationClassification);

  return result
    ? { folderPath: result.folderPath, classification: result.matchResult! }
    : undefined;
}

getAllPackageJsonDependencies.rawText:
 (
  operation: Operation
): string[] => {
  const dependencies = operation.dependencies
    ? Object.keys(operation.dependencies)
    : [];
  const devDependencies = operation.devDependencies
    ? Object.keys(operation.devDependencies)
    : [];
  const peerDependencies = operation.peerDependencies
    ? Object.keys(operation.peerDependencies)
    : [];

  return [...dependencies, ...devDependencies, ...peerDependencies];
}

getCommonAncestor.rawText:
 (path1: string, path2: string): string => {
  const chunks = path1.split("/");
  const chunks2 = path2.split("/");

  const firstFolderMismatchIndex = chunks.findIndex((chunk, index, array) => {
    const isMismatch = chunks2[index] !== chunk;

    return isMismatch;
  });

  const commonAncestor = chunks.slice(0, firstFolderMismatchIndex).join("/");

  return commonAncestor;
}

getOperationClassificationObject.rawText:
async (): Promise<OperationClassificationObject> => {
  const operationFolders = await exploreOperationFolders({});

  return mergeObjectsArray(
    operationFolders
      .map((operationBasePath) => {
        const operationClassification =
          getOperationClassification(operationBasePath);
        if (!operationClassification) return;
        const operationName = getLastFolder(operationBasePath);
        return { [operationName]: operationClassification };
      })
      .filter(notEmpty)
  );
}

getOperationClassification.rawText:
 (
  folderPath: string
): OperationClassification | undefined => {
  if (folderPath === undefined) {
    console.log(
      "Incorrect type at getOperationClassification"
      // getOperationClassification.caller
    );
    process.exit(1);
  }

  if (!isOperation(folderPath)) {
    return;
  }

  const packageJsonPath = path.join(folderPath, "package.json");
  const packageJson = tryParseJson<Operation>(
    fs.readFileSync(packageJsonPath, "utf8")
  );

  const tsconfigPath = path.join(folderPath, "tsconfig.json");

  const tsconfig = readJsonFileSync<TsConfig>(tsconfigPath);

  if (!tsconfig) return;

  if (!packageJson || packageJson.workspaces) {
    return;
  }

  const nextConfigPath = path.join(folderPath, "next.config.js");
  const existsNextConfig = fs.existsSync(nextConfigPath);
  const isNextApp = existsNextConfig;
  if (isNextApp) return "ui-web";

  const appJsonPath = path.join(folderPath, "app.json");
  const existsAppJson = fs.existsSync(appJsonPath);
  const isReactNativeApp = existsAppJson;
  if (isReactNativeApp) return "ui-app";

  const isTs = packageCompilesTs(packageJson);

  const isEsm = tsconfigCompilesEsm(tsconfig);

  const isUi = isUiOperation(tsconfig, packageJson);

  const compileType = isEsm ? "esm" : isTs ? "ts" : "cjs";

  if (isUi) {
    return `ui-${compileType}`;
  }

  const hasTypesNode = hasDependency(packageJson, "@types/node");

  if (hasTypesNode) {
    if (packageJson.operation?.isNodeServer) {
      return "server-cjs";
    }
    return `node-${compileType}`;
  }

  return compileType;
}

getOperationPathParse.rawText:
 (
  absolutePath: string
): OperationPathParse | undefined => {
  if (!absolutePath) return;
  const pathParse = getPathParse(absolutePath);
  if (!pathParse) return;
  const projectRoot = getProjectRoot(absolutePath);
  if (!projectRoot) return;
  const operationBasePath = findOperationBasePath(absolutePath);
  if (!operationBasePath) return;
  const operationSrcPath = path.join(operationBasePath, "src");

  const operationFolderName = getLastFolder(operationBasePath);
  const operationName = readJsonFileSync<Operation>(
    path.join(operationBasePath, "package.json")
  )?.name;

  const operationRelativeTypescriptFilePath = getOperationRelativePath(
    absolutePath,
    operationBasePath
  );

  const parsedPath = path.parse(absolutePath);
  const srcFileId = makeRelative(
    path.join(parsedPath.dir, parsedPath.name),
    operationSrcPath
  );

  const relativeOperationBasePathFromProjectRoot = operationBasePath.slice(
    projectRoot.length
  );

  return {
    relativePathFromProjectRoot: pathParse.relativePathFromProjectRoot,
    srcFileId,
    operationFolderName,
    operationRelativeTypescriptFilePath,
    relativeOperationBasePathFromProjectRoot,
    operationName,
  };
}

getOperationPath.rawText:
 async (
  /**
   * specify the operation folder name
   */
  operationName: string,
  config?: {
    manualProjectRoot?: string;
    /**
     * if true, will not use sdk (defaults to using it first...)
     */
    notUseSdk?: boolean;
  }
): Promise<string | undefined> => {
  // NB: In case of manualProjectRoot, we should not use the SDK! The sdk is from our own project root.
  if (!config?.notUseSdk && !config?.manualProjectRoot) {
    const projectRelativeOperationPath =
      operations[operationName as keyof typeof operations];
    const projectRoot = getProjectRoot();
    if (projectRelativeOperationPath && projectRoot) {
      return path.join(projectRoot, projectRelativeOperationPath);
    }
  }

  // if that didn't work, let's find it in realtime
  const basePath = getPathsWithOperations({
    manualProjectRoot: config?.manualProjectRoot,
  });

  const operationPaths: string[] = await exploreOperationFolders({ basePath });

  if (operationPaths.length === 0) {
    log("No operations available", { type: "error" });
    return;
  }
  const operationPathsWithTheirFolder = await Promise.all(
    operationPaths.map(async (p) => ({
      path: p,
      folderName: getLastFolder(p),
    }))
  );

  const foundPath = operationPathsWithTheirFolder.find(
    (f) => f.folderName === operationName
  )?.path;

  return foundPath;
}

getPathParse.rawText:
 (absolutePath: string): PathParse | undefined => {
  const projectRoot = getProjectRoot(absolutePath);
  if (!projectRoot) return;
  const relativePathFromProjectRoot = absolutePath.slice(projectRoot.length);
  return { relativePathFromProjectRoot };
}

getPathsWithOperations.rawText:
 (config?: {
  /**
   * if given, this will be taken as the project root instead of the one that can be found automatically (useful for bundling or working with multiple projects that alter each other)
   */
  manualProjectRoot?: string;
}): string[] => {
  const rootPath = config?.manualProjectRoot || getProjectRoot();

  if (!rootPath) {
    log(`no rootpath found!`, { type: "error" });
    process.exit(1);
  }

  if (isBundle(rootPath)) {
    const pathsWithOperations = ["apps", "packages", "modules"]
      .map((folderName) => path.join(rootPath, folderName))
      .filter((fullPath) => fs.existsSync(fullPath));

    if (pathsWithOperations.length === 0) {
      log(`Couldn't find any operations in ${rootPath}`);
      process.exit(1);
    }

    return pathsWithOperations;
  }
  const toolsPath = path.join(rootPath, "operations/tools");
  const bundlesPath = path.join(rootPath, "operations/niches");
  if (!fs.existsSync(toolsPath) || !fs.existsSync(bundlesPath)) {
    log(`Couldn't find tools or bundles folder in ${rootPath}`);
    process.exit(1);
  }

  return [toolsPath, bundlesPath];
}

getProjectRoot.rawText:
 (fullSourcePath?: string): string | undefined => {
  const matchFolder = findFolderWhereMatch(
    fullSourcePath || process.cwd(),
    hasProjectRootFile
  );
  if (!matchFolder) return;
  return matchFolder.folderPath;
}

getRelativeLinkPath.rawText:
 (
  absoluteFromFilePath: string,
  absoluteToFilePath: string,
  debug?: boolean
): string => {
  const commonAncestorPath = getCommonAncestor(
    absoluteFromFilePath,
    absoluteToFilePath
  );

  //1 - go from `absoluteFromPath` to `commonAncestorPath`
  const commonAncestorRelativeFromPath = makeRelative(
    absoluteFromFilePath,
    commonAncestorPath
  );
  const commonAncestorRelativeToPath = makeRelative(
    absoluteToFilePath,
    commonAncestorPath
  );

  const foldersToGoBackAmount =
    commonAncestorRelativeFromPath.split("/").length - 1;

  const backOrStart =
    foldersToGoBackAmount === 0 ? "./" : "../".repeat(foldersToGoBackAmount);

  //2 - go from `commonAncestorPath` to `absoluteToPath`
  const relativeLinkPath = `${backOrStart}${commonAncestorRelativeToPath}`;

  if (debug) {
    console.log({
      commonAncestorPath,
      commonAncestorRelativeFromPath,
      commonAncestorRelativeToPath,
    });
  }

  return relativeLinkPath;
}

getRelativePath.rawText:
 (
  absolutePath: string,
  relativeFrom: "project-root"
) => {
  const rootPath =
    relativeFrom === "project-root" ? getRootPath() : getRootPath();

  if (!rootPath) return;

  const relativePath = absolutePath.replace(rootPath, "");

  return relativePath;
}

getRootPath.rawText:
 (
  /**
   * if not specified, will return project root path
   */
  name?: OSRootFolder | ProjectRootFolder,
  config?: { manualProjectRoot?: string }
): string | undefined => {
  const projectRootDir = config?.manualProjectRoot || getProjectRoot();
  if (!projectRootDir) return;

  if (!name) {
    return projectRootDir;
  }

  if (isBundle(projectRootDir)) {
    if (projectRootFolders.concat(osRootFolders).includes(name)) {
      const projectFolder =
        name === "text"
          ? "docs"
          : name === "operations"
          ? "packages"
          : name === databaseFolderName
          ? databaseFolderName
          : null;
      if (!projectFolder) {
        console.log("get root path: This should never happen", name);
        return;
      }

      const folderPath = path.resolve(projectRootDir, projectFolder);
      return folderPath;
    }
  }

  // non-bundled projects

  if (projectRootFolders.concat(osRootFolders).includes(name)) {
    const folderPath = path.resolve(projectRootDir, name);
    return folderPath;
  }

  console.log(
    "getRootPath: Should never happen, probably a wrong input was provided:",
    { name }
  );

  return;
}

getSrcRelativeFileId.rawText:
 (operationRelativePath: string) => {
  const srcRelativePath = operationRelativePath.replace("src/", "");
  const parts = srcRelativePath.split(".");
  // remove the last one
  parts.pop();
  const srcRelativeFileId = parts.join(".");

  const finalId = srcRelativeFileId.startsWith("/")
    ? srcRelativeFileId.substring(1)
    : srcRelativeFileId;
  return finalId;
}

getSubExtensions.rawText:
 (absolutePath: string): string[] => {
  const fileName = path.parse(absolutePath).name;
  const allSubExtensionsArray = fileName?.split(".") || [];
  const allSubExtensions = allSubExtensionsArray.slice(
    1,
    allSubExtensionsArray.length - 1
  );

  return allSubExtensions;
}

hasProjectRootFile.rawText:
 (absolutePath: string) => {
  const osRootFilePath = path.join(absolutePath, ".project-root");
  const osRootFileExists = fs.existsSync(osRootFilePath);
  return osRootFileExists;
}

isBundle.rawText:
 (folderPath?: string): boolean => {
  const realFolderPath = folderPath || getProjectRoot();
  if (!realFolderPath) return false;
  // get package-json and check operation config
  const packageJson = readJsonFileSync<Operation>(
    path.join(realFolderPath, "package.json")
  );
  const isBundle = packageJson?.operation?.isBundle || false;
  return isBundle;
}

isOperation.rawText:
 (absoluteFolderPath: string): boolean => {
  const packageJsonPath = path.join(absoluteFolderPath, "package.json");
  const existsPackageJson = fs.existsSync(packageJsonPath);
  const tsConfigPath = path.join(absoluteFolderPath, "tsconfig.json");
  const existsTsConfig = fs.existsSync(tsConfigPath);
  // NB: must have these two in order to be an operation at all
  if (!existsPackageJson || !existsTsConfig) {
    return false;
  }
  return true;
}

isUiOperation.rawText:
 (
  tsconfig: TsConfig | null,
  packageJson: Operation | null
) => {
  const isReactPackage =
    !!packageJson &&
    (hasDependency(packageJson, "react") ||
      hasDependency(packageJson, "react-native") ||
      hasDependency(packageJson, "next") ||
      hasDependency(packageJson, "expo"));

  const usesJsx = !!tsconfig?.compilerOptions?.jsx;

  return isReactPackage && usesJsx;
}

isWorkspaceRoot.rawText:
 (
  folderPath: string
): undefined | { isBundle: boolean; isWorkspaceRoot: boolean } => {
  const packageJsonPath = path.join(folderPath, "package.json");
  const existsPackageJson = fs.existsSync(packageJsonPath);
  if (!existsPackageJson) return;

  const packageJson = tryParseJson<Operation>(
    fs.readFileSync(packageJsonPath, "utf8")
  );

  if (!packageJson || !packageJson.workspaces) {
    return;
  }

  return {
    isBundle: packageJson.operation?.isBundle || false,
    isWorkspaceRoot: true,
  };
}

tsconfigCompilesEsm.rawText:
 (tsconfig: TsConfig) => {
  return (
    !!tsconfig.compilerOptions.module &&
    !!tsconfig.compilerOptions.moduleResolution &&
    tsconfig.compilerOptions.module !== ModuleKind.CommonJS &&
    tsconfig.compilerOptions.moduleResolution !== ModuleResolutionKind.Classic
  );
}

getTsConfig.rawText:
 (packageFolder: string) => {
  const jsonPath = path.join(packageFolder, "tsconfig.json");
  const json = readJsonFile<TsConfig>(jsonPath);
  return json;
}

