getObjectSchema.rawText:
 (
  type: Type,
  decorators: Decorator[],
  schemaProps: { [key: string]: any } = {}
) => {
  const schema: SimpleJsonSchema = {};
  // schema = {} handleExplicitValidation("object", schema, decorators);
  schema.type = "object";
  schema.properties = {};
  schema.required = schema.required || [];
  const typeText = type.getText();
  const nonNullableType = type.getNonNullableType();
  const nonNullableTypeText = nonNullableType.getText();
  schema.optional = nonNullableTypeText !== typeText;
  type
    .getNonNullableType()
    .getProperties()
    .forEach((prop) => {
      const key = prop.getName();
      const isGetter = prop.hasFlags(SymbolFlags.GetAccessor);
      if (["request", "reply"].includes(key) || isGetter) return;
      const valueDeclaration = prop.getValueDeclarationOrThrow();
      const decorators = (valueDeclaration as any).getDecorators
        ? (valueDeclaration as any).getDecorators()
        : [];

      schema.properties[key] = {
        ...(getParamSchema(valueDeclaration.getType(), decorators, prop) || {}),
        ...(schemaProps[key] || {}),
      };

      if (!schema.properties[key]) {
        console.warn("missing type for - " + key);
        schema.properties[key] = { type: "object" };
      }
      if (schema.properties[key].optional !== true) {
        schema.required?.push(key);
      }
      delete schema.properties[key].optional;
    });

  if (!schema.required.length) {
    delete schema.required;
  }

  return schema;
}

getParamSchema.rawText:
 (
  type: Type,
  decorators: Decorator[] = [],
  prop: tsSymbol | undefined = undefined
) => {
  const typeText = type.getText();
  const nonNullableType = type.getNonNullableType();
  const nonNullableTypeText = nonNullableType.getText();
  let schema: SimpleJsonSchema = {};

  schema.optional = nonNullableTypeText !== typeText;

  if (nonNullableType.isArray()) {
    schema = handleExplicitValidation("array", schema, decorators);
    schema.type = "array";
    // NB: recursion!
    schema.items =
      getParamSchema(nonNullableType.getArrayElementTypeOrThrow(), []) || {};
    Object.keys(schema.items).forEach(
      (key) => delete schema.items[key].optional
    );
    delete schema.items.optional;
    return schema;
  }
  if (nonNullableType.getText() === "Date") {
    schema.type = "string";
    schema.format = "date-time";
    return schema;
  }

  if (isPrimitive(nonNullableType)) {
    schema.type = typeText.replace(" | undefined", "");
    if (schema.type === "string") {
      schema["allOf"] = [{ transform: ["trim"] }, { minLength: 1 }];
    }
    schema = handleExplicitValidation(
      nonNullableType.getText(),
      schema,
      decorators
    );
    return schema;
  }

  if (nonNullableType.isClass() || nonNullableType.isInterface()) {
    const name =
      nonNullableType.getText().split(").")[1] || nonNullableType.getText();
    const importPath = typeText.split('").')[0].split('import("')[1];
    if (importPath && importPath.includes("/node_modules/")) return schema;
    // eslint-disable-next-line @typescript-eslint/no-var-requires
    const c =
      importPath && !importPath?.includes("/node_modules/")
        ? require(importPath)[name]
        : undefined;
    if (importPath && !c) {
      console.log(`not found type: ${name}`);
      return schema;
    }
    // NB: we seem not to have it, see https://medium.com/jspoint/introduction-to-reflect-metadata-package-and-its-ecmascript-proposal-8798405d7d88
    // I installed reflect-metdata and added as require on top. Hope that works!
    const classSchema = c
      ? // @ts-ignore
        Reflect.getMetaData(symbols.validations, c.prototype) || {}
      : {};

    schema["$ref"] = "#/definitions/" + name;
    if (!definitions[name]) {
      definitions[name] = getObjectSchema(type, decorators, classSchema);
    }
    return schema;
  }

  if (nonNullableType.isObject()) {
    schema = getObjectSchema(type, decorators);
    return schema;
  }
  // enum --------------------------------------
  if (nonNullableType.isEnumLiteral() && prop) {
    const name = prop.getName();
    const enumMembers = prop
      ?.getValueDeclarationOrThrow()
      ?.getSourceFile()
      .getEnum((e) => e.getName() === nonNullableType.getText())
      ?.getMembers();

    const enumSchema: {
      enum?: string[];
      "x-enumNames"?: (string | number | undefined)[];
      type?:
        | "string"
        | "number"
        | "bigint"
        | "boolean"
        | "symbol"
        | "undefined"
        | "object"
        | "function";
    } = {};
    enumSchema.enum = enumMembers?.map((m) => m.getName());
    enumSchema["x-enumNames"] = enumMembers?.map((m) => m.getValue());

    enumSchema.type = enumSchema.enum?.[0]
      ? typeof enumSchema.enum?.[0]
      : undefined;

    definitions[name] = enumSchema;
    schema["$ref"] = "#/definitions/" + name;
    return schema;
  }

  if (nonNullableType.isEnum()) {
    const name = last(nonNullableType.getText().split("."))!;
    const enumSchema: any = {};
    enumSchema.enum = nonNullableType
      .getUnionTypes()
      .map((t) => t.getLiteralValueOrThrow());
    enumSchema["x-enumNames"] = nonNullableType
      .getUnionTypes()
      .map((t) => last(t.getText().split(".")) as string);
    enumSchema.type = typeof enumSchema.enum[0];
    definitions[name] = enumSchema;
    schema["$ref"] = "#/definitions/" + name;
    return schema;
  }

  const unionTypes = type.getUnionTypes().filter((t) => !t.isUndefined());
  if (unionTypes.length > 1) {
    schema.oneOf = unionTypes.map((t) =>
      getParamSchema(t, decorators)
    ) as SimpleJsonSchema[];
    if (!schema.oneOf[0]) {
      delete schema.oneOf;
      schema.enum = unionTypes.map((t) => t.getText().slice(1, -1));
      schema["x-enumNames"] = unionTypes.map((t) => t.getText().slice(1, -1));
      schema.type = typeof schema.enum[0];
    }
    return schema;
  }
}

getParametersFromInterfaces.rawText:
 (
  functionName: string,
  /**
   * NB: we need ALL interfaces here across the project because there may be others that were not found in the interfaces in the file, but only in the functions.
   */
  interfaces: Creation<TsInterface>[]
) => {
  const parametersInterface = interfaces.find(
    (x) => x.name === `NamedParameters<typeof ${functionName}>`
  );

  /*
    NB: this is how a named parameters typeDefinition looks for a function with 2 arguments
    {
      "type": "object",
      "properties": {
        "markdownString": { "type": "string" },
        "level": { "type": "number" }
      },
      "required": ["markdownString", "level"],
      "additionalProperties": false
    }
    */
  const properties = parametersInterface?.type.typeDefinition?.properties;

  const parameters: FunctionParameter[] = properties
    ? Object.keys(properties)
        .map((name) => {
          const propertySchema = getSchema(
            parametersInterface.type.typeDefinition?.properties?.[name]
          );
          const required =
            parametersInterface.type.typeDefinition?.required?.includes(name) ||
            false;

          return propertySchema
            ? {
                name,
                schema: propertySchema,
                simplifiedSchema: simplifySchema(
                  name,
                  propertySchema,
                  getPossibleRefs(interfaces),
                  []
                ),
                required,
              }
            : undefined;
        })
        .filter(notEmpty)
    : [];

  return parameters;
}

getPossibleRefs.rawText:
 (
  interfaces: Creation<TsInterface>[]
): { name: string; schema: JSONSchema7 }[] => {
  const possibleRefs2 = interfaces.map((tsInterface) => {
    return tsInterface.type.typeDefinition
      ? {
          name: tsInterface.name,
          schema: tsInterface.type.typeDefinition,
        }
      : null;
  });

  const possibleRefs = possibleRefs2.filter(notEmpty);

  return possibleRefs;
}

getSizeSummary.rawText:
 async (
  filePath: string
): Promise<SizeSummary> => {
  const stat = await fs.stat(filePath);

  const bytes = stat.size;
  const characters = bytes;

  const bytesPerCharacter = bytes / characters;
  let lines = 1;
  const charactersPerLine = characters / lines;
  const numberOfFiles = 1;
  let linesPerFile = lines;
  if (bytes > 1024 * 1024) {
    // NB: too big to watch content

    return {
      bytes,
      lines: 0,
      characters,
      bytesPerCharacter,
      charactersPerLine,
      linesPerFile,
      numberOfFiles,
    };
  }

  const content = await fs.readFile(filePath, "utf8");
  lines = content.split("\n").length;
  linesPerFile = lines;

  return {
    bytes,
    lines,
    characters,
    bytesPerCharacter,
    charactersPerLine,
    linesPerFile,
    numberOfFiles,
  };
}

getSpecialExtensionDbStorageMethod.rawText:
 (extensions?: string[]) => {
  if (!extensions) return;

  /**
   * put any models here that should, if they are used as extension, have a db model generated
   */
  const specialExtensions = {
    TsIndexModelType: "jsonMultiple",
    OperationIndexModelType: "jsonMultiple",
    DefaultModelType: "jsonMultiple",
    SlugModelType: "jsonMultiple",
    CsvModelType: "csv",
    KeyValueMarkdownModelType: "keyValueMarkdown",
    CategoryModelType: "jsonMultiple",
    MarkdownModelType: "markdown",
  };

  const specialExtensionKeys = Object.keys(specialExtensions);

  const specialExtension = extensions.find((x) =>
    specialExtensionKeys.includes(x)
  ) as undefined | keyof typeof specialExtensions;

  if (!specialExtension) return;

  const dbStorageMethod = specialExtensions[
    specialExtension
  ] as DbStorageMethod;
  return dbStorageMethod;
}

getTsStatements.rawText:
 async (
  sourceFile: SourceFile,
  /**
   * NB: we require all interfaces here across the monorepo because the ones indexed by the schema generator aren't complete.
   */
  tsInterfaces: Creation<TsInterface>[],
  operationRelativeTypescriptFilePath: string,
  fileContent: string
) => {
  const tsVariables: Creation<TsVariable>[] = sourceFile
    .getVariableStatements()
    .map((variableStatement) => {
      const declarations = variableStatement.getDeclarations();
      const inits = declarations
        .map((x) => x.getInitializer())
        .filter(notEmpty);

      const name = declarations[0]?.getName();
      const value = inits[0]?.getText();

      const isExported = variableStatement.isExported();

      const classification = variableStatement
        .getDeclarationKind()
        .toString()
        .toLowerCase() as TsVariable["classification"];

      const description = variableStatement
        .getJsDocs()
        .map((x) => x.getText())
        .map(stripComment)
        .join("\n\n");

      const slug = kebabCase(name);

      const type = getTypeInfo(declarations[0]?.getType());
      const tsVariable: Creation<TsVariable> = {
        classification,
        comments: [],
        isExported,
        name,
        slug,
        operationRelativeTypescriptFilePath,
        type,
        value,
        description,
      };
      return tsVariable;
    });

  const morphVars: VariableDeclarationInfo[] = sourceFile
    .getStatements()
    .map((x) => {
      const variableDeclarations = x
        .asKind(SyntaxKind.VariableStatement)
        ?.getDeclarations();

      if (!variableDeclarations || variableDeclarations.length === 0) return;

      const explicitTypeName = variableDeclarations
        .map((x) => x.getTypeNode()?.getText())
        .filter(notEmpty)[0];
      return {
        explicitTypeName,
        isExported: variableDeclarations[0].isExported(),
        variableDeclarations,
        // NB: if this is a VariableStatement
        kindName: x.getKindName(),

        // NB: this is how we can check if it's an arrow function (declaration Initializer Kind Names Includes Arrow Function)
        isArrowFunction: variableDeclarations
          .map((v) => v.getInitializer())
          .map((x) => x?.getKindName())
          .includes("ArrowFunction"),

        // NB: if it's a variable, we can get the name like this
        names: variableDeclarations.map((x) => x.getName()),

        comments: x
          .getLeadingCommentRanges()
          .map((x) => x.getText())
          .map(stripComment),
      };
    })
    .filter(notEmpty);

  // const stringLiterals = morphVars.filter((v) => !v.isArrowFunction);

  /*
   * 
   * This was the old way of parsing tsVariables, but it was not finding the comments.
   * 
   * 
  const tsVariables: Creation<TsVariable>[] = stringLiterals
    .map((v) => {
      const inits = v.variableDeclarations
        .map((x) => x.getInitializer())
        .filter(notEmpty);

      const initComments = inits
        .map((x) => x.getLeadingCommentRanges().map((x) => x.getText()))
        .flat()
        .join("\n\n");
      const variableDeclarationComments = v.variableDeclarations
        .map((x) => x.getLeadingCommentRanges().map((x) => x.getText()))
        .flat()
        .join("\n\n");

      const name = v.names[0] || "__NO_NAME__";
      const slug = kebabCase(name);
      const value =
        inits.map((x) => x.getText()).filter(notEmpty)[0] || "__NO_VALUE__";

      // TODO: TEST, should return const, var or let
      const classification = v.variableDeclarations[0]
        .getVariableStatement()
        ?.getDeclarationKind()
        .toString()
        .toLowerCase() as TsVariable["classification"];

      const variableDeclaration = v.variableDeclarations?.[0];

      if (!variableDeclaration) {
        return;
      }

      const variableComments = getAllComments(
        variableDeclaration,
        fileContent,
        operationRelativeTypescriptFilePath
      );

      const statementComments: Creation<TsComment>[] = variableDeclaration
        .getDescendantStatements()
        .map((x) => {
          const allComments = getAllComments(
            x,
            fileContent,
            operationRelativeTypescriptFilePath
          );
          return allComments;
        })
        .flat();

      const comments = variableComments
        ? [...variableComments, ...statementComments]
        : statementComments;

      const isExported = v.isExported;

      console.log({ comments, initComments, variableDeclarationComments });
      const description = undefined;
      const variable: Creation<TsVariable> = {
        description,
        isExported,
        operationRelativeTypescriptFilePath,
        comments,
        name,
        slug,
        value,
        // NB: still using typeToSchema here, is there another way?
        type: getTypeInfo(variableDeclaration.getType()),
        classification,
      };
      return variable;
    })
    .filter(notEmpty);
    */

  const morphFunctions = sourceFile.getFunctions();
  const arrowFunctionVars = morphVars.filter((v) => v.isArrowFunction);

  const arrowFunctions: Creation<TsFunction>[] = arrowFunctionVars
    .map((v) => {
      const isExported = v.isExported;

      const arrowFunction = v.variableDeclarations?.map((x) =>
        x.getInitializer()?.asKind(SyntaxKind.ArrowFunction)
      )[0];

      if (!arrowFunction) {
        log("Should never get here, arrow function not found", {
          type: "error",
        });
        return;
      }

      const description = v.comments.join("\n\n");

      const name = v.names?.[0] || "no name";

      const returnType = getTypeInfo(
        arrowFunction.getReturnType().getApparentType()
      );
      const functionText = arrowFunction.getFullText();
      const fullText = functionText.concat(description);

      const parsedDescription = parseFrontmatterMarkdownString(description);
      const frontmatter = parsedDescription.parameters;

      const frontmatterParameters =
        getFrontmatterFunctionParameters(frontmatter);

      const fn: Creation<TsFunction> = {
        ...frontmatterParameters,
        isExported,
        explicitTypeName: v.explicitTypeName,

        operationRelativeTypescriptFilePath,

        // TODO:
        commentsInside: [],
        rawText: functionText,
        name,
        slug: kebabCase(name),
        parameters: getParametersFromInterfaces(name, tsInterfaces),
        description,
        returnType,
        maxIndentationDepth: getMaxIndentationDepth(functionText),

        // TODO: isolate the size calculations...

        // size of function including comments
        size: getSizeSummary(fullText),

        // split it up
        //codeSize: {},
        //commentSize:{},

        // cumulativeCodeSize:{},
        // cumulativeCommentSize:{},
        // cumulativeSize:{},
      };

      return fn;
    })
    .filter(notEmpty);

  const regularFunctions: Creation<TsFunction>[] = morphFunctions.map(
    (functionDeclaration) => {
      const returnType = getTypeInfo(
        functionDeclaration.getReturnType().getApparentType()
      );

      const functionText = functionDeclaration.getFullText();

      const description = functionDeclaration
        .getJsDocs()
        .map((x) => x.getFullText())
        .join("\n\n");

      const fullText = functionText.concat(description);

      const parsedDescription = parseFrontmatterMarkdownString(description);
      const frontmatter = parsedDescription.parameters;
      const frontmatterParameters =
        getFrontmatterFunctionParameters(frontmatter);

      const name = functionDeclaration.getName() || "__anonymous__";

      const fn: Creation<TsFunction> = {
        ...frontmatterParameters,

        name,

        slug: kebabCase(name),
        isExported: functionDeclaration.isExported(),
        operationRelativeTypescriptFilePath,
        // TODO:
        commentsInside: [],
        // function metadata
        // function info
        description,
        parameters: getParametersFromInterfaces(name, tsInterfaces),
        returnType,
        size: getSizeSummary(fullText),
        rawText: functionText,
        maxIndentationDepth: getMaxIndentationDepth(functionText),
      };

      return fn;
    }
  );

  const tsFunctions = arrowFunctions.concat(regularFunctions);

  // console.log({ tsFunctions: tsFunctions.length });
  return { tsFunctions, tsVariables };
}

getTypeInfo.rawText:
 (type: Type, schema?: Schema): TypeInfo => {
  const rawType = type.getApparentType().getText();
  const isArray = type.isArray();
  const isEnum = type.isEnum();
  const isEnumLiteral = type.isEnumLiteral();
  const isPrimitive = type.isString() || type.isNumber() || type.isBoolean();
  const isObject = type.isObject();

  let typeDefinition: Schema | undefined = undefined;
  try {
    typeDefinition = schema || typeToSchema(type);
  } catch (e) {
    log(`Type to schema failed with error: ${e}`, { type: "debug" });
  }

  const typeInfo: TypeInfo = {
    rawType,
    typeDefinition,
    // TODO:
    typeCoverage: 0,
    // this is easy to infer
    isArray,
    isEnum,
    isObject,
    isPrimitive,
    isEnumLiteral,
    simplifiedSchema: typeDefinition
      ? simplifySchema("type", typeDefinition, [], [])
      : undefined,
  };

  return typeInfo;
}

getValidatedOperationPathParse.rawText:
 (
  filePath: string
): undefined | CompleteOperationPathParse => {
  const fileParse = path.parse(filePath);

  if (![".ts", ".tsx"].includes(fileParse.ext)) {
    log(`Incorrect extension ${fileParse.ext}`, { type: "warning" });
    return;
  }

  // NB: filename is not only a name here but a relative path
  if (!fs.existsSync(filePath)) {
    log(`couldn't retreive filepath ${filePath}`, { type: "error" });
    return;
  }

  const operationPathParse = getOperationPathParse(filePath);
  if (!operationPathParse) {
    log("Operation must have a pathparse to be indexed", { type: "error" });
    return;
  }

  return { ...operationPathParse, filePath };
}

hasTypescriptFileChanged.rawText:
 (
  eventName: WatchEventType,
  path: string
) => {
  // for indexation we don't care about any other event than adding or changing a file
  if (!["add", "change"].includes(eventName)) return false;
  // in order to index a file, it must be in an operation, which means it must be in src
  if (!path.includes("/src/")) return false;
  // only ts and tsx files matter, the rest doesn't need to be indexed
  if (!["ts", "tsx"].includes(getExtension(path))) return false;

  return true;
}

