indexTypescriptFile.rawText:
 async (
  project: Project,
  file: CompleteOperationPathParse,
  projectRoot: string
) => {
  const problems: string[] = [];

  const { filePath, operationName, operationRelativeTypescriptFilePath } = file;
  if (!operationName) return;

  // console.log(`indexing file`, {
  //   operationName,
  //   filePath,
  //   operationRelativeTypescriptFilePath,
  // });
  // END VALIDATION

  const fileContent = await fs.readFile(filePath, "utf8");

  //select correct SourceFile from tsmorph project
  const sourceFile = project.getSourceFile(filePath);

  if (!sourceFile) {
    const problem = `couldn't load file ${filePath}`;
    problems.push(problem);

    await db.update(
      "Operation",
      (item) => item.name === operationName,
      (old) =>
        mergeNestedObject(old, {
          operation: { buildResultIndexed: { indexInteracesErrors: problems } },
        }),
      { operationName }
    );

    log(problem, { type: "error" });
    return;
  }

  const newTsInterfaces = await findAndUpsertTsInterfaces({
    filePath,
    sourceFile,
    operationName,
    projectRoot,
  });

  if (!newTsInterfaces) {
    log("Shouldn't happen but tsInterfaces is undefined here...");
    return;
  }

  const allTsInterfaces = await db.get("TsInterface");

  const allWithNewTsInterfaces = [
    ...newTsInterfaces,
    ...allTsInterfaces,
  ].filter(onlyUnique2<Creation<TsInterface>>((a, b) => a.name === b.name));
  // NB: interfaces are a prerequisite for function...

  // console.log({
  //   newTsInterfaces: newTsInterfaces.length,
  //   allTsInterfaces: allTsInterfaces.length,
  //   allWithNewTsInterfacesUnique: allWithNewTsInterfaces.length,
  // });
  // TODO:
  const tsLintWarnings: TsLintWarning[] = [];

  // TODO: get main comment from top of file or associated md
  const mainComment = undefined;
  const pathMetaData = await calculatePathMetaData(filePath);

  const { tsFunctions, tsVariables } = await getTsStatements(
    sourceFile,
    allWithNewTsInterfaces,
    operationRelativeTypescriptFilePath,
    fileContent
  );

  // gets all top level statements
  const topLevelComments: Creation<TsComment>[] = sourceFile
    .getStatementsWithComments()
    .map((x) => {
      const comments = getAllComments(
        x,
        fileContent,
        operationRelativeTypescriptFilePath
      );
      return comments;
    })
    .flat();

  const functionComments: Creation<TsComment>[] = tsFunctions
    .map((f) => f.commentsInside)
    .flat();
  const interfaceComments: Creation<TsComment>[] = newTsInterfaces
    .map((f) => f.commentsInside)
    .flat();
  const variableComments: Creation<TsComment>[] = tsVariables
    .map((f) => f.comments)
    .flat();

  // TODO: get all top level comments from the statements, but also get all comments already found in functions, variables, and interfaces, put together.
  const tsComments: Creation<TsComment>[] = [
    topLevelComments,
    functionComments,
    interfaceComments,
    variableComments,
  ].flat();

  // Inserting all results into the database...

  // @ts-ignore
  await db.remove(
    "TsFunction",
    (fn) =>
      fn.operationRelativeTypescriptFilePath ===
        operationRelativeTypescriptFilePath &&
      !tsFunctions.map((x) => x.name).includes(fn.name),
    { operationName, manualProjectRoot: projectRoot }
  );

  // console.log({ tsFunctions: tsFunctions.length, operationName, projectRoot });
  // @ts-ignore
  await db.upsert("TsFunction", tsFunctions, {
    operationName,
    manualProjectRoot: projectRoot,
  });

  await db.remove(
    "TsVariable",
    (v) =>
      v.operationRelativeTypescriptFilePath ===
        operationRelativeTypescriptFilePath &&
      !tsVariables.map((x) => x.name).includes(v.name),
    { operationName, manualProjectRoot: projectRoot }
  );
  await db.upsert("TsVariable", tsVariables, {
    operationName,
    removeUntouched: true,
    manualProjectRoot: projectRoot,
  });

  await db.remove(
    "TsComment",
    (c) =>
      c.operationRelativeTypescriptFilePath ===
      operationRelativeTypescriptFilePath,
    { operationName, manualProjectRoot: projectRoot }
  );
  await db.upsert("TsComment", tsComments, {
    operationName,
    removeUntouched: true,
    manualProjectRoot: projectRoot,
  });
}

indexTypescript.rawText:
 async (props: {
  showLogging?: boolean;
  /**
   * filepaths of files to index. must be files from the same operation.
   */
  filePaths: string[];
  manualProjectRoot: string | null;
}): Promise<void> => {
  const { filePaths, manualProjectRoot, showLogging } = props;
  // NB: fix to globally alter real fs in order to fix EMFile error that happens in TSMorph (see https://github.com/isaacs/node-graceful-fs)
  gracefulFs.gracefulify(realFs);

  const firstFilePath = filePaths[0];
  if (!firstFilePath) {
    log("Please provide some file paths");
  }

  // NB: we can assume all files are from the same operation
  const operationBasePath = findOperationBasePath(firstFilePath);
  if (!operationBasePath) {
    log("Operation must have a basepath to be indexed", { type: "error" });
    return;
  }
  const projectRoot = manualProjectRoot || getProjectRoot(firstFilePath);
  if (!projectRoot) {
    log("No project root found", { type: "error" });
    return;
  }
  const packageJson = await readJsonFile<Operation>(
    path.join(operationBasePath, "package.json")
  );

  if (!packageJson) {
    log("Operation must have a package.json to be indexed", { type: "error" });
    return;
  }

  const operationName = packageJson.name;

  if (!operationName) {
    log("Operation must have a name to be indexed", { type: "error" });
    return;
  }

  const problems: string[] = [];

  // TODO: this takes a long time. I should probably execute everything for every file after this and use the same project

  //create tsmorph project for full operation
  const project = getTsMorphProject(operationBasePath);

  if (!project) {
    const problem = "couldn't load project";
    problems.push(problem);
    await db.update(
      "Operation",
      (item) => item.name === getFolder(operationBasePath),
      (old) =>
        mergeNestedObject(old, {
          operation: { buildResultIndexed: { indexErrors: problems } },
        })
    );

    log(problem, { type: "error" });
    return;
  }

  const filePathsToIndex = filePaths
    .map(getValidatedOperationPathParse)
    .filter(notEmpty);

  // NB: one by one because if you do multiple at once, writing commets goes corrupt because concurrent writing.
  await oneByOne(filePathsToIndex, async (file, index) => {
    if (showLogging) {
      process.stdout.write(`${index + 1}) ${file.srcFileId} `);
    }
    const result = await indexTypescriptFile(project, file, projectRoot);
    if (showLogging) {
      console.log("‚úÖ");
    }
    return result;
  });
}

makeTsComment.rawText:
 (config: {
  operationRelativeTypescriptFilePath: string;
  commentRange: CommentRange;
  /**
   * if we can give the statement a name, it should be provided here
   */
  statementName: string | undefined;
  rawStatement: string;
  /** needed for calculating line numbers */
  fileContent: string;
}): Creation<TsComment> => {
  const {
    commentRange,
    rawStatement,
    statementName,
    fileContent,
    operationRelativeTypescriptFilePath,
  } = config;
  const fullCommentText = commentRange.getText();
  const strippedComment = stripComment(fullCommentText);

  // NB: wouldn't hurt to put character positions in the TsComment as well, would it? But let's not do it until we need it
  const firstCharacter = commentRange.getPos();
  const lastCharacter = commentRange.getEnd();
  const firstLine = getNumberOfLines(fileContent.substring(0, firstCharacter));
  const lastLine = getNumberOfLines(fileContent.substring(0, lastCharacter));
  const isSingleLineComment = firstLine === lastLine;

  const { parameters, raw: comment }: MarkdownParse = isSingleLineComment
    ? { raw: strippedComment, parameters: {} }
    : parseFrontmatterMarkdownString(strippedComment);

  const types = findCommentTypes(comment);

  const tsComment: Creation<TsComment> = {
    name: "Comment",
    slug: kebabCase("Comment"),
    operationRelativeTypescriptFilePath,
    comment,
    statementName,
    rawStatement,
    parameters,
    types,
    firstLine,
    lastLine,
  };

  return tsComment;
}

