fetchWithTimeout.rawText:
 async (
  url: string,
  timeoutMs: number
): Promise<{
  isSuccessful: boolean;
  isConnected: boolean;
  response?: any;
  message: string;
}> => {
  try {
    const abortController = new AbortController();
    const id = setTimeout(() => abortController.abort(), timeoutMs || 10000);

    const response = await fetch(url, {
      method: "GET",
      signal: abortController.signal,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        return {
          isConnected: true,
          isSuccessful: true,
          message: "Resolved",
          result: response,
        };
      })
      .catch((error) => {
        if (!timeoutMs) {
          console.warn(error);
        }
        return {
          isSuccessful: false,
          isConnected: false,
          message: "The API didn't resolve: " + error, //error + error.status +
        };
      });

    clearTimeout(id);
    return response;
  } catch (e) {
    return {
      isSuccessful: false,
      isConnected: false,
      message: "The API didn't resolve: " + e,
    };
  }
}

