replaceLastOccurence.rawText:
 (
  string: string,
  searchValue: string,
  replaceValue: string
) => {
  const [reversedString, reversedSearchValue, reversedReplaceValue] = [
    string,
    searchValue,
    replaceValue,
  ].map(reverseString);

  const replacedReversedString = reversedString.replace(
    reversedSearchValue,
    reversedReplaceValue
  );

  const replacedString = reverseString(replacedReversedString);

  return replacedString;
}

