runTestsForOperation.rawText:
 async (
  operationName: string,
  writeResultsToIndex?: boolean,
  manualProjectRoot?: string
) => {
  // console.log(`TESTING ${operationName}`);
  const operationBasePath = await getOperationPath(operationName, {
    manualProjectRoot,
  });
  if (!operationBasePath) {
    console.log("k-test:Couldn't find operation base path...", {
      operationName,
    });
    return;
  }

  const operationIndexJsPath = path.join(operationBasePath, "build/index.js");

  if (!fs.existsSync(operationIndexJsPath)) {
    console.log("Couldn't find operation build/index.js file", {
      operationName,
      operationIndexJsPath,
    });
    return;
  }

  const tests: Test | undefined = require(operationIndexJsPath).test;

  if (!tests) {
    log(`No tests found for ${operationName}`, { type: "debug" });
    return;
  }

  return runTests(tests, operationName);
}

runTests.rawText:
 async (
  test: Test,
  operationName?: string
): Promise<boolean> => {
  const tests = makeArray(test);
  if (tests.length === 0) return true;
  const testsValid = await Promise.all(
    // NB: doing an extra check on the type of the test in the array here, because it may contain a corrupt value
    tests.map((t) => (typeof t === "function" ? t() : true))
  );
  const passedAmount = testsValid.filter((x) => x).length;
  const failedAmount = testsValid.filter((x) => !x).length;

  /**
   * TODO:
   * - makeTest should return the reuslts of the test, not only a boolean
   * - save test results into operationIndex if operationName is given
   */

  console.log(
    `${
      operationName ? `${operationName}: ` : ""
    }‚úÖ ${passedAmount} tests passed, ${
      failedAmount === 0 ? "" : "‚ùå "
    }${failedAmount} tests failed`
  );
  return isAllTrue(testsValid);
}

