getKvmdItemsRecursively.rawText:
 (
  chunk: MarkdownChunk,
  categoryStackUntilNow?: CategoryStack
): Storing<KeyValueMarkdownModelType>[] => {
  // NB: copy
  let categoryStack = categoryStackUntilNow ? [...categoryStackUntilNow] : [];

  let allKvmdItems: Storing<KeyValueMarkdownModelType>[] = [];

  // If this chunk has a title (this is a header)
  if (chunk.title) {
    const headerParsedKvmdLine = parseKvmdLine(chunk.title);

    // NB: titles with an empty key are ignored
    if (headerParsedKvmdLine) {
      // NB: also add the title as a model item
      const headerKvmdItem: Storing<KeyValueMarkdownModelType> = {
        ...headerParsedKvmdLine,
        isHeaderCalculated: true,
        // NB: copy!
        categoryStack: [...categoryStack],
      };
      allKvmdItems.push(headerKvmdItem);

      // NB: the slug of the title is added to the categoryStack, but only AFTER adding the kvmd item of the title
      categoryStack.push(headerParsedKvmdLine.slug);
    }
  }

  const childKvmdItems =
    chunk.children
      ?.map((childChunk) => getKvmdItemsRecursively(childChunk, categoryStack))
      .flat() || [];

  allKvmdItems = allKvmdItems.concat(childKvmdItems);

  const lineKvmdItems = (chunk.content || "")
    // NB: we are splitting on newlines here because content can still contain newlines.
    .split("\n")
    .map((line) => {
      const parsedKvmdLine = parseKvmdLine(line);
      if (!parsedKvmdLine) return;

      const contentKvmdItem: Storing<KeyValueMarkdownModelType> = {
        ...parsedKvmdLine,
        isHeaderCalculated: false,
        categoryStack,
      };

      return contentKvmdItem;
    })
    .filter(notEmpty);

  allKvmdItems = allKvmdItems.concat(lineKvmdItems);

  return allKvmdItems;
}

getParagraphsRecursively.rawText:
 (
  chunk: MarkdownChunk,
  categoryStackUntilNow?: CategoryStack
): MarkdownParagraph[] => {
  // NB: no copy!
  const categoryStack = categoryStackUntilNow ? categoryStackUntilNow : [];

  if (chunk.title) {
    categoryStack.push(chunk.title);
  }

  const childParagraphs =
    chunk.children
      ?.map((childChunk) => getParagraphsRecursively(childChunk, categoryStack))
      .flat() || [];

  const thisParagraph: MarkdownParagraph = {
    paragraph: chunk.content || "",
    categoryStack,
  };

  return [thisParagraph, ...childParagraphs];
}

kvmdDataToString.rawText:
 (
  kvmdData: KeyValueMarkdownModelType,
  previous: KeyValueMarkdownModelType | undefined
) => {
  // headers only need a newline before it if it's not the first item (so there is a previous)
  const newlineIfHeader = kvmdData.isHeaderCalculated && !!previous ? "\n" : "";

  const level = kvmdData.isHeaderCalculated
    ? kvmdData.categoryStack.length + 1
    : 0;

  const headerPrefix = kvmdData.isHeaderCalculated
    ? "#".repeat(level).concat(" ")
    : "";

  const isFirstItem =
    !kvmdData.isHeaderCalculated && previous?.isHeaderCalculated;
  const itemPrefix = isFirstItem ? "\n" : "";

  const line = `${newlineIfHeader}${headerPrefix}${itemPrefix}${
    kvmdData.name ? `${kvmdData.name}: ` : ""
  }${kvmdData.value || ""}${
    kvmdData.comment ? ` <!-- ${kvmdData.comment} -->` : ""
  }\n`;

  return line;
}

kvmdParseToMarkdownString.rawText:
 (
  keyValueMarkdownParse: KeyValueMarkdownParse
) => {
  const frontmatterString = frontmatterParseToString(
    keyValueMarkdownParse.parameters
  );

  //2 go over every row, and parse it back into the line
  const result = keyValueMarkdownParse.data
    .map((kvmdData, index) => {
      const previous: KeyValueMarkdownModelType | undefined =
        keyValueMarkdownParse.data[index - 1];
      const string = kvmdDataToString(kvmdData, previous);
      return string;
    })
    .join("");

  return `${frontmatterString}${result}`;
}

markdownStringToKvmdParse.rawText:
 (
  kvMdString: string,
  dbFileLocation: DbFileLocation
): KeyValueMarkdownParse => {
  const { parameters, content } = mdToJsonParse(kvMdString);
  const { absolutePath, modelName, ...modelLocation } = dbFileLocation;

  const parentKey = `parent_${camelCase(modelName)}Slug`;

  if (!content) {
    console.log("NO CONTENT FOUND");
    return { parameters: {}, data: [] };
  }

  const kvmdItems = content
    .map((chunk) => getKvmdItemsRecursively(chunk))
    .flat();

  const data: KeyValueMarkdownModelType[] = kvmdItems.map((x) => ({
    ...x,
    ...modelLocation,
    // NB: here we set the reference to its parent (which is equal to the last item in the categoryStack)
    [parentKey]: x.categoryStack[x.categoryStack.length - 1],
  }));

  return { parameters, data };
}

parseKvmdLine.rawText:
 (string: string): KvmdLine | undefined => {
  const htmlCommentRegex = /<!--[\s\S]*?-->/g;
  const lineWithoutComment = string.replace(htmlCommentRegex, "");

  const commentMatch = string.match(htmlCommentRegex)?.[0];
  const comment = commentMatch
    ? commentMatch.replace("<!--", "").replace("-->", "").trim()
    : null;

  const [keyString, valueString] = lineWithoutComment
    .split(":")
    .map((x) => x.trim());

  const value = valueString?.length ? valueString : undefined;
  const slug = slugify(keyString);

  if (keyString.length === 0) return;

  return {
    id: slug,
    name: keyString,
    value,
    comment,
    slug,
  };
}

