PingApi.rawText:
 () => {
  const [isOffline, setIsOffline] = useState(false);
  useEffect(() => {
    const intervalId = setInterval(() => {
      getItem(API_NO_RESPONSE_TIME_KEY).then((value) => {
        if (!value) {
          if (isOffline) {
            setIsOffline(false);
            return;
          }
        }

        const lastNoResponse = Number(value);
        const timeAgo = Date.now() - lastNoResponse;
        if (timeAgo < timeBeforeNotOffline && isOffline) {
          setIsOffline(false);
        } else {
          setIsOffline(true);
        }
      });
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <Div className="mx-4">
      <ALink href="/settings">{isOffline ? "üî∫" : "üíö"} API</ALink>
    </Div>
  );
}

preIndexLint.rawText:
 async ({
  operationFolderPath,
}: {
  operationFolderPath: string;
}) => {
  const problems: string[] = [];

  const packageJson = await getPackageJson({ operationFolderPath });
  if (!packageJson) {
    problems.push("package.json should exist");
  } else {
    if (!packageJson.name) {
      problems.push("package.json needs a name");
    }
    if (!packageJson.version) {
      problems.push("package.json needs a version");
    }
  }

  const tsConfig = await getTsConfig(operationFolderPath);
  if (!tsConfig) {
    problems.push("tsconfig.json should exist");
  } else {
    // we can lint tsconfig here too
  }

  const operationFolderName = getLastFolder(operationFolderPath);

  if (operationFolderName !== packageJson?.name) {
    problems.push(
      "operation folder should have the same name as the package.json name"
    );
  }

  return problems;
}

isLocked.rawText:
 (absolutePath: string) => {
  const parsedPath = path.parse(absolutePath);
  const potentialLockFilePath = path.join(
    parsedPath.dir,
    `${parsedPath.name}.lock`
  );

  if (fs.existsSync(potentialLockFilePath)) {
    const lock = readJsonFileSync<LockInfo>(potentialLockFilePath);

    // there's a lockfile
    if (lock?.updatedAt && Date.now() - lock.updatedAt < maximumLockTime) {
      // status has recently enough been updated
      return true;
    }
  }
  return false;
}

lock.rawText:
 async (
  aboluteLockableFilePath: string,
  status?: string
) => {
  const parsedPath = path.parse(aboluteLockableFilePath);
  const lockFilePath = path.join(parsedPath.dir, `${parsedPath.name}.lock`);

  await writeJsonToFile<LockInfo>(lockFilePath, {
    status: status || "busy",
    updatedAt: Date.now(),
  });
}

getCallerFileName.rawText:
 () => {
  const stack = new Error().stack;
  if (!stack) return;

  // console.log("Complete stack", stack);
  const firstRelevantLine = stack.split("\n")[3];
  if (!firstRelevantLine) return;

  const jsFilePath = firstRelevantLine.split("at ")[1].split(":")[0];

  if (!jsFilePath) return;

  const srcPath = jsFilePath.replace("/build/", "/src/").replace(".js", ".ts");

  return srcPath;
}

parseTitle.rawText:
 (markdown: string) => {
  const sentences = markdown
    .split("\n")
    .map((x) => x.split("."))
    .flat();

  const [title, ...rest] = sentences;

  return { title, rest: rest.filter((x) => x.length > 0).join("\n") };
}

sendMail.rawText:
 async (
  mailData: MailDataFromOptional | MailDataFromOptional[],
  isMultiple?: boolean
): Promise<ClientResponse | undefined> => {
  const persona = await getPrimaryPersona();

  if (!persona) {
    return;
  }

  const { sendgridApiKey, sendgridFromEmail } = persona;

  if (!sendgridApiKey) {
    console.log("Please setup your Sendgrid credentials first");
    return;
  }

  const realMailData: MailDataRequired[] = makeArray(mailData)
    .map((data) => {
      const { from: dataFrom, ...otherData } = data;

      const from = dataFrom || sendgridFromEmail;

      if (!from) {
        console.log(
          "From is missing in email and also not present in OsConfig. Please provide at least one of those. Not sending email!"
        );
        return;
      }

      // NB: it's a bit ugly, but doesn't work without typecast, and i'm pretty sure the type is correct...
      const mailData = { from, ...otherData } as MailDataRequired;

      return mailData;
    })
    .filter(notEmpty);

  sgMail.setApiKey(sendgridApiKey);

  const result = await sgMail
    .send(realMailData, isMultiple)
    .then(([result]) => {
      return result;
    })
    .catch((error: ResponseError) => {
      console.log("Error sending email", { error });

      return undefined;
    });

  return result;
}

makeFileType.rawText:
 async (
  filePath: string
): Promise<FileType | undefined> => {
  const exists = fs.existsSync(filePath);
  const stats = exists ? await fs.stat(filePath) : null;
  const mtime_ms = stats?.mtimeMs || 0;

  const operationBasePath = findOperationBasePath(filePath);
  if (!operationBasePath) return;
  const operationSrcPath = path.join(operationBasePath, "src");
  const relativePath = filePath.slice(operationSrcPath.length);
  const name = relativePath;
  // should not only be name, but also the relative path from src/*
  // const parsedPath = path.parse(filePath);
  //const name = `${parsedPath.name}${parsedPath.ext}`;

  const size = stats?.size || 0;
  const type = "f";
  const file: FileType = { exists, mtime_ms, name, size, type };
  return file;
}

