  if (!src) return undefined;

  if (
    isPathRelative(src) &&
    config.projectRelativeBaseFolderPath &&
    config.projectRelativeMarkdownFilePath
  ) {
    return getUrlFromRelativeUrl(
      src,
      relativeUrlStrategy,
      config.projectRelativeBaseFolderPath,
      config.projectRelativeMarkdownFilePath
    );
  }

  return src;
}

