MarkdownCodeblock.rawText:
 (props: {
  /**
   * STRING! don't put a JSON object here,  first use JSON.stringify!
   */
  text: string;
  extension?: string;
  /**
   * If set, this is the mode when minimised
   */
  minimalMode?: CodeblockMode;
  /**
   * If set, this is the mode when expanded
   */
  expandedMode?: CodeblockMode;
  /**
   * if true, by default it's expanded
   */
  isInitiallyExpanded?: boolean;
  /**
   * if true, mode cannot be changed
   */
  isModeStatic?: boolean;
}) => {
  const {
    text,
    extension,
    expandedMode,
    isInitiallyExpanded,
    minimalMode,
    isModeStatic,
  } = props;

  const [isExpanded, setIsExpanded] = useState(isInitiallyExpanded);

  const mode = isExpanded
    ? expandedMode || defaultExpandedMode
    : minimalMode || defaultMinimalMode;

  const realText =
    mode === "preview"
      ? text.length > 200
        ? text.slice(0, 198) + ".."
        : text
      : text;

  const nothingToExpand = mode === "preview" && text === realText;
  return (
    <Div className="w-full">
      {isModeStatic ? null : nothingToExpand ? null : (
        <Div>
          <ClickableIcon
            onClick={() => {
              setIsExpanded(!isExpanded);
            }}
            emoji={isExpanded ? "‚ûñ Minimise" : "‚ûï Expand"}
          />
        </Div>
      )}

      <Div
        className={
          mode === "scroll"
            ? "overflow-y-auto max-h-[60vh] max-w-[60vw]"
            : mode === "preview"
            ? "opacity-25 bg-black"
            : undefined
        }
      >
        {renderMarkdownContent(
          createCodeblockMarkdown(realText, extension || null),
          // The config below is not needed since all that is renderedd is a codeblock, which doesn't need to render links etc.
          {
            projectRelativeBaseFolderPath: "",
            projectRelativeMarkdownFilePath: "",
          }
        )}
      </Div>
    </Div>
  );
}

