mdContentParseRecursively.rawText:
 (
  markdownString: string,
  level: number
): MarkdownChunk[] => {
  const chunks = parseMdToChunks(markdownString, level);

  const chunksWithChildren = chunks.map(
    (chunk: MarkdownChunk): MarkdownChunk[] => {
      // NB: chunk is a paragraph
      if (chunk.level === 0 && !chunk.title && chunk.content) {
        // const lines = chunk.content.split("\n");

        // // For every line, copy the chunk...
        // const chunks = lines?.map((line) => ({
        //   ...chunk,
        //   content: line,
        // }));

        // return chunks;
        return [chunk];
      }

      // NB: h6 is highest level
      const children =
        chunk.content && [1, 2, 3, 4, 5, 6].includes(chunk.level)
          ? mdContentParseRecursively(chunk.content, chunk.level + 1)
          : undefined;

      const content = children ? undefined : chunk.content;

      // NB: here we are splitting content with multiple lines into lines only for level 0 chunks
      return content
        ? [{ ...chunk, content }]
        : [{ ...chunk, children, content: undefined }];
    }
  );

  return chunksWithChildren.flat();
}

