    content: mdContentParseRecursively(raw, 1),
  };
}

parseFrontmatterMarkdownString.rawText:
 (
  markdownWithFrontmatter: string,
  config?: MarkdownParseConfig
): MarkdownParse => {
  const lines = markdownWithFrontmatter.split("\n");

  const frontmatterStartIndex = lines.findIndex((x) => x === "---");

  //console.log({ frontmatterStartIndex });
  if (frontmatterStartIndex === -1) {
    return { raw: markdownWithFrontmatter, parameters: {}, content: [] };
  }

  const linesAfterStart = lines.slice(frontmatterStartIndex + 1);
  const frontmatterEndIndex =
    linesAfterStart.findIndex((x) => x === "---") + frontmatterStartIndex + 1;

  //console.log({ frontmatterEndIndex });
  const frontmatterLines = lines.slice(
    frontmatterStartIndex + 1,
    frontmatterEndIndex
  );

  const parameters: Frontmatter = mergeObjectsArray(
    frontmatterLines.map((line) => {
      const needTrim = !config?.noFinal;
      const key = line.split(":")[0]?.trim();
      const value = line.split(":")[1];
      const finalValue = needTrim ? value?.trim() : value;

      return { [key]: finalValue };
    })
  );

  const raw = lines.slice(frontmatterEndIndex + 1).join("\n");

  return { parameters, raw, content: [] };
}

parseMdToChunks.rawText:
 (
  markdownString: string,
  level: number
): MarkdownChunk[] => {
  const lines = markdownString.split("\n");

  const chunks = lines.reduce(
    (chunks, line) => {
      // NB: please note the space at the end, it's very important for this to work
      const prefix = level !== 0 ? `${"#".repeat(level)} ` : null;

      if (prefix && line.startsWith(prefix)) {
        // Seems to be a header!

        // NB: new header of this level detected, new chunk begins
        return chunks.concat([
          {
            level,
            title: removeHeaderPrefix(line),
          },
        ]);
      } else {
        // line is part of previous chunk, just add it to it

        // NB: since the initial value contains something, we can assert it not being empty
        const lastItem: MarkdownChunk = chunks.pop()!;
        const previousContent = lastItem.content ? `${lastItem.content}\n` : "";
        const content = `${previousContent}${line}`;
        return chunks.concat([{ ...lastItem, content }]);
      }
    },
    // NB: by default it's a paragraph without header
    [{ level: 0 }] as MarkdownChunk[]
  );
  // NB: we need something... filter out empty chunks!
  return chunks.filter((x) => x.content || x.title || x.children);
}

removeHeaderPrefix.rawText:
 (string: string) => {
  const trimmed = string.split("").reduce(
    (cum, letter) => {
      const isHeaderPrefix = cum.isHeaderPrefix && letter === "#";
      return {
        string: isHeaderPrefix ? "" : cum.string.concat(letter),
        isHeaderPrefix,
      };
    },
    { string: "", isHeaderPrefix: true }
  );

  return trimmed.string.trim();
}

