getOperationSummary.rawText:
 async (config: {
  operationName: string;
  manualProjectRoot?: string;
}): Promise<undefined | OperationSummary> => {
  const { manualProjectRoot, operationName } = config;

  const projectRoot = manualProjectRoot || getProjectRoot();
  if (!projectRoot) {
    log("Projectroot not found", { type: "error" });
    return;
  }

  const operationFolderPath = await getOperationPath(operationName, {
    manualProjectRoot,
  });

  if (!operationFolderPath) {
    log("OperationFolder not found", { type: "error" });
    return;
  }

  /**    
  operation info
  */

  const operation = (await db.get("Operation", { operationName }))[0];

  if (!operation) {
    log("Operation not found", { type: "error" });
    return;
  }

  const description = operation.operation?.markdown;

  const coreDependenciesString =
    noEmptyString(operation.operation?.coreDependenciesIndexed?.join(", ")) ||
    "none";
  const operationDependenciesString =
    noEmptyString(
      operation.operation?.operationDependenciesIndexed?.join(", ")
    ) || "none";
  const packageDependenciesString =
    noEmptyString(
      operation.operation?.packageDependenciesIndexed?.join(", ")
    ) || "none";

  const docsPath = path.join(operationFolderPath, "docs");

  const docs = await getMarkdownContents(docsPath);

  /**
   * all imports at other operations
   */
  const imports = (await db.get("TsImport")).filter(
    (x) => x.operationName !== operationName
  );

  const tsFunctions = (await db.get("TsFunction", { operationName })).filter(
    onlyUnique2<TsFunction>((a, b) => a.name === b.name)
  );

  const exportedTsFunctions = tsFunctions.filter((x) => x.isExported);

  const variables = await db.get("TsVariable", { operationName });

  const exportedVariables = variables.filter((x) => x.isExported);

  const interfaces = (await db.get("TsInterface", { operationName })).filter(
    (x) => !x.name.startsWith("NamedParameters<")
  );
  const exportedInterfaces = interfaces.filter((x) => x.isExported);

  const dependantTsFunctions: DependantCountObject[] = await Promise.all(
    exportedTsFunctions.map(addDependantCount("tsFunction", imports))
  );

  const dependantTsInterfaces: DependantCountObject[] = await Promise.all(
    exportedInterfaces.map(addDependantCount("tsInterface", imports))
  );

  const dependantTsVariables: DependantCountObject[] = await Promise.all(
    exportedVariables.map(addDependantCount("tsVariable", imports))
  );

  // Sort it! :D
  const sortedDependantCountArray = [
    dependantTsFunctions,
    dependantTsInterfaces,
    dependantTsVariables,
  ]
    .flat()
    .sort((a, b) =>
      a.externalDependantFiles.length > b.externalDependantFiles.length ? -1 : 1
    );

  const externalItems = sortedDependantCountArray.filter(
    (x) => x.externalDependantFiles.length > 0
  );

  const internalItems = sortedDependantCountArray.filter(
    (x) => x.externalDependantFiles.length === 0
  );

  const items: StatementItem[] = [
    tsFunctions.map((tsFunction) => ({ tsFunction })),
    interfaces.map((tsInterface) => ({ tsInterface })),
    variables.map((tsVariable) => ({ tsVariable })),
  ].flat();

  const testItems = items.filter((x) =>
    isConventionFileStatement(
      (x.tsFunction || x.tsInterface || x.tsVariable)!,
      "test"
    )
  );
  const cliItems = items.filter((x) =>
    isConventionFileStatement(
      (x.tsFunction || x.tsInterface || x.tsVariable)!,
      "cli"
    )
  );

  const summary: OperationSummary = {
    operationFolderPath,
    operationName,
    classification: operation.operation?.classificationIndexed,
    description,
    size: operation.operation?.sizeIndexed,
    coreDependenciesString,
    operationDependenciesString,
    packageDependenciesString,
    docs,
    cliItems,
    testItems,
    internalItems,
    externalItems,
  };

  return summary;
}

getPublicMarkdownNestedPathObject.description:
Recursively searches a folder for public markdown files, and returns a `NestedObject` with the keys being the file or folder names, and the leafs being the absolute file paths.

File and folder names are stripped (number prefixes are removed, )

example:
```json
{
"README": "path/path/README.md"
"folder1":{
"README": "path/path/folder1/REAMDE.md"
"file1": "path/path/folder1/file1.md",
}
}

getPublicMarkdownNestedPathObject.rawText:
 async (
  absoluteFolderPath: string
): Promise<NestedObject<string>> => {
  const promises = (
    await getPublicMarkdownFilePaths(absoluteFolderPath, true)
  ).map(async (result) => {
    const name = getFolderJs(removeExtensionsFromPath(result.path));

    const nestedPathObjectOrPath: NestedObject<string> | string =
      result.isFolder
        ? await getPublicMarkdownNestedPathObject(result.path)
        : result.path;

    return {
      [name]: nestedPathObjectOrPath,
    };
  });

  const completeNestedObject: NestedObject<string> = mergeObjectsArray(
    await Promise.all(promises)
  );

  return completeNestedObject;
}

