getTypeDescriptorRecursive.rawText:
 (
  schema: JSONSchema7,
  /**
   * If true, references will be links, otherwise, just the name of the referred interface
   */
  isMarkdown: boolean
): string => {
  const refLink = getRefLink(schema.$ref);
  // console.log({ $ref: schema.$ref, refLink });

  const type = Array.isArray(schema.type) ? schema.type[0] : schema.type;

  if (refLink) {
    return isMarkdown ? `\`${refLink}\`` : refLink;
  } //(#${getImplicitId(refLink)})

  if (schema.enum) {
    const enumString = `${schema.enum.map(String).join(" / ")}`;
    return type === "number" ? `${enumString} (number)` : enumString;
  }

  if (type === "boolean") return "boolean";
  if (type === "string") return "string";
  if (type === "number" || type === "integer") return "number";
  if (type === "null") return "null";
  if (type === "array") {
    const items = getSchemaItems(schema);

    const itemsString = items
      .map((item) => getTypeDescriptorRecursive(item, true))
      .join(" | ");

    return `${items.length > 1 ? `(${itemsString})` : itemsString}[]`;
  }

  //   NB: only type object is left now

  const properties = getProperties(schema);

  const additionalProperties = getSchema(schema.additionalProperties);
  const additionalPropertiesString = additionalProperties
    ? `[key: string]: ${getTypeDescriptorRecursive(
        additionalProperties,
        isMarkdown
      )}`
    : "";

  const propertyStrings = properties
    .map((property) => {
      const propertyString = `${property.name}${
        property?.required ? ":" : "?:"
      } ${getTypeDescriptorRecursive(property.schema, isMarkdown)}`;

      return propertyString;
    })
    .concat(additionalPropertiesString);

  return isMarkdown
    ? noNewlines(`{ ${propertyStrings.join(", \n")} }`)!
    : propertyStrings.join(", ");
}

