mergeMarkdownParse.rawText:
 (
  markdownParses: MarkdownParse[],
  fileName?: string
): { merged: MarkdownParse; outline: MergedMarkdownOutlineUrl[] } => {
  const allParameters = mergeObjectsArray(
    markdownParses.map((x) => x.parameters)
  );

  const allMarkdownChunks = markdownParses.map((x) => {
    const newMarkodwnChunk: MarkdownChunk = {
      children: upMarkdownChunkLevelRecursively(x.content),
      level: 1,
      content: undefined,
      title: x.fileName
        ? humanCase(removeExtensionsFromPath(x.fileName))
        : undefined,
    };

    return newMarkodwnChunk;
  });

  const newMarkdownParse: MarkdownParse = {
    parameters: allParameters,
    raw: markdownChunksToMarkdownStringRecursive(allMarkdownChunks),
    content: allMarkdownChunks,
    fileName,
  };

  const outline: MergedMarkdownOutlineUrl[] = markdownParses
    .map((markdownParse) => {
      if (!markdownParse.fileName) return;

      const title = humanCase(markdownParse.fileName);

      const implicitId = getImplicitId(title);
      return {
        title,
        // NB: because the title in the markdownparse is humancase, we need to slugify that to get the hashtagpath
        hashtagPath: implicitId,
      };
    })
    .filter(notEmpty);

  return { merged: newMarkdownParse, outline };
}

