operationToMarkdown.rawText:
 async (config: {
  operationSummary: OperationSummary;
  /**
   * - save: saves the result in the operation `README.md` and `CONTRIBUTING.md` and other default md files
   * - string: returns the full markdown string
   * - parse: returns the `MarkdownParse`
   */
  returnType?: "parse" | "string" | "save";
  includeDocs?: boolean;
  includeInfo?: boolean;
}): Promise<string | MarkdownParse | undefined> => {
  const {
    operationSummary: {
      classification,
      cliItems,
      coreDependenciesString,
      description,
      docs,
      externalItems,
      internalItems,
      operationDependenciesString,
      operationName,
      packageDependenciesString,
      size,
      testItems,
      operationFolderPath,
    },
    returnType,
  } = config;

  const detailDocsContentStrings = docs?.map((content) =>
    createMinimizedSectionMarkdown(content.content, content.relativePath)
  );

  const docsBundle = detailDocsContentStrings
    ? await bundleFolderWithMarkdown("Docs", detailDocsContentStrings, "docs")
    : undefined;

  const sizeString = `Size: ${size?.codeSize?.lines} LOC, ${
    size?.dataSize?.characters !== undefined
      ? `${size?.dataSize?.characters} data characters, `
      : ""
  }${
    size?.textSize?.characters !== undefined
      ? `${size?.textSize?.characters} text characters, `
      : ""
  }`;

  const operationInfoMd = `${operationName} (\`OperationClassification\` ${classification})

${description ? `${description}\n\n` : ""}`;

  const infoString = config.includeInfo
    ? `
${sizeString}

Imported dependencies:

- From Core Libraries: ${coreDependenciesString}
- From Packages: ${packageDependenciesString}
- From Operations: ${operationDependenciesString}`
    : undefined;

  const operationInfoMarkdownParse = mdToJsonParse(
    operationInfoMd,
    operationName
  );

  const [cliMarkdown, testMarkdown] = [cliItems, testItems].map((items) =>
    items.map(statementItemToMarkdown).filter(notEmpty).join("\n\n")
  );

  const [externalItemsMarkdown, internalItemsMarkdown] = [
    externalItems,
    internalItems,
  ].map((countArray) => {
    const res = countArray
      .map(statementItemToMarkdown)
      .filter(notEmpty)
      .join("\n\n");

    return res;
  });

  const apiReference = mdToJsonParse(externalItemsMarkdown!, "api-reference");

  const infoMarkdownParse = infoString
    ? mdToJsonParse(infoString, "info")
    : undefined;

  const internalSection = createMinimizedSection(
    internalItemsMarkdown,
    "internal",
    `Show internal (${internalItems.length})`
  );

  const testSection = createMinimizedSection(
    testMarkdown,
    "tests",
    `Show test information(${testItems.length})`
  );

  const cliSection = createMinimizedSection(
    cliMarkdown,
    "CLI",
    `Show CLI information (${cliItems.length})`
  );

  const { merged } = mergeMarkdownParse(
    [
      operationInfoMarkdownParse,
      infoMarkdownParse,
      config?.includeDocs
        ? mdToJsonParse(docsBundle?.outlineString || "")
        : undefined,
      config?.includeDocs ? docsBundle?.markdownParse : undefined,
      apiReference,
      cliSection,
      testSection,
      internalSection,
    ].filter(notEmpty)
  );

  let returnString: string | undefined = undefined;
  if (returnType !== "parse") {
    returnString = markdownParseToMarkdownString(merged);
  }

  const shouldSave = returnType === "save" || !returnType;
  if (shouldSave && returnString) {
    await fs.writeFile(
      path.join(operationFolderPath, "README.md"),
      returnString,
      "utf8"
    );
  }

  const returnValue =
    returnType === "parse"
      ? merged
      : returnType === "string"
      ? returnString
      : undefined;

  return returnValue;
}

