getAllMarkdownReaderPages.rawText:
 async (config?: {
  manualProjectRoot?: string;
}): Promise<undefined | WebPage<any>[]> => {
  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const publicBundleConfig = await readJsonFile<PublicBundleConfig>(
    path.join(projectRoot, "public-bundle-config.json")
  );

  const bundleMarkdownReaderConfig =
    publicBundleConfig?.bundleMarkdownReaderConfig;

  // the docs folder with all its files
  const docsPaths = await findAllDocsFolderPaths(true);

  const customOperationDocsPaths =
    bundleMarkdownReaderConfig?.customOperationNames
      ?.map((operationName) => {
        const relativeOperationPath =
          operations[operationName as keyof typeof operations];
        if (!relativeOperationPath) return;
        const absoluteDocsPath = path.join(
          projectRoot,
          relativeOperationPath,
          "docs"
        );
        if (!fs.existsSync(absoluteDocsPath)) return;
        return {
          absoluteDocsPath,
          queryPath: bundleMarkdownReaderConfig?.docsInRoot
            ? operationName
            : `docs/${operationName}`,
        };
      })
      .filter(notEmpty) || [];

  const customOperationDocsPages: ReaderWebPage[] =
    await getMarkdownReaderPages({
      projectRoot,
      basePaths: customOperationDocsPaths.map((x) => x.absoluteDocsPath),
      queryPathCustomPrefix: (basePath) =>
        customOperationDocsPaths.find((x) => x.absoluteDocsPath === basePath)
          ?.queryPath,
    });

  const relativeDocsPages: ReaderWebPage[] = await getMarkdownReaderPages({
    projectRoot,
    basePaths: docsPaths,
    // remove docs prefix if docs should be shown as in root
    mapQueryPath: bundleMarkdownReaderConfig?.docsInRoot
      ? (old) =>
          old.startsWith("docs/")
            ? old.slice("docs/".length)
            : // NB: "docs/README.md" becomes "docs", which should, in turn, become "" in this case...
            old === "docs"
            ? ""
            : old
      : undefined,
  });

  // operations
  const operationPages: ReaderWebPage[] = await getOperationPages(
    projectRoot,
    bundleMarkdownReaderConfig
  );

  // markdown model type pages
  const markdownModelTypePages = await getMarkdownModelPages(projectRoot);
  // todo pages
  const todoPages = await getReaderTodoPages(projectRoot);

  const mainReadme: ReaderWebPage | undefined =
    bundleMarkdownReaderConfig?.docsInRoot
      ? undefined
      : {
          queryPath: "",
          pageData: { projectRelativeFilePath: "README.md" },
          isMenuHidden: false,
        };

  const dictionaryPage: CustomWebPage = {
    queryPath: "dictionary",
    isMenuHidden: false,
    pageData: null,
  };

  const allPages: WebPage<any>[] = [
    mainReadme,
    ...relativeDocsPages,
    ...customOperationDocsPages,
    ...operationPages,
    // `/dictionary` page with all words/definitions, categorised
    dictionaryPage,
    // all markdown model types, optionally (with config)
    ...markdownModelTypePages,
    // all todo markdown folders, both in operations and outside (with config)
    ...todoPages,
  ].filter(notEmpty);

  const filteredPages = allPages.filter(
    onlyUnique2<ReaderWebPage>((a, b) => a.queryPath === b.queryPath)
  );

  return filteredPages;
}

getChildren.rawText:
 (webPages: WebPage<any>[], queryPath: string) => {
  const queryPathDepth = queryPath.split("/").length;
  const childrenKeys = webPages
    .filter(
      (x) =>
        x.queryPath.startsWith(queryPath) &&
        x.queryPath.split("/").length - queryPathDepth === 1
    )
    .map((x) => x.queryPath.split("/")[queryPathDepth]);

  return childrenKeys;
}

getFolderExplorationInfo.rawText:
 async (
  webPages: WebPage<any>[],
  queryPath: string,
  projectRoot: string
): Promise<{
  title: string | undefined;
  description: string | null;
  descriptionProjectRelativeMarkdownPath: string | null;
  children: {
    projectRelativeMarkdownPath: string | null;
    title: string;
    firstParagraph: string | null;
    folderName: string;
  }[];
}> => {
  const childrenKeys = getChildren(webPages, queryPath);

  const potentialReadmePath = path.join(projectRoot, queryPath, "README.md");
  const readmeMarkdownParse = fs.existsSync(potentialReadmePath)
    ? await readMarkdownFile(potentialReadmePath)
    : null;

  const queryPathTitle = queryPath.split("/").pop();
  const readmeInfo = getMarkdownIntro(readmeMarkdownParse);
  const title = readmeInfo.title || queryPathTitle;
  const description = readmeInfo.firstParagraph;

  const children = await Promise.all(
    childrenKeys.map(async (folderName) => {
      const potentialReadmePath = path.join(
        projectRoot,
        queryPath,
        folderName,
        "README.md"
      );
      const potentialOperationPath = path.join(
        projectRoot,
        queryPath,
        folderName,
        "OPERATION.md"
      );

      const pathToRead = fs.existsSync(potentialOperationPath)
        ? potentialOperationPath
        : fs.existsSync(potentialReadmePath)
        ? potentialReadmePath
        : undefined;

      const markdownParse = pathToRead
        ? await readMarkdownFile(pathToRead)
        : null;

      const { title, firstParagraph } = getMarkdownIntro(markdownParse);
      return {
        title: title || folderName,
        firstParagraph,
        folderName,
        projectRelativeMarkdownPath: pathToRead || null,
      };
    })
  );

  return {
    title,
    description,
    children,
    descriptionProjectRelativeMarkdownPath: readmeMarkdownParse
      ? potentialReadmePath
      : null,
  };
}

getMarkdownModelPages.rawText:
 async (
  projectRoot: string
): Promise<ReaderWebPage[]> => {
  const markdownModelNames = getObjectKeysArray(modelQueryConfig).filter(
    (modelName) => {
      const config = modelQueryConfig[modelName] as QueryConfig;

      const isMarkdownModelType = config.dbStorageMethod === "markdown";
      const noSpecificPath = !config.operationRelativePath;
      return isMarkdownModelType && noSpecificPath;
    }
  );

  const basePaths = markdownModelNames
    .map((modelName) =>
      path.join(projectRoot, "db", pluralize(kebabCase(modelName)))
    )
    .filter((potentialBasePath) => fs.existsSync(potentialBasePath));

  const markdownReaderPages = await getMarkdownReaderPages({
    projectRoot,
    basePaths,
  });

  return markdownReaderPages;
}

getMarkdownPageInfo.rawText:
 async (config: {
  projectRoot: string;
  webPages: WebPage<any>[];
  queryPath: string;
  contentPage: ReaderWebPage;
  webOperationName: string;
  markdownCallToActions: MarkdownCallToAction[];
}): Promise<{
  markdownFile: WebMarkdownFile | null;
  nextQueryPath: string | null;
  previousQueryPath: string | null;
  projectRelativeMarkdownPath: string | null;
}> => {
  const {
    contentPage,
    markdownCallToActions,
    projectRoot,
    queryPath,
    webOperationName,
    webPages,
  } = config;

  const parentQuery = getOneFolderUpPath(queryPath);
  const siblings = getChildren(webPages, parentQuery);
  const thisPageKey = queryPath.split("/").pop();
  const meIndex = siblings.findIndex((sibling) => thisPageKey === sibling);

  const previousFolder = siblings[meIndex - 1];
  const previousQueryPath = previousFolder
    ? `${parentQuery}/${previousFolder}`
    : null;
  const nextFolder = siblings[meIndex + 1];
  const nextQueryPath = nextFolder ? `${parentQuery}/${nextFolder}` : null;

  const absoluteMarkdownPath = contentPage.pageData?.projectRelativeFilePath
    ? path.join(projectRoot, contentPage.pageData.projectRelativeFilePath)
    : undefined;

  const markdownFile = absoluteMarkdownPath
    ? await readMarkdownFileToModel(
        absoluteMarkdownPath,
        webOperationName,
        markdownCallToActions
      )
    : null;

  const projectRelativeMarkdownPath = absoluteMarkdownPath
    ? makeRelative(absoluteMarkdownPath, projectRoot)
    : null;

  return {
    markdownFile,
    nextQueryPath,
    previousQueryPath,
    projectRelativeMarkdownPath,
  };
}

getMarkdownReaderQueryPaths.rawText:
 async (config?: {
  manualProjectRoot?: string;
}) => {
  const pages = await getAllMarkdownReaderPages(config);
  // NB: this is kind of a hack, but it's way harder to add it into the pages I guess, so it's easier like this.
  const queryPaths = pages?.map((x) => x.queryPath);

  const queryPaths2 = pages
    ?.map((x) => x.queryPath)
    .map((queryPath) => getAllFoldersUntilFolder(queryPath))
    .flat()
    .filter(onlyUnique2())
    .filter((x) => x !== "/")
    .map((x) => trimSlashes(x));

  // console.log({ queryPaths2 });
  return queryPaths2;
}

getOperationPages.rawText:
 async (
  projectRoot: string,
  bundleMarkdownReaderConfig?: BundleMarkdownReaderConfig
): Promise<ReaderWebPage[]> => {
  const operationBasePaths = getPathsWithOperations({
    manualProjectRoot: projectRoot,
  });

  const operationPagesPerType: { [key: string]: ReaderWebPage[] } =
    mergeObjectsArray(
      await Promise.all(
        operationBasePaths.map(async (absoluteBasePath) => {
          const folders = (
            await exploreOperationFolders({ basePath: absoluteBasePath })
          ).map((result) => ({
            projectRelativePath: makeRelative(result, projectRoot),
          }));

          const projectRelativeBasePath = makeRelative(
            absoluteBasePath,
            projectRoot
          );

          // console.log({ folders });

          const pages: ReaderWebPage[] = folders.map((folder) => {
            const folderName = getLastFolder(folder.projectRelativePath);

            /**
             * Sometimes the bundle states the menu items should not be shown. The pages still remain available though, otherwise it would cause lots of dead links!
             */
            const isMenuHidden =
              projectRelativeBasePath === "apps" &&
              bundleMarkdownReaderConfig?.omitAppsMenu
                ? true
                : projectRelativeBasePath === "packages" &&
                  bundleMarkdownReaderConfig?.omitPackagesMenu
                ? true
                : projectRelativeBasePath === "modules" &&
                  bundleMarkdownReaderConfig?.omitModulesMenu
                ? true
                : false;

            // console.log({ isMenuItem, projectRelativeBasePath, folderName });

            const readerWebPage: ReaderWebPage = {
              queryPath: folder.projectRelativePath,
              // operation filePath is README.md
              pageData: {
                projectRelativeFilePath: path.join(
                  folder.projectRelativePath,
                  "README.md"
                ),
                internalLinkWord: folderName,
              },
              isMenuHidden,
            };

            return readerWebPage;
          });

          return { [projectRelativeBasePath]: pages };
        })
      )
    );

  const operationsPages: ReaderWebPage[] =
    bundleMarkdownReaderConfig?.customOperationNames
      ?.map((operationName) => {
        const projectRelativeOperationPath =
          operations[operationName as keyof typeof operations];

        if (!projectRelativeOperationPath) return;

        const readerWebPage: ReaderWebPage = {
          pageData: {
            projectRelativeFilePath: path.join(
              projectRelativeOperationPath,
              "README.md"
            ),
          },
          queryPath: projectRelativeOperationPath,
          isMenuHidden: false,
        };

        return readerWebPage;
      })
      .filter(notEmpty) || [];

  // NB: we need to get the right order, so it needs to be done separately
  const operationPages: ReaderWebPage[] = isBundle(projectRoot)
    ? [
        ...operationsPages,
        ...operationPagesPerType.packages,
        ...operationPagesPerType.apps,
        ...operationPagesPerType.modules,
      ]
    : [
        ...operationsPages,
        ...operationPagesPerType["operations/tools"],
        ...operationPagesPerType["operations/niches"],
      ];

  return operationPages;
}

getReaderTodoPages.rawText:
 async (
  projectRoot: string
): Promise<ReaderWebPage[]> => {
  const operationsPath = getRootPath("operations");
  if (!operationsPath) return [];

  const todoFolderPaths = await findAllTodoFolderPaths(operationsPath, false);
  const markdownReaderPages: ReaderWebPage[] = await getMarkdownReaderPages({
    projectRoot,
    basePaths: todoFolderPaths,
    mapQueryPath: (queryPath) => {
      const almostPath = queryPath
        .replaceAll("todo/", "")
        .replace("operations/", "todo/");

      return almostPath.startsWith("todo/") ? almostPath : "todo/" + almostPath;
    },
  });

  return markdownReaderPages;
}

markdownReaderGetStaticPaths.rawText:
 async (
  context: GetStaticPathsContext
) => {
  const paths: string[] | undefined = await getMarkdownReaderQueryPaths();

  // console.log({ paths });
  if (!paths) {
    return { paths: [], fallback: "blocking" };
  }

  const staticPaths = paths
    .filter((x) => x !== "")
    .map((p) => ({ params: { paths: p.split("/") } }));

  return {
    paths: staticPaths,
    fallback: "blocking",
  };
}

markdownReaderGetStaticPropsFromPages.rawText:
 async (
  context: GetStaticPropsContext,
  fileWebPages: WebPage<any>[],
  webOperationName: string
): Promise<{
  props: MarkdownReaderPageProps;
}> => {
  const menu = getMenuPagesObject(fileWebPages);
  const projectRoot = getProjectRoot();

  if (!projectRoot) {
    return { props: { menu } };
  }

  const publicBundleConfig = await readJsonFile<PublicBundleConfig>(
    path.join(projectRoot, "public-bundle-config.json")
  );

  const queryPath = getQueryPath(context.params);

  // console.log("get static props", queryPath);

  // definitions/statements/functions/interfaces/operations

  // NB: finds a page with actual content
  const contentPage = fileWebPages.find((x) => x.queryPath === queryPath);

  if (!contentPage) {
    // NB: we need to render an exploration page
    const {
      children,
      title,
      description,
      descriptionProjectRelativeMarkdownPath,
    } = await getFolderExplorationInfo(fileWebPages, queryPath, projectRoot);
    return {
      props: {
        publicBundleConfig,
        menu,
        content: {
          children,
          title,
          description,
          projectRelativeMarkdownPath: descriptionProjectRelativeMarkdownPath,
        },
      },
    };
  }

  // NB: There's a content page. We need to return it (`MarkdownFile`), including its previous, next pages, and a title

  const markdownCallToActions = await db.get("MarkdownCallToAction");

  const filteredMarkdownCtas = markdownCallToActions.filter((x) => {
    const isExternalRoot =
      x.hostname !== publicBundleConfig?.deploymentHostname &&
      (!x.path || x.path === "");

    if (isExternalRoot) return true;

    const isInternalNonRoot =
      x.hostname === publicBundleConfig?.deploymentHostname &&
      x.path &&
      x.path !== "";

    return isInternalNonRoot;
  });

  const markdownPageInfo = await getMarkdownPageInfo({
    projectRoot,
    webPages: fileWebPages,
    queryPath,
    contentPage,
    webOperationName,
    markdownCallToActions: filteredMarkdownCtas,
  });

  // console.log(
  //   {
  //     projectRoot,
  //     webPages: fileWebPages,
  //     queryPath,
  //     contentPage,
  //     webOperationName,
  //     markdownCallToActions: filteredMarkdownCtas,
  //   },
  //   { markdownPageInfo }
  // );

  const augmentedWordObject = await getAugmentedWordObject(projectRoot);

  return {
    // Passed to the page component as props
    props: {
      content: {
        ...markdownPageInfo,
        augmentedWordObject,
      },
      publicBundleConfig,
      menu,
    },
  };
}

markdownReaderGetStaticProps.rawText:
 async (
  context: GetStaticPropsContext
): Promise<{
  props: MarkdownReaderPageProps;
}> => {
  const flat = (await getAllMarkdownReaderPages()) || [];
  return markdownReaderGetStaticPropsFromPages(
    context,
    flat,
    "markdown-reader-web"
  );
}

getQueryPath.rawText:
 (parsedUrlQuery: ParsedUrlQuery | undefined) => {
  const paths = parsedUrlQuery?.paths;
  const queryPath = Array.isArray(paths)
    ? paths.join("/")
    : paths === undefined
    ? ""
    : paths;
  return queryPath;
}

markdownParseToMarkdownModelType.rawText:
 (
  markdownParse: MarkdownParse | null
): Storing<MarkdownModelType> | null => {
  if (!markdownParse) return null;

  const { parameters, raw, fileName } = markdownParse;

  const name = parameters.name ? String(parameters.name) : fileName!;
  const slug = kebabCase(name);
  const id = parameters.id ? String(parameters.id) : generateId();
  const createdAt = parseMarkdownModelTimestamp(
    parameters,
    markdownParse,
    "createdAt"
  );
  const createdFirstAt = parseMarkdownModelTimestamp(
    parameters,
    markdownParse,
    "createdFirstAt"
  );
  const updatedAt = parseMarkdownModelTimestamp(
    parameters,
    markdownParse,
    "updatedAt"
  );
  const deletedAt = parseMarkdownModelTimestamp(
    parameters,
    markdownParse,
    "deletedAt"
  );

  const openedAt = parseMarkdownModelTimestamp(
    parameters,
    markdownParse,
    "openedAt"
  );

  const markdownModelType: Storing<MarkdownModelType> = {
    ...parameters,
    id,
    createdAt,
    createdFirstAt,
    deletedAt,
    updatedAt,
    openedAt,
    markdown: raw,
    name,
    slug,
  };

  return markdownModelType;
}

parseMarkdownModelTimestamp.rawText:
 (
  parameters: Frontmatter,
  markdownParse: MarkdownParse,
  parameterName:
    | "createdAt"
    | "createdFirstAt"
    | "updatedAt"
    | "deletedAt"
    | "openedAt"
): number => {
  const parameterValue = parameters[parameterName];
  const markdownParseValue = markdownParse[parameterName];

  const parsedParameterValue =
    typeof parameterValue === "number" && !isNaN(parameterValue)
      ? parameterValue
      : typeof parameterValue === "string"
      ? tryParseDate(parameterValue)
      : undefined;

  const generatedValue =
    parameterName === "deletedAt" || parameterName === "openedAt"
      ? 0
      : Date.now();

  const parsedTimestamp =
    parsedParameterValue !== undefined
      ? parsedParameterValue
      : markdownParseValue !== undefined
      ? markdownParseValue
      : generatedValue;

  return parsedTimestamp;
}

tryParseDate.rawText:
 (dateString: string): number | undefined => {
  try {
    return new Date(dateString).valueOf();
  } catch {}
}

findCodeblocks.rawText:
 (markdownString: string): string[] => {
  const result = flattenMarkdownString(
    markdownString,
    (token) => token.type === "code"
  );

  const codesblocks: string[] = result
    .map((token) => {
      if (token.type !== "code") return;

      return token.text;
    })
    .filter(notEmpty);

  return codesblocks;
}

