  return links;
}

flattenMarkdownString.rawText:
 (
  markdownString: string,
  findFunction: (token: marked.Token) => boolean
): marked.Token[] => {
  const tokenList = marked.lexer(markdownString);
  const result = tokenList
    .map((x) => flattenMarkedTokenRecursive(x, findFunction))
    .filter(notEmpty)
    .flat();

  return result;
}

flattenMarkedTokenRecursive.rawText:
 (
  token: marked.Token,
  findFunction: (token: marked.Token) => boolean
): marked.Token[] => {
  if (findFunction(token)) {
    return [token];
  }

  if (token.type === "table") {
    const header = token.header
      .map((token) => {
        const result = token.tokens
          .map((x) => flattenMarkedTokenRecursive(x, findFunction))
          .flat();
        return result;
      })
      .flat();

    const rows = token.rows
      .map((row) => {
        const result = row
          .map((token) => {
            const result = token.tokens
              .map((x) => flattenMarkedTokenRecursive(x, findFunction))
              .flat();

            return result;
          })
          .flat();

        return result;
      })
      .flat();

    return [header, rows].flat();
  }

  if (token.type === "list") {
    const result = token.items
      .map((token) => {
        const result = token.tokens
          .map((x) => flattenMarkedTokenRecursive(x, findFunction))
          .flat();
        return result;
      })
      .flat();

    return result;
  }

  if (
    token.type === "del" ||
    token.type === "em" ||
    token.type === "heading" ||
    token.type === "link" ||
    token.type === "paragraph" ||
    token.type === "strong"
  ) {
    const result = token.tokens
      .map((x) => flattenMarkedTokenRecursive(x, findFunction))
      .flat();
    return result;
  }

  return [];
}

getNewPerformance.rawText:
 (
  label: string,
  uniqueId: string,
  isNew?: boolean
): PerformanceItem | undefined => {
  const timePrevious = timer[uniqueId];
  const timeNow = Date.now();
  timer[uniqueId] = timeNow;

  if (isNew) return;

  const durationMs = timeNow - timePrevious;

  return { label, durationMs };
}

