NestedMenuItem.rawText:
 (
  props: {
    item: NestedWebPage;
    /**
     * If true, items with a single child will be merged into the parent
     */
    mergeSingleChilds?: boolean;
    headersClickable?: boolean;
    level?: number;
  } & MouseEventCallbacks
) => {
  const {
    onClick,
    onDoubleClick,
    onContextMenu,
    item,
    level,
    headersClickable,
    mergeSingleChilds,
  } = props;

  const realItem: NestedWebPage = mergeSingleChilds
    ? getRealItemRecursive(item)
    : item;

  const {
    children,
    target,
    isMenuHidden,
    menuTitleAugmentation,
    menuTitleTooltip,
    queryPath,
  } = realItem;

  const shownChildren = children?.filter((x) => !x.isMenuHidden);

  const title = getTitle(realItem);

  const router = useRouter();
  const currentPath = router.asPath.substring(1).split("?")[0];

  const [isExpanded, toggleExpanded, setIsExpanded, setNotExpanded] =
    useExpanded(queryPath);

  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );

  const isSelected = currentPath === queryPath;
  const realLevel = level || 0;

  const hasChildren = shownChildren && shownChildren.length > 0;

  const isClickable = headersClickable || !hasChildren;
  const textClassName = isClickable
    ? `${isSelected ? "text-blue-500" : ""} hover:text-blue-800 `
    : undefined;

  const href = isSelected
    ? undefined
    : isClickable
    ? !queryPath || queryPath.length === 0
      ? "/"
      : queryPath
    : undefined;

  const allChildrenWereHidden = children?.length && !shownChildren?.length;
  return allChildrenWereHidden ? null : (
    <Div className="w-full">
      <Div
        className={`w-full flex flex-row justify-center items-center pr-4 ${
          isClickable
            ? `hover:bg-gray-200 hover:dark:bg-gray-800 ${
                isSelected ? "bg-gray-200 dark:bg-gray-800 " : ""
              }`
            : ""
        }`}
      >
        <ALink
          style={{
            paddingLeft: realLevel * 10 + 5,
            paddingBottom: 10,
            paddingTop: 10,
          }}
          className="flex flex-row justify-between"
          textClassName="flex flex-1 text-xs"
          href={href}
          target={target}
          onDoubleClick={(event) => event && onDoubleClick?.(event, realItem)}
          onContextMenu={(event) => onContextMenu?.(event, realItem)}
          onClick={(event) => {
            if (isExpanded && isSelected) {
              setNotExpanded();
            } else {
              onClick?.(event, realItem);
              setIsExpanded();
              if (!realItem.children) {
                setIsMobileMenuEnabled(false);
              }
            }
          }}
        >
          <Span
            //line-clamp-1 truncate text-ellipsis
            textClassName={textClassName}
          >
            {title}
          </Span>
          <Span className="flex flex-row">
            {menuTitleAugmentation ? (
              <Span>{menuTitleAugmentation}</Span>
            ) : null}
          </Span>
        </ALink>
        {hasChildren ? (
          <Div
            className="text-3xl cursor-pointer"
            onClick={() => toggleExpanded()}
          >
            {isExpanded ? "‚ñæ" : "‚ñ∏"}
          </Div>
        ) : null}
      </Div>

      {hasChildren && isExpanded ? (
        <Div className="w-full">
          {shownChildren.map((child, i) => {
            return (
              <NestedMenuItem
                key={`menu-${href}-${i}`}
                item={child}
                headersClickable={headersClickable}
                level={realLevel + 1}
              />
            );
          })}
        </Div>
      ) : null}
    </Div>
  );
}

NestedMenu.rawText:
 (
  props: {
    items?: NestedWebPage[];
    headersClickable?: boolean;
  } & MouseEventCallbacks
) => {
  const router = useRouter();
  const [expanded, setExpanded, { hydrated }] = useStore("menu.expanded");

  const expandCompleteUrlInMenu = () => {
    const parts = trimSlashes(router.asPath).split("/");
    const cummulativeParts = parts
      .map((part, index, array) => {
        if (part === "") return;
        return array.slice(0, index + 1).join("/");
      })
      .filter(notEmpty);
    const cumulativePartsExpandedObject = mergeObjectsArray(
      cummulativeParts.map((url) => ({ [url]: true }))
    );
    setExpanded({ ...expanded, ...cumulativePartsExpandedObject });
  };
  useEffect(() => {
    expandCompleteUrlInMenu();
  }, [router.asPath]);

  const { items, ...menuItemProps } = props;
  const menuItems = items
    ?.filter((x) => !x.isMenuHidden)
    .map((nestedWebPage, index) => {
      return (
        <NestedMenuItem
          mergeSingleChilds
          item={nestedWebPage}
          key={`menu${index}`}
          {...menuItemProps}
        />
      );
    });

  return <Div className="w-full">{menuItems}</Div>;
}

useExpanded.rawText:
 (queryPath: string = "") => {
  const [expanded, setExpanded, { hydrated }] = useStore("menu.expanded");

  const isExpanded =
    expanded[queryPath] !== undefined ? expanded[queryPath] : false;

  // console.log({ queryPath, isExpanded });
  const setIsExpanded = () => {
    return setExpanded({ ...expanded, [queryPath]: true });
  };

  const setNotExpanded = () => {
    return setExpanded({ ...expanded, [queryPath]: false });
  };

  const toggleExpanded = () => {
    return setExpanded({
      ...expanded,
      [queryPath]: !isExpanded,
    });
  };

  const tuple: [
    boolean,
    () => Promise<void>,
    () => Promise<void>,
    () => Promise<void>,
    { hydrated: boolean }
  ] = [isExpanded, toggleExpanded, setIsExpanded, setNotExpanded, { hydrated }];

  return tuple;
}

getAvailableOperationName.rawText:
 async (
  rootFolderPath: string,
  preferredFolderName: string,
  manualProjectRoot?: string
): Promise<string> => {
  //making sure we make a folder that doesn't exist yet:
  let n = 0;
  let availableFolderName = preferredFolderName;

  while (true) {
    const folderAlreadyExists = fs.existsSync(
      path.join(rootFolderPath, availableFolderName)
    );

    const operationAlreadyExists =
      (await getOperationPath(availableFolderName, {
        manualProjectRoot,
        notUseSdk: !!manualProjectRoot,
      })) !== undefined;

    if (!folderAlreadyExists && !operationAlreadyExists) break;

    n++;
    availableFolderName = `${preferredFolderName}${n}`;
  }

  return availableFolderName;
}

