oneByOne.rawText:
 async <T, U>(
  array: T[],
  callback: (instance: T, index: number) => Promise<U>
): Promise<U[]> => {
  const getResults = async (
    results: Promise<U[]>,
    instance: T,
    index: number
  ) => {
    const awaitedResults = await results;
    const result: U = await callback(instance, index);
    const newResults = [...awaitedResults, result];
    return newResults;
  };

  const result = await array.reduce(
    getResults,
    new Promise<U[]>((resolve) => resolve([]))
  );

  return result;
}

getDependenciesSummary.rawText:
 async (operationName: string) => {
  const operationImports = await db.get("TsImport", {
    operationName,
  });

  const coreDependencies = operationImports
    .filter((x) => ["node", "react"].includes(x.classification))
    .map((x) => x.name);
  const operationDependencies = operationImports
    .filter((x) => ["operation"].includes(x.classification))
    .map((x) => x.name);
  const packageDependencies = operationImports
