getOperationMetaData.rawText:
 async (
  /**
   * any path in an operation
   */
  operationBasePath: string
): Promise<undefined | OperationMetaData> => {
  if (!operationBasePath) return;
  const projectRoot = getProjectRoot(operationBasePath);
  if (!projectRoot) return;
  const packageJson = await getPackageJson({
    operationFolderPath: operationBasePath,
  });
  const operationName = packageJson?.name;

  if (!operationName) {
    return;
  }
  const srcPath = path.join(operationBasePath, "src");
  const operationFolderName = getLastFolder(operationBasePath);

  const operations = await db.get("Operation", { operationName });
  const operation: Operation | undefined = takeFirst(operations);

  const relativeOperationLocationPath = makeRelative(
    path.join(operationBasePath, ".."),
    projectRoot
  );
  if (!relativeOperationLocationPath) return;

  return {
    operationBasePath,
    operation,
    operationName,
    srcPath,
    operationFolderName,
    /**
     * where the operation is located relative to the project root
     */
    relativeOperationLocationPath,
  };
}

recalculateOperationIndexJson.rawText:
 async (
  operationBasePath: string,
  manualProjectRoot?: string
) => {
  const operationMetaData = await getOperationMetaData(operationBasePath);

  if (!operationMetaData) {
    log(`Operation on ${operationBasePath} doesn't have a basepath`, {
      type: "error",
    });
    return;
  }

  const {
    operation,
    operationFolderName,
    operationName,
    relativeOperationLocationPath,
  } = operationMetaData;

  if (!operation || !operationName || !relativeOperationLocationPath) {
    log(
      `Operation on ${operationBasePath} doesn't have a name or relativeOperationLocationPath or package.json`,
      { type: "error" }
    );
    return;
  }

  const classificationIndexed = getOperationClassification(operationBasePath)!; //NB: we know it's there because operationBasePath must be valid

  const { coreDependencies, operationDependencies, packageDependencies } =
    await getDependenciesSummary(operationName);

  const categories = await categorizeFiles({
    basePath: operationBasePath,
    ignoreIndexFiles: true,
  });

  const sizeIndexed = await getFolderSummary(categories);
  // console.log("UPDATE QUERY");
  await db.update(
    "Operation",
    (item) => item.name === operation.name,
    (old) =>
      mergeNestedObject(old, {
        operation: {
          lastRebuildAt: Date.now(),
          sizeIndexed,
          folderNameIndexed: operationFolderName,
          relativeOperationLocationPathIndexed: relativeOperationLocationPath,
          classificationIndexed,
          coreDependenciesIndexed: coreDependencies,
          operationDependenciesIndexed: operationDependencies,
          packageDependenciesIndexed: packageDependencies,
        },
      }),
    { operationName: operation.name }
  );
}

parsePrimitiveArray.rawText:
 (string: string) => {
  const result = string.split(",").map((x) => x.trim());
  return result;
}

parsePrimitiveBoolean.rawText:
 (string: string) => {
  if (string.trim() === "true") return true;
  if (string.trim() === "false") return false;
}

parsePrimitiveString.rawText:
 (string: string) => {
  if (string.startsWith('"') && string.endsWith('"')) {
    return string.substring(1, string.length - 1);
  }
  if (string.charAt(0) === " ") return string.slice(1);
  return string;
}

parsePrimitive.rawText:
 (
  string: string,
  simplifiedSchema?: SimplifiedSchema
): PrimitiveResult => {
  if (simplifiedSchema) {
    if (simplifiedSchema.type === "boolean") {
      const parsedBoolean = parsePrimitiveBoolean(string);
      return parsedBoolean || false;
    }

    if (simplifiedSchema.type === "null") {
      return null;
    }
    if (simplifiedSchema.type === "number") {
      return !isNaN(Number(string)) ? Number(string) : undefined;
    }

    if (simplifiedSchema.type === "string") {
      return parsePrimitiveString(string);
    }

    if (simplifiedSchema.type === "array") {
      return parsePrimitiveArray(string);
    }
  }

  // NB: failsafes
  if (string === undefined) return undefined;
  if (string === null) return null;

  const parsedBoolean = parsePrimitiveBoolean(string);
  if (parsedBoolean !== undefined) return parsedBoolean;

  if (string === "true") return true;
  if (string === "false") return false;
  if (string === "null") return null;
  if (string === "undefined") return undefined;
  // NB: an empty string will count as 0 in the next statement
  if (string === "") return "";
  if (!isNaN(Number(string))) return Number(string);
  return parsePrimitiveString(string);
}

calculatePathMetaData.rawText:
 async (
  absolutePath: string
): Promise<PathMetaData | undefined> => {
  const exists = fs.existsSync(absolutePath);
  if (!exists) return;
  const pathParse = getPathParse(absolutePath);
  if (!pathParse) return;

  const stats = await fs.stat(absolutePath);
  const isFolder = stats.isDirectory();
  const fullFileName = isFolder ? undefined : path.parse(absolutePath).base;

  const fileType = isFolder ? null : determineFileType(absolutePath);

  const categorizedFiles: CategorizedFilePaths = isFolder
    ? await categorizeFiles({ basePath: absolutePath })
    : {
        code: fileType === "code" ? [absolutePath] : [],
        data: fileType === "data" ? [absolutePath] : [],
        text: fileType === "text" ? [absolutePath] : [],
      };

  const folderSummary = await getFolderSummary(categorizedFiles);
  const mainComment = await getPathMainComment(absolutePath);

  const pathMetaData: PathMetaData = {
    mainComment,
    fullFileName,
    createdAt: stats.ctimeMs,
    updatedAt: stats.mtimeMs,
    ...pathParse,
    sizes: folderSummary,
  };

  return pathMetaData;
}

categorizeFiles.rawText:
 async ({
  basePath,
  type,
  ignoreIndexFiles,
}: {
  basePath: string | string[];
  /**
   * ignore index files or not
   */
  ignoreIndexFiles?: boolean;
  /**
   * if given, only search for files of a specific containing data type
   */
  type?: "code" | "data" | "text";
}): Promise<CategorizedFilePaths> => {
  const defaultIgnore = ["node_modules", databaseFolderName, buildFolderName];

  const filePaths = (
    await explore({
      basePath,
      extension: type ? (extensions[type] as SearchableExtension[]) : undefined,
      ignore: ignoreIndexFiles
        ? ["index.ts", "index.tsx", ...defaultIgnore]
        : defaultIgnore,
    })
  ).map((x) => x.path);

  return filePaths.reduce(
    (categorizedFilePathsObject, filePath) => {
      const fileType = determineFileType(filePath);
      // console.log({ filePath, fileType });
      if (!fileType) return categorizedFilePathsObject;

      categorizedFilePathsObject[fileType].push(filePath);

      return categorizedFilePathsObject;
    },
    { code: [] as string[], data: [] as string[], text: [] as string[] }
  );
}

getFolderSummary.rawText:
 async (
  categorizedFiles: CategorizedFilePaths
): Promise<FolderSummary> => {
  const categoriesSummary: Omit<FolderSummary, "size"> = {
    codeSize: await sumSizeSummary(categorizedFiles.code),
    dataSize: await sumSizeSummary(categorizedFiles.data),
    textSize: await sumSizeSummary(categorizedFiles.text),
  };

  const folderSummary: FolderSummary = {
    ...categoriesSummary,
    size: sumAllKeys(Object.values(categoriesSummary), [
      "bytes",
      "characters",
      "lines",
      "numberOfFiles",
    ]),
  };

  return folderSummary;
}

getPathMainComment.description:
export const sumFolderSummary = (
firstFolderSummary: FolderSummary,
secondFolderSummary: FolderSummary
): FolderSummary => {
const folderSummaryKeys = Object.keys(
firstFolderSummary
) as (keyof FolderSummary)[];
const sum = mergeObjectsArray(
folderSummaryKeys.map((keyName) => {
return {
[keyName]: sumAllKeys([
firstFolderSummary[keyName],
secondFolderSummary[keyName],
],["bytes","characters","lines","numberOfFiles"]),
};
})
) as FolderSummary;

return sum;
};


takes all PathMetaData of an array that contains all child files, and merges them, taking the newest update date, the earliest created-date, and summing size

export const sumFileGeneralMetaData = async (
childrenMetaDataArray: PathGeneralMetaData[]
): Promise<PathGeneralMetaData | null> => {
const sum = childrenMetaDataArray.reduce(
(sumMetaData, pathMetaData: PathGeneralMetaData) => {
const newPathMetaData: PathGeneralMetaData = {
createdAt:
!sumMetaData || pathMetaData.createdAt < sumMetaData.createdAt
? pathMetaData.createdAt
: sumMetaData.createdAt,
updatedAt:
!sumMetaData || pathMetaData.updatedAt > sumMetaData.updatedAt
? pathMetaData.updatedAt
: sumMetaData.updatedAt,
sizes: !sumMetaData
? pathMetaData.sizes
: sumFolderSummary(sumMetaData.sizes, pathMetaData.sizes),
};

return newPathMetaData;
},
null as PathGeneralMetaData | null
);

return sum;
};

getPathMainComment.rawText:
 async (
  absolutePath: string
): Promise<TsComment | undefined> => {
  const indexId = await getIndexId(absolutePath, getLastFolder(absolutePath));
  if (!indexId) return;
  const folderPath = getFolder(absolutePath);
  const folderFiles = await fs.readdir(folderPath, "utf8");
  const foundReadmePath = folderFiles.find(
    (x) => path.parse(x).name.toLowerCase() === "readme"
  );

  const readmeExists = !!foundReadmePath;

  const markdown = readmeExists
    ? await fs.readFile(foundReadmePath, "utf8")
    : "";
  const { raw: comment, parameters } = parseFrontmatterMarkdownString(markdown);

  const types = findCommentTypes(comment);

  const mainComment: TsComment | undefined = {
    rawStatement: undefined,
    statementName: undefined,
    ...indexId,
    comment,
    parameters,
    types,
    firstLine: 0,
    lastLine: getNumberOfLines(markdown),
  };

  return mainComment;
}

