isPortUsed.rawText:
 (port: number): Promise<boolean> => {
  return new Promise<boolean>((resolve, reject) => {
    const server = net.createServer();

    server.once("error", function (err) {
      if ((err as any).code === "EADDRINUSE") {
        resolve(true);
      }
    });

    server.once("listening", function () {
      // close the server if listening doesn't fail
      server.close();

      resolve(false);
    });

    server.listen(port);
  });
}

lateFetchPeerMessageSync.rawText:
 async () => {
  // goes over all peers, and fetches all their messages since the last sync
  const peers = await db.get("Device", {
    include: { referenceKey: "personId" },
  });

  const result = await oneByOne(peers, async (peer) => {
    const lastSyncedAt = peer.lastSyncDatabaseAtObject.PeerMessage;

    // get new PeerMessages after last sync
    const apiResult = await apiWithConfig.getDbModel(
      {
        apiUrl: `http://${peer.name}:${ports["function-server"]}`,
        authToken: peer.authToken,
        timeout: 2000,
      },
      "PeerMessage",
      {
        filter: [
          // {
          //   objectParameterKey: "peerSlug",
          //   operator: "equal",
          //   value: peer.,
          // },
          {
            // updatedAt is better than createdAt because it can also work if the model can be updated and if the messages can be edited for example. The id stays the same so upsert will overwrite it
            objectParameterKey: "updatedAt",
            operator: "greaterThan",
            value: String(lastSyncedAt),
          },
        ],
      }
    );
    const newPeerMessages = apiResult.result?.data as PeerMessage[] | undefined;

    if (!newPeerMessages) {
      // PEER is not online
      return;
    }

    // TODO: this should be augmented I think
    // @ts-ignore
    const upsertResult = await db.upsert("PeerMessage", newPeerMessages);

    // Update peer to state that we have just synced (and the peer seems to be online)
    const updatedResult = await db.update(
      "Device",
      (p) => p.name === peer.name,
      (p) => ({
        ...p,
        lastOnlineAt: Date.now(),
        lastSyncDatabaseAtObject: {
          ...p.lastSyncDatabaseAtObject,
          PeerMessage: Date.now(),
        },
      })
    );

    return upsertResult;
  });

  const add = (previous: number, current: number) => previous + current;
  const sum = result
    .filter(notEmpty)
    .map((x) => x.amountInserted)
    .filter(notEmpty)
    .reduce(add, 0);

  return { newMessagesAmount: sum };
}

