startApp.rawText:
 async (
  operationName: string,
  isDev?: boolean
): Promise<{
  isSuccessful: boolean;
  error?: Error;
  proc?: pm2.Proc;
  message?: string;
}> => {
  const operationFolderPath = await getOperationPath(operationName);
  if (!operationFolderPath) {
    return { isSuccessful: false, message: "No operation folder path" };
  }
  const packageJson = await getPackageJson({ operationFolderPath });
  if (!packageJson) {
    return { isSuccessful: false, message: "no package json found" };
  }

  const port = packageJson?.operation?.port;

  if (!port) {
    return {
      isSuccessful: false,
      message: "Operation must have a port assigned",
    };
  }

  const isAlready = await isPortUsed(port);

  if (isAlready) {
    return {
      isSuccessful: true,
      message: `App was already running (port ${port})`,
    };
  }

  const action = (resolve: Pm2Resolver) => {
    pm2.start(
      {
        time: true,
        name: `${appPrefix}${operationName}`,
        output: undefined,
        error: undefined,
        script: isDev ? "yarn dev" : "yarn start",
        cwd: operationFolderPath,
      },
      (error, proc) => {
        if (error) {
          resolve({ isSuccessful: false, error });
        }
        resolve({ isSuccessful: true, proc });
      }
    );
  };

  const result = await pm2ConnectDisconnect(action);

  return result;
}

