        await delay(5000);
        console.log("TRY AGAIN");
        const browser = await openNewBrowser();
        if (!browser) return;
        if (browser) {
            if (pageId) {
                return getBrowserPageById(browser, pageId);
            }

            const page = await getNewPage(browser);

            await page?.setBypassCSP(true);
